     1'use client';
     2
     3import React, { useRef, useEffect, useState, useMemo, useCallback } from 'react';
     4import Map, { Marker, Popup, Source, Layer, type MapRef } from 'react-map-gl/mapbox';
     5import mapboxgl, { type MapMouseEvent } from 'mapbox-gl';
     6import { Location, Hunt, Report, GeoJsonFeature, PrivacyLevel, DataType } from '@/lib/types/api';
     7import { Squares2X2Icon } from '@heroicons/react/24/outline';
     8import 'mapbox-gl/dist/mapbox-gl.css';
     9
    10// Map mode type
    11export type MapMode = 'view' | 'create-hunt' | 'create-report' | 'create-location';
    12// New unified components
    13import { PrivacyLayerControlPanel } from '@/components/map/layers/PrivacyLayerControlPanel';
    14import { PrivacyLayerProvider, usePrivacyLayers } from '@/contexts/PrivacyLayerContext';
    15import { MapErrorBoundary } from './MapErrorBoundary';
    16// import { FallbackMapControls } from '@/components/map/FallbackMapControls';
    17
    18// Clustering components and utilities
    19import { useSimpleClustering, useClusterPopup } from '@/hooks/useMapClustering';
    20import { useBatchViewportCulling } from '@/hooks/useViewportCulling';
    21import { ClusterPopup } from '@/components/map/ClusterPopup';
    22import { ClusterToggle } from '@/components/map/ClusterControl';
    23import { 
    24  getClusterCirclePaint, 
    25  getClusterSymbolLayout, 
    26  getClusterTextPaint
    27} from '@/utils/mapClustering';
    28
    29interface UnifiedMapProps {
    30  // Legacy data props for backward compatibility
    31  locations?: Location[];
    32  hunts?: Hunt[];
    33  reports?: Report[];
    34  
    35  // Selection and interaction
    36  selectedLocation?: Location | null;
    37  selectedHunt?: Hunt | null;
    38  selectedReport?: Report | null;
    39  onLocationSelect?: (location: Location | null) => void;
    40  onHuntSelect?: (hunt: Hunt | null) => void;
    41  onReportSelect?: (report: Report | null) => void;
    42  onMapClick?: (event: { lng: number; lat: number }) => void;
    43  
    44  // Map configuration
    45  center?: [number, number];
    46  zoom?: number;
    47  height?: string;
    48  mapStyle?: string;
    49  
    50  // Controls
    51  showControls?: boolean;
    52  showLayerControls?: boolean;
    53  layerControlPosition?: 'top-left' | 'top-right' | 'bottom-left' | 'bottom-right';
    54  
    55  // Privacy layer configuration
    56  initialVisibleLayers?: string[];
    57  
    58  // Creation tools
    59  onAddHunt?: (coordinates: { lng: number; lat: number }) => void;
    60  onAddReport?: (coordinates: { lng: number; lat: number }) => void;
    61  onAddLocation?: (coordinates: { lng: number; lat: number }) => void;
    62  
    63  // Visual markers
    64  showLocationMarker?: boolean;
    65  locationMarkerPosition?: { lng: number; lat: number };
    66  
    67  // Custom controls
    68  showCustomControls?: boolean;
    69}
    70
    71const MAPBOX_TOKEN = process.env.NEXT_PUBLIC_MAPBOX_ACCESS_TOKEN;
    72
    73// Critical fix for Mapbox GL JS v3+ with react-map-gl v8
    74if (MAPBOX_TOKEN) {
    75  mapboxgl.accessToken = MAPBOX_TOKEN;
    76}
    77
    78// Internal map component that uses privacy layer context
    79function MapContent({
    80  locations = [],
    81  hunts = [],
    82  reports = [],
    83  selectedLocation,
    84  selectedHunt,
    85  selectedReport,
    86  onLocationSelect,
    87  onHuntSelect,
    88  onReportSelect,
    89  onMapClick,
    90  center = [-98.5795, 39.8283],  // Center of continental US
    91  zoom = 4,  // Zoom level appropriate for seeing entire US
    92  height = '400px',
    93  mapStyle = 'mapbox://styles/mapbox/satellite-streets-v12', // Changed to standard style for debugging
    94  showControls = true,
    95  showLayerControls = true,
    96  layerControlPosition = 'top-right',
    97  onAddHunt,
    98  onAddReport,
    99  onAddLocation,
   100  showLocationMarker = false,
   101  locationMarkerPosition,
   102  showCustomControls = true,
   103}: UnifiedMapProps) {
   104  console.log(' MapContent - Component rendering, about to call usePrivacyLayers');
   105  const mapRef = useRef<MapRef>(null);
   106  const [viewState, setViewState] = useState({
   107    longitude: center[0],
   108    latitude: center[1],
   109    zoom: zoom,
   110  });
   111  
   112  // Layer control panel state
   113  const [layerControlOpen, setLayerControlOpen] = useState(true);
   114  
   115  // Debug panel states
   116  
   117  // Map interaction mode state
   118  const [mapMode, setMapMode] = useState<MapMode>('view');
   119  const [tempMarker, setTempMarker] = useState<{ lng: number; lat: number } | null>(null);
   120  
   121  // Map style loading state
   122  const [mapStyleLoaded, setMapStyleLoaded] = useState(false);
   123  
   124  // Clustering state and configuration
   125  const {
   126    clusteringEnabled,
   127    clusterConfigs,
   128    handleClusterClick: onClusterClick,
   129    getSourceConfig,
   130    toggleClustering,
   131  } = useSimpleClustering({
   132    initialEnabled: true,
   133    performanceMonitoring: true,
   134  });
   135  
   136  // Cluster popup state
   137  const {
   138    popupInfo: clusterPopupInfo,
   139    showPopup: showClusterPopup,
   140    hidePopup: hideClusterPopup,
   141  } = useClusterPopup();
   142
   143  // Get privacy layer data and state
   144  const {
   145    huntsData,
   146    locationsData,
   147    reportsData,
   148    visibleLayers,
   149    timeRange,
   150    toggleLayer,
   151    setTimeRange,
   152    setMapRef,
   153    isLoading,
   154    error,
   155    showAllLayers,
   156  } = usePrivacyLayers();
   157  
   158  // Set map ref in context when it becomes available
   159  useEffect(() => {
   160    if (mapRef.current) {
   161      setMapRef(mapRef.current);
   162    }
   163  }, [setMapRef]);
   164
   165  // Viewport culling for performance optimization
   166  const viewportCulling = useBatchViewportCulling(
   167    mapRef.current,
   168    {
   169      hunts: huntsData,
   170      locations: locationsData,
   171      reports: reportsData,
   172    },
   173    true // Enable viewport culling
   174  );
   175
   176  // Process GeoJSON data for rendering with privacy-based visibility
   177  const processedData = useMemo(() => {
   178    console.log(' PROCESSING DATA DEBUG - Start:', {
   179      visibleLayersSet: Array.from(visibleLayers),
   180      visibleLayersSize: visibleLayers.size,
   181      rawDataCounts: {
   182        hunts: huntsData?.features?.length || 0,
   183        locations: locationsData?.features?.length || 0,
   184        reports: reportsData?.features?.length || 0
   185      },
   186      viewportCullingStats: viewportCulling.statistics,
   187      huntsDataSample: huntsData?.features?.slice(0, 2).map(f => ({
   188        type: f.type,
   189        geometry: f.geometry,
   190        properties: f.properties
   191      }))
   192    });
   193    
   194    // Mobile optimization: limit features if on mobile device
   195    const isMobile = typeof window !== 'undefined' && window.innerWidth < 768;
   196    const maxFeatures = isMobile ? 200 : 1000; // Limit features on mobile
   197    
   198    const processed = {
   199      hunts: { type: 'FeatureCollection' as const, features: [] as GeoJsonFeature[] },
   200      locations: { type: 'FeatureCollection' as const, features: [] as GeoJsonFeature[] },
   201      reports: { type: 'FeatureCollection' as const, features: [] as GeoJsonFeature[] },
   202    };
   203
   204    // Use viewport-culled data for better performance
   205    const sourceHunts = viewportCulling.culledData.hunts;
   206    const sourceLocations = viewportCulling.culledData.locations;
   207    const sourceReports = viewportCulling.culledData.reports;
   208    
   209    console.log(' VIEWPORT CULLED DATA:', {
   210      sourceHunts: sourceHunts.length,
   211      sourceLocations: sourceLocations.length,
   212      sourceReports: sourceReports.length
   213    });
   214
   215    // VISIBILITY DEBUGGING - Show us exactly what's in visibleLayers (simplified to avoid circular refs)
   216    console.log(' VISIBILITY DEBUG - visibleLayers Set:', {
   217      size: visibleLayers.size,
   218      count: visibleLayers.size,
   219      hasPrivateHunts: visibleLayers.has('private-hunts'),
   220      hasFriendsHunts: visibleLayers.has('friends-hunts'),
   221      hasPublicHunts: visibleLayers.has('public-hunts')
   222    });
   223
   224    // Process hunts data by privacy level
   225    if (sourceHunts.length > 0) {
   226      console.log(' PROCESSING HUNTS - Source data sample:', {
   227        totalSourceHunts: sourceHunts.length,
   228        firstFeatureProps: sourceHunts[0]?.properties,
   229        privacyLevels: sourceHunts.map(f => f.properties?.privacy_level).slice(0, 5)
   230      });
   231      
   232      // DEBUG: Show ALL hunts regardless of visibility settings
   233      const visibleHunts = sourceHunts; // TEMPORARY: Skip visibility filtering for debugging
   234      
   235      console.log(' DEBUG MODE: Showing ALL hunts without filtering', {
   236        totalHunts: sourceHunts.length,
   237        visibleLayersCheck: Array.from(visibleLayers)
   238      });
   239      
   240      console.log(' VISIBILITY FILTER RESULTS:', {
   241        originalHuntsCount: sourceHunts.length,
   242        visibleHuntsCount: visibleHunts.length,
   243        filteredOutCount: sourceHunts.length - visibleHunts.length,
   244        wasFiltered: visibleHunts.length === 0 ? ' ALL HUNTS FILTERED OUT' : ' Some hunts passed filter'
   245      });
   246      
   247      // Limit features on mobile and sort by relevance
   248      const limitedHunts = isMobile 
   249        ? visibleHunts
   250            .sort((a, b) => new Date(b.properties?.created_at || 0).getTime() - new Date(a.properties?.created_at || 0).getTime())
   251            .slice(0, maxFeatures)
   252        : visibleHunts;
   253      
   254      limitedHunts.forEach((feature) => {
   255        const privacyLevel = feature.properties?.privacy_level || 'private';
   256        processed.hunts.features.push({
   257          ...feature,
   258          properties: {
   259            ...feature.properties,
   260            layerId: `${privacyLevel}-hunts`,
   261            privacyLevel,
   262            dataType: 'hunts',
   263          }
   264        } as unknown as GeoJsonFeature);
   265      });
   266    }
   267
   268    // Process locations data by privacy level
   269    if (sourceLocations.length > 0) {
   270      sourceLocations.forEach((feature) => {
   271        const privacyLevel = feature.properties?.privacy_level || 'private';
   272        if (visibleLayers.has(`${privacyLevel}-locations`)) {
   273          processed.locations.features.push({
   274            ...feature,
   275            properties: {
   276              ...feature.properties,
   277              layerId: `${privacyLevel}-locations`,
   278              privacyLevel,
   279              dataType: 'locations',
   280            }
   281          } as unknown as GeoJsonFeature);
   282        }
   283      });
   284    }
   285
   286    // Process reports data by privacy level
   287    if (sourceReports.length > 0) {
   288      sourceReports.forEach((feature) => {
   289        const privacyLevel = feature.properties?.privacy_level || 'private';
   290        if (visibleLayers.has(`${privacyLevel}-reports`)) {
   291          processed.reports.features.push({
   292            ...feature,
   293            properties: {
   294              ...feature.properties,
   295              layerId: `${privacyLevel}-reports`,
   296              privacyLevel,
   297              dataType: 'reports',
   298            }
   299          } as unknown as GeoJsonFeature);
   300        }
   301      });
   302    }
   303
   304    console.log(' UnifiedMap - Processed data FINAL:', {
   305      huntsCount: processed.hunts.features.length,
   306      locationsCount: processed.locations.features.length,
   307      reportsCount: processed.reports.features.length,
   308      totalFeatures: processed.hunts.features.length + processed.locations.features.length + processed.reports.features.length,
   309      visibleLayersAtEnd: Array.from(visibleLayers),
   310      processedHuntsSample: processed.hunts.features.slice(0, 2).map(f => ({
   311        id: f.properties?.id,
   312        coords: f.geometry?.coordinates
   313      }))
   314    });
   315    
   316    return processed;
   317  }, [viewportCulling.culledData, visibleLayers]);
   318
   319  // Auto-show all layers immediately for debugging
   320  useEffect(() => {
   321    // Show all layers immediately for debugging
   322    console.log(' DEBUG: Showing all layers immediately');
   323    showAllLayers();
   324  }, []); // Run once on mount
   325  
   326  // Also add layers programmatically when data is available
   327  useEffect(() => {
   328    // Only proceed once we have data
   329    if ((huntsData?.features?.length || 0) > 0 || 
   330        (locationsData?.features?.length || 0) > 0 || 
   331        (reportsData?.features?.length || 0) > 0) {
   332      console.log(' Data is now available:', {
   333        hunts: huntsData?.features?.length || 0,
   334        locations: locationsData?.features?.length || 0,
   335        reports: reportsData?.features?.length || 0
   336      });
   337      
   338      // CRITICAL FIX: Add layers programmatically
   339      const map = mapRef.current?.getMap();
   340      if (map && map.isStyleLoaded()) {
   341        console.log(' ADDING DATA LAYERS PROGRAMMATICALLY');
   342        
   343        // ALWAYS add test data layer for debugging
   344        try {
   345          if (map.getLayer('prog-test-debug')) map.removeLayer('prog-test-debug');
   346          if (map.getSource('prog-test-debug')) map.removeSource('prog-test-debug');
   347        } catch (_) {
   348          // Layer might not exist yet
   349        }
   350        
   351        map.addSource('prog-test-debug', {
   352          type: 'geojson',
   353          data: {
   354            type: 'FeatureCollection',
   355            features: [
   356              {
   357                type: 'Feature',
   358                geometry: { type: 'Point', coordinates: [-98.5795, 39.8283] },
   359                properties: { debug: true }
   360              }
   361            ]
   362          }
   363        });
   364        
   365        map.addLayer({
   366          id: 'prog-test-debug',
   367          type: 'circle',
   368          source: 'prog-test-debug',
   369          paint: {
   370            'circle-radius': 80,
   371            'circle-color': '#FF00FF',
   372            'circle-stroke-color': '#FFFF00',
   373            'circle-stroke-width': 5,
   374            'circle-opacity': 1
   375          }
   376        });
   377        console.log(' Added debug test layer programmatically');
   378        
   379        // Add hunt layers if data exists
   380        if (processedData.hunts.features.length > 0) {
   381          // Remove existing if any
   382          try {
   383            if (map.getLayer('prog-hunts-circles')) map.removeLayer('prog-hunts-circles');
   384            if (map.getSource('prog-hunts')) map.removeSource('prog-hunts');
   385          } catch (e) {
   386            // Layer might not exist yet
   387          }
   388          
   389          // Add source and layer
   390          map.addSource('prog-hunts', {
   391            type: 'geojson',
   392            data: processedData.hunts
   393          });
   394          
   395          map.addLayer({
   396            id: 'prog-hunts-circles',
   397            type: 'circle',
   398            source: 'prog-hunts',
   399            paint: {
   400              'circle-radius': 12,
   401              'circle-color': '#FF6B35',
   402              'circle-stroke-color': '#FFFFFF',
   403              'circle-stroke-width': 2,
   404              'circle-opacity': 0.8
   405            }
   406          });
   407          console.log(' Added hunt layers programmatically');
   408        }
   409        
   410        // Add location layers if data exists
   411        if (processedData.locations.features.length > 0) {
   412          try {
   413            if (map.getLayer('prog-locations-circles')) map.removeLayer('prog-locations-circles');
   414            if (map.getSource('prog-locations')) map.removeSource('prog-locations');
   415          } catch (e) {
   416            // Layer might not exist yet
   417          }
   418          
   419          map.addSource('prog-locations', {
   420            type: 'geojson',
   421            data: processedData.locations
   422          });
   423          
   424          map.addLayer({
   425            id: 'prog-locations-circles',
   426            type: 'circle',
   427            source: 'prog-locations',
   428            paint: {
   429              'circle-radius': 10,
   430              'circle-color': '#4285F4',
   431              'circle-stroke-color': '#FFFFFF',
   432              'circle-stroke-width': 2,
   433              'circle-opacity': 0.8
   434            }
   435          });
   436          console.log(' Added location layers programmatically');
   437        }
   438        
   439        // Add report layers if data exists
   440        if (processedData.reports.features.length > 0) {
   441          try {
   442            if (map.getLayer('prog-reports-circles')) map.removeLayer('prog-reports-circles');
   443            if (map.getSource('prog-reports')) map.removeSource('prog-reports');
   444          } catch (e) {
   445            // Layer might not exist yet
   446          }
   447          
   448          map.addSource('prog-reports', {
   449            type: 'geojson',
   450            data: processedData.reports
   451          });
   452          
   453          map.addLayer({
   454            id: 'prog-reports-circles',
   455            type: 'circle',
   456            source: 'prog-reports',
   457            paint: {
   458              'circle-radius': 10,
   459              'circle-color': '#34A853',
   460              'circle-stroke-color': '#FFFFFF',
   461              'circle-stroke-width': 2,
   462              'circle-opacity': 0.8
   463            }
   464          });
   465          console.log(' Added report layers programmatically');
   466        }
   467      }
   468    }
   469  }, [huntsData, locationsData, reportsData, processedData, showAllLayers]);
   470
   471  // Handle map click for feature selection, clustering, and interaction
   472  const handleMapClick = useCallback(async (event: MapMouseEvent) => {
   473    const coordinates = { lng: event.lngLat.lng, lat: event.lngLat.lat };
   474    
   475    // Handle creation modes first
   476    if (mapMode !== 'view') {
   477      setTempMarker(coordinates);
   478      
   479      switch (mapMode) {
   480        case 'create-hunt':
   481          onAddHunt?.(coordinates);
   482          break;
   483        case 'create-report':
   484          onAddReport?.(coordinates);
   485          break;
   486        case 'create-location':
   487          onAddLocation?.(coordinates);
   488          break;
   489      }
   490      
   491      // Reset to view mode after creation
   492      setMapMode('view');
   493      setTempMarker(null);
   494      return;
   495    }
   496    
   497    // Check if we clicked on a cluster first - only query layers that exist
   498    const existingClusterLayers = [
   499      'privacy-hunts-clusters', 
   500      'privacy-locations-clusters', 
   501      'privacy-reports-clusters'
   502    ].filter(layerId => {
   503      try {
   504        return mapRef.current?.getMap()?.getLayer(layerId) !== undefined;
   505      } catch {
   506        return false;
   507      }
   508    });
   509    
   510    const clusterFeatures = existingClusterLayers.length > 0 ? 
   511      mapRef.current?.queryRenderedFeatures(event.point, {
   512        layers: existingClusterLayers
   513      }) : [];
   514    
   515    if (clusterFeatures && clusterFeatures.length > 0) {
   516      const feature = clusterFeatures[0];
   517      const properties = feature.properties || {};
   518      
   519      if ('cluster_id' in properties && feature.source) {
   520        // Handle cluster click
   521        if (mapRef.current) {
   522          await onClusterClick(event, mapRef.current);
   523        }
   524        
   525        // Show cluster popup
   526        const sourceId = feature.source;
   527        const dataType = sourceId.includes('hunts') ? 'hunts' : 
   528                        sourceId.includes('locations') ? 'locations' : 'reports';
   529        showClusterPopup(
   530          properties.cluster_id as number,
   531          sourceId,
   532          [coordinates.lng, coordinates.lat],
   533          properties,
   534          dataType as DataType
   535        );
   536        return;
   537      }
   538    }
   539    
   540    // Check if we clicked on a feature from the GeoJSON layers - only query layers that exist
   541    const existingCircleLayers = [
   542      'privacy-hunts-circles', 
   543      'privacy-locations-circles', 
   544      'privacy-reports-circles'
   545    ].filter(layerId => {
   546      try {
   547        return mapRef.current?.getMap()?.getLayer(layerId) !== undefined;
   548      } catch {
   549        return false;
   550      }
   551    });
   552    
   553    const features = existingCircleLayers.length > 0 ? 
   554      mapRef.current?.queryRenderedFeatures(event.point, {
   555        layers: existingCircleLayers
   556      }) : [];
   557
   558    if (features && features.length > 0) {
   559      const feature = features[0];
   560      const { dataType, id } = feature.properties || {};
   561      
   562      // Find the corresponding data item and trigger selection
   563      if (dataType === 'hunts') {
   564        const hunt = hunts.find(h => h.id === id);
   565        if (hunt) {
   566          onHuntSelect?.(hunt);
   567          return;
   568        }
   569      } else if (dataType === 'locations') {
   570        const location = locations.find(l => l.id === id);
   571        if (location) {
   572          onLocationSelect?.(location);
   573          return;
   574        }
   575      } else if (dataType === 'reports') {
   576        const report = reports.find(r => r.id === id);
   577        if (report) {
   578          onReportSelect?.(report);
   579          return;
   580        }
   581      }
   582    }
   583    
   584    // Regular map click handler
   585    onMapClick?.(coordinates);
   586  }, [mapMode, onClusterClick, showClusterPopup, hunts, locations, reports, onHuntSelect, onLocationSelect, onReportSelect, onAddHunt, onAddReport, onAddLocation, onMapClick]);
   587
   588  console.log(' UnifiedMap - Privacy layer data:', {
   589    hasHuntsData: !!huntsData,
   590    huntsFeatures: huntsData?.features?.length || 0,
   591    hasLocationsData: !!locationsData,
   592    locationsFeatures: locationsData?.features?.length || 0,
   593    hasReportsData: !!reportsData,
   594    reportsFeatures: reportsData?.features?.length || 0,
   595    visibleLayersSize: visibleLayers.size,
   596    visibleLayersArray: Array.from(visibleLayers),
   597    viewportCulling: viewportCulling.statistics,
   598    isLoading,
   599    error: error?.message
   600  });
   601
   602  // CSS Diagnostic Logging
   603  useEffect(() => {
   604    const runCssDiagnostics = () => {
   605      console.log(' CSS DIAGNOSTICS: Checking for potential CSS conflicts...');
   606      
   607      // Check if mapbox-gl.css is loaded
   608      const mapboxStyles = Array.from(document.styleSheets).find(sheet => 
   609        sheet.href && sheet.href.includes('mapbox-gl')
   610      );
   611      console.log(' Mapbox GL CSS loaded:', !!mapboxStyles);
   612      
   613      // Check for conflicting CSS rules
   614      const potentialConflicts = [
   615        'mapboxgl-ctrl',
   616        'mapboxgl-ctrl-group',
   617        'mapboxgl-ctrl-top-left',
   618        'mapboxgl-ctrl-top-right',
   619        'mapboxgl-ctrl-bottom-left',
   620        'mapboxgl-ctrl-bottom-right'
   621      ];
   622      
   623      potentialConflicts.forEach(className => {
   624        const elements = document.getElementsByClassName(className);
   625        if (elements.length > 0) {
   626          console.log(` Found ${elements.length} elements with class: ${className}`);
   627          Array.from(elements).forEach((el, index) => {
   628            const computedStyle = window.getComputedStyle(el);
   629            console.log(`  Element ${index} styles:`, {
   630              display: computedStyle.display,
   631              visibility: computedStyle.visibility,
   632              opacity: computedStyle.opacity,
   633              position: computedStyle.position,
   634              zIndex: computedStyle.zIndex,
   635            });
   636          });
   637        }
   638      });
   639      
   640      // Check map container styles
   641      const mapContainer = document.querySelector('[class*="mapbox"]');
   642      if (mapContainer) {
   643        const containerStyle = window.getComputedStyle(mapContainer);
   644        console.log(' Map container styles:', {
   645          position: containerStyle.position,
   646          overflow: containerStyle.overflow,
   647          zIndex: containerStyle.zIndex,
   648          height: containerStyle.height,
   649          width: containerStyle.width,
   650        });
   651      }
   652    };
   653    
   654    // Run diagnostics after component mount
