            <div className="relative">
              <div className="w-8 h-8 bg-blue-500 border-2 border-white rounded-full shadow-lg animate-bounce" />
              <div className="absolute -top-1 -right-1">
                {mapMode === 'create-hunt' && <span className="text-lg"></span>}
                {mapMode === 'create-report' && <span className="text-lg"></span>}
                {mapMode === 'create-location' && <span className="text-lg"></span>}
              </div>
            </div>
          </Marker>
        )}

        {/* Cluster Popup */}
        {clusterPopupInfo && (
          <ClusterPopup
            clusterId={clusterPopupInfo.clusterId}
            sourceId={clusterPopupInfo.sourceId}
            coordinates={clusterPopupInfo.coordinates}
            properties={clusterPopupInfo.properties}
            dataType={clusterPopupInfo.dataType as DataType}
            mapRef={mapRef.current}
            onClose={hideClusterPopup}
            onExpand={() => {
              if (mapRef.current) {
                // Create a minimal MapMouseEvent-like object
                const mockEvent = { 
                  lngLat: { 
                    lng: clusterPopupInfo.coordinates[0], 
                    lat: clusterPopupInfo.coordinates[1] 
                  },
                  point: { x: 0, y: 0 },
                  type: 'click' as const,
                  target: mapRef.current.getMap(),
                  originalEvent: new MouseEvent('click'),
                  defaultPrevented: false,
                  preventDefault: () => {}
                } as MapMouseEvent;
                onClusterClick(mockEvent, mapRef.current);
                hideClusterPopup();
              }
            }}
          />
        )}

        {/* Popups */}
        {selectedHunt && (
          <Popup
            longitude={selectedHunt.longitude}
            latitude={selectedHunt.latitude}
            anchor="bottom"
            onClose={() => onHuntSelect?.(null)}
            closeButton={true}
            closeOnClick={false}
          >
            <div className="p-3 min-w-[250px]">
              <div className="flex items-center justify-between mb-2">
                <h3 className="font-semibold text-gray-900">
                  Hunt - {new Date(selectedHunt.date_of_hunt).toLocaleDateString()}
                </h3>
                <span className="text-lg"></span>
              </div>

              {selectedHunt.total_birds > 0 && (
                <div className="flex items-center gap-4 mb-2 text-sm">
                  <span className="flex items-center gap-1">
                    <span className="text-green-600 font-medium">{selectedHunt.total_birds}</span> birds
                  </span>
                  {selectedHunt.total_ducks > 0 && (
                    <span className="flex items-center gap-1">
                       {selectedHunt.total_ducks}
                    </span>
                  )}
                  {selectedHunt.total_geese > 0 && (
                    <span className="flex items-center gap-1">
                       {selectedHunt.total_geese}
                    </span>
                  )}
                </div>
              )}

              {selectedHunt.notes && (
                <p className="text-sm text-gray-600 mt-2 line-clamp-3">
                  {selectedHunt.notes}
                </p>
              )}

              <div className="text-xs text-gray-500 mt-2 border-t border-gray-200 pt-2">
                <div>{selectedHunt.latitude.toFixed(4)}, {selectedHunt.longitude.toFixed(4)}</div>
                <div>Created: {new Date(selectedHunt.created_at).toLocaleDateString()}</div>
              </div>
            </div>
          </Popup>
        )}

        {selectedLocation && (
          <Popup
            longitude={selectedLocation.longitude}
            latitude={selectedLocation.latitude}
            anchor="bottom"
            onClose={() => onLocationSelect?.(null)}
            closeButton={true}
            closeOnClick={false}
          >
            <div className="p-2">
              <h3 className="font-semibold text-gray-900">
                {selectedLocation.name || 'Unnamed Location'}
              </h3>
              {selectedLocation.description && (
                <p className="text-sm text-gray-600 mt-1">
                  {selectedLocation.description}
                </p>
              )}
              <div className="text-xs text-gray-500 mt-1">
                {selectedLocation.latitude.toFixed(4)}, {selectedLocation.longitude.toFixed(4)}
              </div>
            </div>
          </Popup>
        )}

        {selectedReport && (
          <Popup
            longitude={selectedReport.longitude}
            latitude={selectedReport.latitude}
            anchor="bottom"
            onClose={() => onReportSelect?.(null)}
            closeButton={true}
            closeOnClick={false}
          >
            <div className="p-3 min-w-[250px]">
              <div className="flex items-center justify-between mb-2">
                <h3 className="font-semibold text-gray-900">
                  {selectedReport.title}
                </h3>
                <span className="text-lg"></span>
              </div>

              <div className="text-sm text-gray-600 mb-2 line-clamp-3">
                {selectedReport.content}
              </div>

              <div className="flex items-center gap-4 mb-2 text-sm">
                <span className="flex items-center gap-1">
                  <span className="text-blue-600 font-medium">By:</span> {selectedReport.user.username}
                </span>
                {(selectedReport.comment_count && selectedReport.comment_count > 0) && (
                  <span className="flex items-center gap-1">
                     {selectedReport.comment_count}
                  </span>
                )}
                {(selectedReport.photo_count && selectedReport.photo_count > 0) && (
                  <span className="flex items-center gap-1">
                     {selectedReport.photo_count}
                  </span>
                )}
              </div>

              <div className="text-xs text-gray-500 mt-2 border-t border-gray-200 pt-2">
                <div>{selectedReport.latitude.toFixed(4)}, {selectedReport.longitude.toFixed(4)}</div>
                <div>Created: {new Date(selectedReport.created_at).toLocaleDateString()}</div>
              </div>
            </div>
          </Popup>
        )}

        {/* GEOJSON DEBUG OVERLAY - Shows data status */}
        <div style={{
          position: 'absolute',
          top: '10px',
          left: '70px',
          zIndex: 9999,
          backgroundColor: 'rgba(0, 0, 0, 0.8)',
          color: 'white',
          padding: '10px',
          fontSize: '12px',
          borderRadius: '5px',
          fontFamily: 'monospace',
          maxWidth: '400px'
        }}>
          <div style={{ fontWeight: 'bold', marginBottom: '5px' }}> GeoJSON Debug Panel</div>
          <div>Map Loaded: {String(mapStyleLoaded)}</div>
          <div>Hunts: {processedData.hunts.features.length} features</div>
          <div>Locations: {processedData.locations.features.length} features</div>
          <div>Reports: {processedData.reports.features.length} features</div>
          <div>Visible Layers: {Array.from(visibleLayers).join(', ') || 'none'}</div>
          {processedData.hunts.features.length > 0 && (
            <div style={{ marginTop: '5px', fontSize: '10px' }}>
              Sample Hunt: {JSON.stringify(processedData.hunts.features[0]?.geometry?.coordinates || 'no coords')}
            </div>
          )}
        </div>





        {/* Privacy Layer Control Panel with Clustering Toggle */}
        {showLayerControls && (
          <PrivacyLayerControlPanel
            position={layerControlPosition}
            isOpen={layerControlOpen}
            onClose={() => setLayerControlOpen(false)}
            onLayerToggle={handlePrivacyLayerToggle}
            visibleLayers={visibleLayers}
            timeRange={timeRange}
            onTimeRangeChange={setTimeRange}
            mapRef={mapRef}
          >
            {/* Add clustering toggle to the control panel */}
            <div className="px-3 pb-3 border-t border-gray-200">
              <ClusterToggle
                enabled={clusteringEnabled}
                onToggle={toggleClustering}
                className="mt-3"
              />
            </div>
          </PrivacyLayerControlPanel>
        )}

        {/* Layer Control Toggle Button - when panel is closed */}
        {showLayerControls && !layerControlOpen && (
          <button
            onClick={() => setLayerControlOpen(true)}
            className={`absolute ${layerControlPosition === 'top-right' ? 'top-4 right-4' : 
                         layerControlPosition === 'top-left' ? 'top-4 left-4' :
                         layerControlPosition === 'bottom-right' ? 'bottom-4 right-4' : 'bottom-4 left-4'} 
                       z-20 bg-white rounded-lg shadow-lg border border-gray-200 p-2 hover:bg-gray-50 transition-colors`}
            title="Open Layer Controls"
          >
            <Squares2X2Icon className="w-5 h-5 text-gray-600" />
          </button>
        )}

        {/* Creation Tools - Simple Implementation */}
        {showCustomControls && (onAddHunt || onAddReport || onAddLocation) && (
          <div className="absolute bottom-4 right-4 z-20 flex flex-col gap-2">
            {mapMode === 'view' ? (
              <div className="bg-white rounded-lg shadow-lg border border-gray-200 p-2 flex flex-col gap-2">
                {onAddHunt && (
                  <button
                    onClick={() => setMapMode('create-hunt')}
                    className="flex items-center gap-2 px-3 py-2 bg-orange-100 hover:bg-orange-200 text-orange-800 rounded-md text-sm font-medium transition-colors"
                    title="Add Hunt"
                  >
                     Hunt
                  </button>
                )}
                {onAddReport && (
                  <button
                    onClick={() => setMapMode('create-report')}
                    className="flex items-center gap-2 px-3 py-2 bg-blue-100 hover:bg-blue-200 text-blue-800 rounded-md text-sm font-medium transition-colors"
                    title="Add Report"
                  >
                     Report
                  </button>
                )}
                {onAddLocation && (
                  <button
                    onClick={() => setMapMode('create-location')}
                    className="flex items-center gap-2 px-3 py-2 bg-green-100 hover:bg-green-200 text-green-800 rounded-md text-sm font-medium transition-colors"
                    title="Add Location"
                  >
                     Location
                  </button>
                )}
              </div>
            ) : (
              <div className="bg-white rounded-lg shadow-lg border border-gray-200 p-3">
                <p className="text-sm text-gray-600 mb-2">
                  {mapMode === 'create-hunt' && 'Click on the map to place a hunt marker'}
                  {mapMode === 'create-report' && 'Click on the map to place a report marker'}
                  {mapMode === 'create-location' && 'Click on the map to place a location marker'}
                </p>
                <button
                  onClick={() => setMapMode('view')}
                  className="px-3 py-1 bg-gray-100 hover:bg-gray-200 text-gray-800 rounded text-sm transition-colors"
                >
                  Cancel
                </button>
              </div>
            )}
          </div>
        )}
          </>
        )}
      </Map>

      {/* Loading overlay */}
      {isLoading && (
        <div className="absolute inset-0 bg-black bg-opacity-20 flex items-center justify-center z-10">
          <div className="bg-white rounded-lg p-4 shadow-lg flex items-center gap-3">
            <div className="w-5 h-5 border-2 border-blue-500 border-t-transparent rounded-full animate-spin" />
            <span className="text-sm text-gray-700">Loading map data...</span>
          </div>
        </div>
      )}

      {/* Error overlay */}
      {error && (
        <div className="absolute top-4 left-1/2 transform -translate-x-1/2 z-20">
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded shadow-lg max-w-md">
            <p className="text-sm">
              <strong>Map Error:</strong> {error.message}
            </p>
          </div>
        </div>
      )}

    </div>
  );
}

// Main unified map component
export function UnifiedMap(props: UnifiedMapProps) {
  console.log(' UnifiedMap - Component rendering with props:', { 
    initialVisibleLayers: props.initialVisibleLayers?.length || 0,
    hasMapStyle: !!props.mapStyle,
    center: props.center 
  });
  const { initialVisibleLayers = [], ...mapProps } = props;

  return (
    <MapErrorBoundary
      onError={(error, errorInfo) => {
        console.error('Unified map component error:', error, errorInfo);
      }}
    >
      <PrivacyLayerProvider initialVisibleLayers={initialVisibleLayers}>
        <MapContent {...mapProps} />
      </PrivacyLayerProvider>
    </MapErrorBoundary>
  );
}

